import { useCallback, useState } from 'react';
import {
  analyzePerformance,
  testWebVitalsCollection,
} from '@/scripts/test-web-vitals';
import type { TestResults } from '@/app/[locale]/diagnostics/demo/utils';
import {
  exportTestResults,
  PERFORMANCE_CONSTANTS,
  simulatePerformanceIssues,
} from '@/app/[locale]/diagnostics/demo/utils';

// è¯Šæ–­åŠŸèƒ½ Hook
export function useDiagnostics() {
  const [testResults, setTestResults] = useState<TestResults | null>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [logs, setLogs] = useState<string[]>([]);

  const addLog = useCallback((message: string) => {
    const timestamp = new Date().toLocaleTimeString();
    setLogs((prev) => [...prev, `[${timestamp}] ${message}`]);
  }, []);

  const runDiagnostics = useCallback(async () => {
    if (isRunning) return;

    setIsRunning(true);
    setLogs([]);
    addLog('å¼€å§‹æ€§èƒ½è¯Šæ–­...');

    try {
      // 1. æ¨¡æ‹Ÿæ€§èƒ½é—®é¢˜
      addLog('æ¨¡æ‹Ÿæ€§èƒ½é—®é¢˜...');
      simulatePerformanceIssues();

      // 2. ç­‰å¾…ä¸€æ®µæ—¶é—´è®©æ€§èƒ½é—®é¢˜ç”Ÿæ•ˆ
      await new Promise((resolve) =>
        setTimeout(resolve, PERFORMANCE_CONSTANTS.DIAGNOSTIC_DELAY),
      );

      // 3. æ”¶é›† Web Vitals æ•°æ®
      addLog('æ”¶é›† Web Vitals æ•°æ®...');
      const report = testWebVitalsCollection();

      if (report) {
        // 4. åˆ†ææ€§èƒ½æ•°æ®
        addLog('åˆ†ææ€§èƒ½æ•°æ®...');
        analyzePerformance(report);

        // 5. è½¬æ¢ä¸ºç»„ä»¶éœ€è¦çš„æ ¼å¼
        const results: TestResults = {
          metrics: {
            cls: report.metrics.cls,
            lcp: report.metrics.lcp,
            fid: report.metrics.fid,
            fcp: report.metrics.fcp,
            ttfb: report.metrics.ttfb,
          },
          analysis: {
            score: report.analysis.score,
            issues: report.analysis.issues,
            recommendations: report.analysis.recommendations,
          },
          timestamp: Date.now(),
        };

        setTestResults(results);
        addLog(`è¯Šæ–­å®Œæˆï¼æ€»ä½“è¯„åˆ†: ${results.analysis.score}`);
        addLog(`å‘ç° ${results.analysis.issues.length} ä¸ªé—®é¢˜`);
        addLog(`æä¾› ${results.analysis.recommendations.length} æ¡å»ºè®®`);
      } else {
        addLog('è¯Šæ–­å¤±è´¥ï¼šæ— æ³•æ”¶é›†æ€§èƒ½æ•°æ®');
      }
    } catch (error) {
      addLog(`è¯Šæ–­è¿‡ç¨‹ä¸­å‡ºç°é”™è¯¯: ${error}`);
    } finally {
      setIsRunning(false);
    }
  }, [isRunning, addLog]);

  const handleExportResults = useCallback(() => {
    if (testResults) {
      exportTestResults(testResults);
      addLog('æµ‹è¯•ç»“æœå·²å¯¼å‡º');
    }
  }, [testResults, addLog]);

  return {
    testResults,
    isRunning,
    logs,
    runDiagnostics,
    handleExportResults,
  };
}

// @ts-nocheck - å¼€å‘å·¥å…·è±å…ï¼šä»…å¼€å‘ç¯å¢ƒä½¿ç”¨ï¼Œä¸å½±å“ç”Ÿäº§ä»£ç è´¨é‡
// æ€§èƒ½è¯Šæ–­å¸¸é‡å®šä¹‰
export const PERFORMANCE_CONSTANTS = {
  SIMULATION_DELAY: 2000,
  DIAGNOSTIC_DELAY: 2000,
  BLOCKING_DURATION: 100,
  SPACING_INDENT: 2,
  DECIMAL_PLACES: 3,
  AUTO_RUN_DELAY: 1000,
  // Web Vitals é˜ˆå€¼
  CLS_GOOD_THRESHOLD: 0.1,
  CLS_NEEDS_IMPROVEMENT_THRESHOLD: 0.25,
  LCP_GOOD_THRESHOLD: 2500,
  LCP_NEEDS_IMPROVEMENT_THRESHOLD: 4000,
  FID_GOOD_THRESHOLD: 100,
  FID_NEEDS_IMPROVEMENT_THRESHOLD: 300,
  FCP_GOOD_THRESHOLD: 1800,
  FCP_NEEDS_IMPROVEMENT_THRESHOLD: 3000,
  TTFB_GOOD_THRESHOLD: 800,
  TTFB_NEEDS_IMPROVEMENT_THRESHOLD: 1800,
} as const;

// ç±»å‹å®šä¹‰
export interface TestResults {
  metrics: {
    cls: number;
    lcp: number;
    fid: number;
    fcp: number;
    ttfb: number;
    inp?: number;
  };
  analysis: {
    score: number;
    issues: string[];
    recommendations: string[];
  };
  timestamp: number;
}

// å·¥å…·å‡½æ•°ï¼šæ¨¡æ‹Ÿæ€§èƒ½é—®é¢˜ï¼ˆä¼˜åŒ–ç‰ˆæœ¬ - å‡å°‘ CLS å½±å“ï¼‰
export function simulatePerformanceIssues(): void {
  // æ¨¡æ‹Ÿå¸ƒå±€åç§» - ä½¿ç”¨æ›´æ¸©å’Œçš„æ–¹å¼ï¼Œé¿å…ä¸¥é‡çš„ CLS
  const div = document.createElement('div');
  div.style.height = '50px'; // å‡å°‘åˆå§‹é«˜åº¦
  div.style.backgroundColor = 'rgba(255, 0, 0, 0.3)'; // åŠé€æ˜ï¼Œå‡å°‘è§†è§‰å†²å‡»
  div.style.position = 'fixed'; // ä½¿ç”¨ fixed å®šä½ï¼Œå‡å°‘å¯¹é¡µé¢å¸ƒå±€çš„å½±å“
  div.style.top = '10px';
  div.style.right = '10px'; // ç§»åˆ°å³ä¸Šè§’ï¼Œé¿å…å½±å“ä¸»è¦å†…å®¹
  div.style.width = '100px';
  div.style.zIndex = '9999';
  div.style.borderRadius = '4px';
  div.style.transition = 'height 0.3s ease'; // æ·»åŠ è¿‡æ¸¡åŠ¨ç”»ï¼Œå‡å°‘çªå…€æ„Ÿ
  document.body.appendChild(div);

  setTimeout(() => {
    div.style.height = '80px'; // å‡å°‘é«˜åº¦å˜åŒ–å¹…åº¦
    setTimeout(() => {
      div.style.opacity = '0'; // æ·¡å‡ºæ•ˆæœ
      setTimeout(() => {
        if (document.body.contains(div)) {
          document.body.removeChild(div);
        }
      }, DEV_TOOLS_CONSTANTS.DIAGNOSTICS.DIAGNOSTIC_DELAY); // ç­‰å¾…æ·¡å‡ºåŠ¨ç”»å®Œæˆ
    }, PERFORMANCE_CONSTANTS.SIMULATION_DELAY);
  }, PERFORMANCE_CONSTANTS.BLOCKING_DURATION);

  // æ¨¡æ‹Ÿä¸»çº¿ç¨‹é˜»å¡ - å‡å°‘é˜»å¡æ—¶é—´
  const start = performance.now();
  const reducedBlockingTime = Math.min(
    PERFORMANCE_CONSTANTS.BLOCKING_DURATION,
    DEV_TOOLS_CONSTANTS.LAYOUT.DEFAULT_OFFSET,
  ); // æœ€å¤šé˜»å¡50ms
  while (performance.now() - start < reducedBlockingTime) {
    // é˜»å¡ä¸»çº¿ç¨‹
  }
}

// å·¥å…·å‡½æ•°ï¼šå¯¼å‡ºæµ‹è¯•ç»“æœ
export function exportTestResults(testResults: TestResults): void {
  const data = {
    ...testResults,
    exportedAt: new Date().toISOString(),
    userAgent: navigator.userAgent,
    viewport: {
      width: window.innerWidth,
      height: window.innerHeight,
    },
  };

  const blob = new Blob(
    [JSON.stringify(data, null, PERFORMANCE_CONSTANTS.SPACING_INDENT)],
    {
      type: 'application/json',
    },
  );
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = `web-vitals-demo-${new Date().toISOString().split('T')[0]}.json`;
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
  URL.revokeObjectURL(url);
}

// å·¥å…·å‡½æ•°ï¼šè·å–æ€§èƒ½æŒ‡æ ‡çŠ¶æ€
export function getMetricStatus(
  value: number,
  goodThreshold: number,
  needsImprovementThreshold: number,
): 'default' | 'secondary' | 'destructive' {
  if (value <= goodThreshold) return 'default';
  if (value <= needsImprovementThreshold) return 'secondary';
  return 'destructive';
}

// å·¥å…·å‡½æ•°ï¼šè·å–æ€§èƒ½æŒ‡æ ‡æ ‡ç­¾
export function getMetricLabel(
  value: number,
  goodThreshold: number,
  needsImprovementThreshold: number,
): string {
  if (value <= goodThreshold) return 'è‰¯å¥½';
  if (value <= needsImprovementThreshold) return 'éœ€è¦æ”¹è¿›';
  return 'è¾ƒå·®';
}

'use client';

import { useCallback, useEffect, useState } from 'react';
import {
  ControlPanel,
  CurrentMetrics,
  HistoricalData,
  ThresholdReference,
} from './components';
import {
  collectCurrentMetrics,
  exportDiagnosticsData,
  loadHistoricalData,
  saveCurrentData,
  type SimpleWebVitals,
} from './utils';

export default function DiagnosticsPage() {
  const [currentMetrics, _setCurrentMetrics] = useState<SimpleWebVitals | null>(
    null,
  );
  const [historicalData, _setHistoricalData] = useState<SimpleWebVitals[]>([]);
  const [isLoading, _setIsLoading] = useState(false);

  // åˆ·æ–°æ•°æ®
  const refreshData = useCallback(async () => {
    _setIsLoading(true);
    try {
      // æ¨¡æ‹Ÿå¼‚æ­¥æ•°æ®æ”¶é›†
      await new Promise((resolve) => setTimeout(resolve, 1000));

      const metrics = collectCurrentMetrics();
      _setCurrentMetrics(metrics);

      // ä¿å­˜åˆ°å†å²è®°å½•
      const updatedHistory = saveCurrentData(metrics);
      _setHistoricalData(updatedHistory);
    } catch (error) {
      console.error('Failed to refresh data:', error);
    } finally {
      _setIsLoading(false);
    }
  }, []);

  // å¯¼å‡ºæ•°æ®
  const handleExportData = useCallback(() => {
    exportDiagnosticsData(currentMetrics, historicalData);
  }, [currentMetrics, historicalData]);

  // åˆå§‹åŒ–æ•°æ®åŠ è½½
  useEffect(() => {
    const historical = loadHistoricalData();
    _setHistoricalData(historical);

    // è‡ªåŠ¨åˆ·æ–°ä¸€æ¬¡
    refreshData();
  }, [refreshData]);

  return (
    <div className='min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100'>
      <div className='container mx-auto px-4 py-8'>
        {/* é¡µé¢æ ‡é¢˜ */}
        <div className='mb-8 text-center'>
          <h1 className='mb-4 text-4xl font-bold tracking-tight'>
            Web Vitals æ€§èƒ½è¯Šæ–­
          </h1>
          <p className='text-muted-foreground mx-auto max-w-3xl text-xl'>
            æ·±å…¥åˆ†æç½‘ç«™æ€§èƒ½æŒ‡æ ‡ï¼Œè¯†åˆ«å…³é”®é—®é¢˜ï¼Œè·å–ä¸“ä¸šçš„ä¼˜åŒ–å»ºè®®ã€‚ å®æ—¶ç›‘æ§
            CLSã€LCPã€FID ç­‰æ ¸å¿ƒ Web Vitals æŒ‡æ ‡ã€‚
          </p>
        </div>

        {/* æ§åˆ¶é¢æ¿ */}
        <ControlPanel
          isLoading={isLoading}
          currentMetrics={currentMetrics}
          onRefresh={refreshData}
          onExport={handleExportData}
        />

        {/* å½“å‰é¡µé¢æ€§èƒ½æŒ‡æ ‡ */}
        {currentMetrics && <CurrentMetrics metrics={currentMetrics} />}

        {/* å†å²æ•°æ® */}
        <HistoricalData data={historicalData} />

        {/* æ€§èƒ½é˜ˆå€¼å‚è€ƒ */}
        <ThresholdReference />
      </div>
    </div>
  );
}

