 */
export async function generateLocaleMetadata({
  params,
}: {
  params: Promise<{ locale: string }>;
}): Promise<Metadata> {
  const { locale } = await params;

  // ç¡®ä¿localeæœ‰æ•ˆ
  if (!routing.locales.includes(locale as Locale)) {
    return {
      title: 'Tucsenberg Web Frontier',
