import { MAGIC_0_3 } from "@/constants/decimal";
import { useIntersectionObserver } from '@/hooks/use-intersection-observer';
import {
  techStackCategories,
  techStackData,
  type TechStackCategory,
} from '@/lib/tech-stack-data';
