export function TechStackSection() {
  const t = useTranslations('home.techStack');
  const [selectedCategory, setSelectedCategory] =
    useState<TechStackCategory>('core');

  // åŠ¨ç”»Hook
  const { ref: titleRef, isVisible: titleVisible } =
    useIntersectionObserver<HTMLDivElement>({
      threshold: MAGIC_0_3,
      triggerOnce: true,
    });

  const { ref: tabsRef, isVisible: tabsVisible } =
    useIntersectionObserver<HTMLDivElement>({
      threshold: MAGIC_0_3,
      triggerOnce: true,
    });

  const categorizedTech = useMemo(() => {
    return Object.keys(techStackCategories).reduce(
      (acc, category) => {
        acc[category as TechStackCategory] = techStackData.filter(
          (item) => item.category === category,
        );
        return acc;
      },
      {} as Record<TechStackCategory, typeof techStackData>,
    );
  }, []);

  return (
    <section
      id='tech-stack'
      className='py-20'
    >
      <div className='container mx-auto px-4'>
        <div className='mx-auto max-w-6xl'>
          <TechStackTitle
            titleRef={titleRef}
            titleVisible={titleVisible}
            title={t('title')}
            subtitle={t('subtitle')}
          />

          <TechStackTabs
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
            tabsRef={tabsRef}
            tabsVisible={tabsVisible}
            categorizedTech={categorizedTech}
            t={t}
          />

          <TechStackStats t={t} />
        </div>
      </div>
    </section>
  );
}

