'use client';

import { useEffect, useState } from 'react';
import { Monitor, Moon, Sun } from 'lucide-react';
import { useTranslations } from 'next-intl';
import { useTheme } from 'next-themes';
import { cn } from '@/lib/utils';

/**
 * ä¸»é¢˜é€‰é¡¹é…ç½®
 */
const THEME_OPTIONS = [
  {
    value: 'light',
    icon: Sun,
    labelKey: 'theme.light',
    ariaLabelKey: 'theme.switchToLight',
  },
  {
    value: 'dark',
    icon: Moon,
    labelKey: 'theme.dark',
    ariaLabelKey: 'theme.switchToDark',
  },
  {
    value: 'system',
    icon: Monitor,
    labelKey: 'theme.system',
    ariaLabelKey: 'theme.switchToSystem',
  },
] as const;

/**
 * åŠ¨ç”»å˜ä½“ç±»å‹
 */
export type AnimationVariant = 'circle-blur' | 'framer-motion';

/**
 * ç»„ä»¶å±æ€§æ¥å£
 */
export interface HorizontalThemeToggleProps {
  /** æ˜¯å¦æ˜¾ç¤ºæ–‡å­—æ ‡ç­¾ */
  'showLabels'?: boolean;
  /** ç»„ä»¶å°ºå¯¸ */
  'size'?: 'sm' | 'default' | 'lg';
  /** è‡ªå®šä¹‰æ ·å¼ç±»å */
  'className'?: string;
  /** æ˜¯å¦ç¦ç”¨åŠ¨ç”»æ•ˆæœ */
  'disableAnimations'?: boolean;
  /** åŠ¨ç”»å˜ä½“ */
  'animationVariant'?: AnimationVariant;
  /** æµ‹è¯•ID */
  'data-testid'?: string;
}

/**
 * è·å–å°ºå¯¸ç›¸å…³çš„æ ·å¼ç±»
 */
const getSizeClasses = (size: 'sm' | 'default' | 'lg') => {
  switch (size) {
    case 'sm':
      return {
        container: 'p-0.5',
        button: 'px-2 py-1 text-xs gap-1.5',
        icon: 'h-3 w-3',
      };
    case 'lg':
      return {
        container: 'p-1',
        button: 'px-4 py-2 text-base gap-2',
        icon: 'h-5 w-5',
      };
    default:
      return {
        container: 'p-0.5',
        button: 'px-3 py-1.5 text-sm gap-2',
        icon: 'h-4 w-4',
      };
  }
};

/**
 * æ°´å¹³ä¸»é¢˜åˆ‡æ¢ç»„ä»¶ - ç®€åŒ–ç‰ˆ
 */
export function HorizontalThemeToggle({
  showLabels = false,
  size = 'default',
  className,
  'disableAnimations': _disableAnimations, // å¿½ç•¥ï¼Œç®€åŒ–ç‰ˆä¸æ”¯æŒåŠ¨ç”»
  'animationVariant': _animationVariant, // å¿½ç•¥ï¼Œç®€åŒ–ç‰ˆä¸æ”¯æŒåŠ¨ç”»å˜ä½“
  'data-testid': testId = 'horizontal-theme-toggle',
}: HorizontalThemeToggleProps) {
  const { theme, setTheme } = useTheme();
  const [mounted, setMounted] = useState(false);
  const t = useTranslations();

  // é˜²æ­¢ hydration mismatch
  useEffect(() => {
    setMounted(true);
  }, []);

  const sizeClasses = getSizeClasses(size);

  // åŠ è½½çŠ¶æ€çš„éª¨æ¶å±
  if (!mounted) {
    return (
      <div
        className={cn(
          'bg-background inline-flex items-center rounded-lg border',
          sizeClasses.container,
          className,
        )}
        data-testid={testId}
      >
        {THEME_OPTIONS.map((option) => (
          <div
            key={option.value}
            className={cn(
              'flex items-center justify-center rounded-md transition-colors',
              sizeClasses.button,
              'bg-muted animate-pulse',
            )}
          >
            <div
              className={cn(
                'rounded',
                sizeClasses.icon,
                'bg-muted-foreground/20',
              )}
            />
            {showLabels && (
              <div className='bg-muted-foreground/20 h-4 w-12 rounded' />
            )}
          </div>
        ))}
      </div>
    );
  }

  return (
    <div
      className={cn(
        'bg-background inline-flex items-center rounded-lg border',
        sizeClasses.container,
        className,
      )}
      role='radiogroup'
      aria-label={t('theme.toggleLabel')}
      data-testid={testId}
    >
      {THEME_OPTIONS.map((themeOption) => {
        const Icon = themeOption.icon;
        const isActive = theme === themeOption.value;
        const label = t(themeOption.labelKey);
        const ariaLabel = t(themeOption.ariaLabelKey);

        return (
          <button
            key={themeOption.value}
            className={cn(
              'flex items-center justify-center rounded-md transition-colors',
              'hover:bg-muted focus-visible:ring-ring focus-visible:ring-2 focus-visible:outline-none',
              sizeClasses.button,
              isActive &&
                'bg-primary text-primary-foreground hover:bg-primary/90',
            )}
            onClick={() => setTheme(themeOption.value)}
            aria-label={ariaLabel}
            role='radio'
            aria-checked={isActive}
            type='button'
          >
            <Icon className={cn(sizeClasses.icon)} />
            {showLabels && <span className='font-medium'>{label}</span>}
          </button>
        );
      })}
    </div>
  );
}

// é‡æ–°å¯¼å‡ºå®Œæ•´ç‰ˆæœ¬çš„ç»„ä»¶é…ç½®
export {
  type AnimationVariant,
  type HorizontalThemeToggleProps,
} from './horizontal-theme-toggle/theme-config';

// ä¸´æ—¶ä½¿ç”¨ç®€åŒ–ç‰ˆæœ¬ï¼Œå¿½ç•¥ä¸æ”¯æŒçš„å±æ€§
export { HorizontalThemeToggle } from '@/components/theme/horizontal-theme-toggle-simple';

import { ANIMATION_DURATION_VERY_SLOW, COUNT_PAIR, PERCENTAGE_FULL } from "@/constants/magic-numbers";

/**
 * ä¸»é¢˜åˆ‡æ¢åŠ¨ç”»å·¥å…·å‡½æ•°
 */
export const createCircleBlurAnimation = (
  buttonElement: HTMLElement | null,
) => {
  if (!buttonElement) return '';

  const rect = buttonElement.getBoundingClientRect();
  const centerX = ((rect.left + rect.width / COUNT_PAIR) / window.innerWidth) * PERCENTAGE_FULL;
  const centerY = ((rect.top + rect.height / COUNT_PAIR) / window.innerHeight) * PERCENTAGE_FULL;

  return `
    @supports (view-transition-name: root) {
      ::view-transition-old(root) {
        animation: none;
      }
      ::view-transition-new(root) {
        animation: circle-blur-expand 0.6s cubic-bezier(0.4, 0, 0.COUNT_PAIR, 1);
        transform-origin: ${centerX}% ${centerY}%;
      }
      @keyframes circle-blur-expand {
        from {
          clip-path: circle(0% at ${centerX}% ${centerY}%);
          filter: blur(8px);
        }
        50% {
          filter: blur(4px);
        }
        to {
          clip-path: circle(150% at ${centerX}% ${centerY}%);
          filter: blur(0);
        }
      }
    }
  `;
};

/**
 * åº”ç”¨ä¸»é¢˜åˆ‡æ¢åŠ¨ç”»
 */
export const applyThemeAnimation = (
  newTheme: string,
  buttonElement: HTMLButtonElement | null,
  animationVariant: 'circle-blur' | 'framer-motion',
  shouldDisableAnimations: boolean,
  setTheme: (theme: string) => void,
) => {
  // ç¡®å®šä½¿ç”¨çš„åŠ¨ç”»å˜ä½“
  const shouldUseCircleBlur =
    animationVariant === 'circle-blur' &&
    !shouldDisableAnimations &&
    'startViewTransition' in document;

  if (shouldUseCircleBlur && buttonElement) {
    // åˆ›å»ºåŠ¨ç”»æ ·å¼
    const styleId = `theme-transition-${Date.now()}`;
    const style = document.createElement('style');
    style.id = styleId;
    style.textContent = createCircleBlurAnimation(buttonElement);
    document.head.appendChild(style);

    // ä½¿ç”¨ View Transition API
    (document as any).startViewTransition(() => {
      setTheme(newTheme);
    });

    // æ¸…ç†åŠ¨ç”»æ ·å¼
    setTimeout(() => {
      const styleEl = document.getElementById(styleId);
      if (styleEl) {
        styleEl.remove();
      }
    }, ANIMATION_DURATION_VERY_SLOW);
  } else {
    // ç›´æ¥åˆ‡æ¢ä¸»é¢˜ï¼Œä¸ä½¿ç”¨åŠ¨ç”»
    setTheme(newTheme);
  }
};

import { THEME_OPTIONS } from '@/components/theme/horizontal-theme-toggle/theme-config';
import { ONE, ZERO } from "@/constants/magic-numbers";

/**
 * å¤„ç†é”®ç›˜å¯¼èˆª
 */
export const createKeyboardHandler = (
  theme: string | undefined,
  handleThemeChange: (
    newTheme: string,
    buttonElement?: HTMLButtonElement,
  ) => void,
) => {
  return (
    event: React.KeyboardEvent<HTMLButtonElement>,
    _themeValue: string,
  ) => {
    const currentIndex = THEME_OPTIONS.findIndex(
      (option) => option.value === theme,
    );
    let nextIndex = currentIndex;
    const buttonElement = event.currentTarget;

    switch (event.key) {
      case 'ArrowLeft':
      case 'ArrowUp': {
        event.preventDefault();
        nextIndex =
          currentIndex > ZERO ? currentIndex - ONE : THEME_OPTIONS.length - ONE;
        const prevOption = THEME_OPTIONS[nextIndex];
        if (prevOption) {
          handleThemeChange(prevOption.value, buttonElement);
        }
        break;
      }
      case 'ArrowRight':
      case 'ArrowDown': {
        event.preventDefault();
        nextIndex =
          currentIndex < THEME_OPTIONS.length - ONE ? currentIndex + ONE : ZERO;
        const nextOption = THEME_OPTIONS[nextIndex];
        if (nextOption) {
          handleThemeChange(nextOption.value, buttonElement);
        }
        break;
      }
      case 'Home': {
        event.preventDefault();
        const firstOption = THEME_OPTIONS[ZERO];
        if (firstOption) {
          handleThemeChange(firstOption.value, buttonElement);
        }
        break;
      }
      case 'End': {
        event.preventDefault();
        const lastOption = THEME_OPTIONS[THEME_OPTIONS.length - ONE];
        if (lastOption) {
          handleThemeChange(lastOption.value, buttonElement);
        }
        break;
      }
    }
  };
};

