/**
 * æ£€æŸ¥ç»„ä»¶æ˜¯å¦å·²æŒ‚è½½çš„hook
 * ä½¿ç”¨useSyncExternalStoreæ¥é¿å…SSR/CSRæ°´åˆä¸åŒ¹é…é—®é¢˜
 * è¿™æ˜¯React 18+æ¨èçš„å¤„ç†å®¢æˆ·ç«¯çŠ¶æ€çš„æ–¹å¼
 */
function useMounted() {
  return React.useSyncExternalStore(
    () => {
      // subscribe - è¿”å›ç©ºçš„æ¸…ç†å‡½æ•°ï¼Œå› ä¸ºmountedçŠ¶æ€ä¸ä¼šæ”¹å˜
      // eslint-disable-next-line no-empty-function
      return () => {};
    },
    () => true, // getSnapshot - å®¢æˆ·ç«¯æ€»æ˜¯è¿”å›true
    () => false, // getServerSnapshot - æœåŠ¡ç«¯æ€»æ˜¯è¿”å›false
  );
}

/**
 * åˆ›å»ºä¸»é¢˜åˆ‡æ¢å¤„ç†å‡½æ•°
 */
function createThemeChangeHandler(
  setCircularTheme: (
    _theme: string,
    _event?: React.MouseEvent<HTMLElement>,
  ) => void,
  announceSwitching: () => void,
  announceThemeChange: (_theme: string) => void,
  prefersReducedMotion: boolean,
  setIsOpen: (_open: boolean) => void,
) {
  return (newTheme: string, event?: React.MouseEvent<HTMLElement>) => {
    // æ’­æŠ¥åˆ‡æ¢çŠ¶æ€
    announceSwitching();

    // æ‰§è¡Œä¸»é¢˜åˆ‡æ¢
    setCircularTheme(newTheme, event);

    // å»¶è¿Ÿæ’­æŠ¥å®ŒæˆçŠ¶æ€
    const reducedMotionDelay = PERCENTAGE_HALF;
    const normalDelay = 400;
    setTimeout(
      () => {
        announceThemeChange(newTheme);
      },
      prefersReducedMotion ? reducedMotionDelay : normalDelay,
    );

    // å…³é—­ä¸‹æ‹‰èœå•
    setIsOpen(false);
  };
}

/**
 * ä¸»é¢˜åˆ‡æ¢é€»è¾‘çš„è‡ªå®šä¹‰hook
 * å°è£…ä¸»é¢˜åˆ‡æ¢ã€æ— éšœç¢æ€§æ”¯æŒå’ŒçŠ¶æ€ç®¡ç†
 */
export function useThemeToggle() {
  const { theme, setCircularTheme } = useEnhancedTheme();
  const [isOpen, setIsOpenState] = useState(false);
  const mounted = useMounted();

  const setIsOpen = setIsOpenState;
  const {
    announceThemeChange,
    announceSwitching,
    prefersReducedMotion,
    prefersHighContrast,
    handleKeyboardNavigation,
    getAriaAttributes,
  } = useAccessibility();

  // å¤„ç†ä¸»é¢˜åˆ‡æ¢
  const handleThemeChange = useCallback(
    (newTheme: string, event?: React.MouseEvent<HTMLElement>) => {
      const handler = createThemeChangeHandler(
        setCircularTheme,
        announceSwitching,
        announceThemeChange,
        prefersReducedMotion,
        setIsOpen,
      );
      handler(newTheme, event);
    },
    [
      setCircularTheme,
      announceSwitching,
      announceThemeChange,
      prefersReducedMotion,
      setIsOpen,
    ],
  );

  // å¤„ç†é”®ç›˜å¯¼èˆª
  const handleKeyDown = useCallback(
    (event: React.KeyboardEvent, action: () => void) => {
      handleKeyboardNavigation(event.nativeEvent, action, () =>
        setIsOpen(false),
      );
    },
    [handleKeyboardNavigation, setIsOpen],
  );

  // è·å–å½“å‰ä¸»é¢˜çš„ARIAå±æ€§
  // åœ¨ç»„ä»¶æŒ‚è½½å‰ä½¿ç”¨ 'system' ä½œä¸ºé»˜è®¤å€¼ä»¥é¿å… SSR/CSR ä¸ä¸€è‡´
  const currentTheme = mounted ? theme || 'system' : 'system';
  const ariaAttributes = getAriaAttributes(currentTheme, isOpen);

  return {
    // çŠ¶æ€
    theme,
    isOpen,
    setIsOpen,

    prefersReducedMotion,
    prefersHighContrast,
    supportsViewTransitions: supportsViewTransitions(),

    // å¤„ç†å‡½æ•°
    handleThemeChange,
    handleKeyDown,

    // ARIAå±æ€§
    ariaAttributes,
  };
}

