export function exportExcelReport(historicalReports: DiagnosticReport[]): void {
  // åˆ›å»ºç®€åŒ–çš„Excelæ ¼å¼ï¼ˆå®é™…ä¸Šæ˜¯CSVï¼Œä½†å¯ä»¥è¢«Excelæ‰“å¼€ï¼‰
