/**
 * è·å–æ€§èƒ½çŠ¶æ€
 */
function getPerformanceStatus(report: DiagnosticReport): string {
  const score = report.score || 0;

  if (score >= MAGIC_90) return 'Excellent';
  if (score >= MAGIC_75) return 'Good';
  if (score >= PERCENTAGE_HALF) return 'Needs Improvement';
  return 'Poor';
}

