import fs from 'fs';
import path from 'path';
import { afterEach, beforeEach, describe, expect, it, vi } from 'vitest';
import type { ContentType } from '@/types/content';
import { ContentError } from '@/types/content';
import { logger } from '@/lib/logger';
import { CONTENT_LIMITS } from '@/constants/app-constants';
import { getContentFiles, parseContentFile } from '@/lib/content-parser';

// Mock dependencies
vi.mock('fs');
vi.mock('@/lib/logger');
vi.mock('@/lib/content-utils', () => ({
  CONTENT_DIR: '/mock/content',
  validateFilePath: vi.fn((filePath: string, baseDir: string) => {
    if (filePath.includes('..') || filePath.includes('invalid')) {
      throw new Error('Invalid file path');
    }
    // For single files, return baseDir + basename
    // For directories, return baseDir + filePath
    if (filePath.includes('/')) {
      return path.join(baseDir, filePath);
    }
    return path.join(baseDir, path.basename(filePath));
  }),
}));
vi.mock('@/lib/content-validation', () => ({
  validateContentMetadata: vi.fn(() => ({
    isValid: true,
    errors: [],
    warnings: [],
  })),
}));

const mockFs = vi.mocked(fs) as {
  existsSync: ReturnType<typeof vi.fn>;
  readFileSync: ReturnType<typeof vi.fn>;
  readdirSync: ReturnType<typeof vi.fn>;
};
const mockLogger = vi.mocked(logger);

describe('content-parser', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  afterEach(() => {
    vi.resetAllMocks();
  });

  describe('parseContentFile', () => {
    const mockFilePath = 'test-content.mdx';
    const mockContentType: ContentType = 'posts';
    const mockFileContent = `---
title: Test Content
description: Test description
date: 2024-01-01
author: Test Author
---

# Test Content

This is test content.`;

    beforeEach(() => {
      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue(mockFileContent);
    });

    it('should successfully parse a valid MDX file', () => {
      const result = parseContentFile(mockFilePath, mockContentType);

      expect(result).toEqual({
        slug: 'test-content',
        metadata: {
          title: 'Test Content',
          description: 'Test description',
          date: new Date('2024-01-01'),
          author: 'Test Author',
        },
        content: '\n# Test Content\n\nThis is test content.',
        filePath: '/mock/content/test-content.mdx',
      });
    });

    it('should throw ContentError when file does not exist', () => {
      mockFs.existsSync.mockReturnValue(false);

      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        ContentError,
      );
      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        'Content file not found: test-content.mdx',
      );
    });

    it('should throw ContentError when file is too large', () => {
      const largeContent = 'x'.repeat(CONTENT_LIMITS.MAX_FILE_SIZE + 1);
      mockFs.readFileSync.mockReturnValue(largeContent);

      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        ContentError,
      );
      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        'Content file too large',
      );
    });

    it('should handle invalid file paths', () => {
      expect(() =>
        parseContentFile('../invalid/path.mdx', mockContentType),
      ).toThrow('Invalid file path');
    });

    it('should handle file read errors', () => {
      mockFs.readFileSync.mockImplementation(() => {
        throw new Error('File read error');
      });

      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        ContentError,
      );
      expect(() => parseContentFile(mockFilePath, mockContentType)).toThrow(
        'Failed to parse content file',
      );
    });

    it('should log warnings when content validation fails', async () => {
      const { validateContentMetadata } = vi.mocked(
        await import('@/lib/content-validation'),
      );
      validateContentMetadata.mockReturnValue({
        isValid: false,
        errors: ['Missing required field'],
        warnings: ['Deprecated field used'],
      });

      parseContentFile(mockFilePath, mockContentType);

      expect(mockLogger.warn).toHaveBeenCalledWith(
        'Content validation failed',
        {
          file: mockFilePath,
          errors: ['Missing required field'],
          warnings: ['Deprecated field used'],
        },
      );
    });

    it('should extract slug from filename correctly', () => {
      const result = parseContentFile('my-blog-post.mdx', mockContentType);
      expect(result.slug).toBe('my-blog-post');
    });

    it('should handle files without frontmatter', () => {
      mockFs.readFileSync.mockReturnValue('# Just content without frontmatter');

      const result = parseContentFile(mockFilePath, mockContentType);

      expect(result.metadata).toEqual({});
      expect(result.content).toBe('# Just content without frontmatter');
    });

    it('should handle empty files', () => {
      mockFs.readFileSync.mockReturnValue('');

      const result = parseContentFile(mockFilePath, mockContentType);

      expect(result.metadata).toEqual({});
      expect(result.content).toBe('');
    });

    it('should preserve file path in result', () => {
      const result = parseContentFile(mockFilePath, mockContentType);
      expect(result.filePath).toBe('/mock/content/test-content.mdx');
    });
  });

  describe('getContentFiles', () => {
    const mockContentDir = 'content/blog';

    beforeEach(() => {
      mockFs.existsSync.mockReturnValue(true);
      mockFs.readdirSync.mockReturnValue([
        'post1.mdx',
        'post2.md',
        'post3.en.mdx',
        'post4.zh.mdx',
        'invalid.txt',
        'draft.mdx',
      ] as string[]);
    });

    it('should return all valid content files when no locale specified', () => {
      const result = getContentFiles(mockContentDir);

      expect(result).toEqual([
        '/mock/content/content/blog/post1.mdx',
        '/mock/content/content/blog/post2.md',
        '/mock/content/content/blog/post3.en.mdx',
        '/mock/content/content/blog/post4.zh.mdx',
        '/mock/content/content/blog/draft.mdx',
      ]);
    });

    it('should filter files by locale when specified', () => {
      const result = getContentFiles(mockContentDir, 'en');

      expect(result).toEqual([
        '/mock/content/content/blog/post1.mdx',
        '/mock/content/content/blog/post2.md',
        '/mock/content/content/blog/post3.en.mdx',
        '/mock/content/content/blog/draft.mdx',
      ]);
    });

    it('should filter Chinese locale files correctly', () => {
      const result = getContentFiles(mockContentDir, 'zh');

      expect(result).toEqual([
        '/mock/content/content/blog/post1.mdx',
        '/mock/content/content/blog/post2.md',
        '/mock/content/content/blog/post4.zh.mdx',
        '/mock/content/content/blog/draft.mdx',
      ]);
    });

    it('should return empty array when directory does not exist', () => {
      mockFs.existsSync.mockReturnValue(false);

      const result = getContentFiles(mockContentDir);

      expect(result).toEqual([]);
      expect(mockLogger.warn).toHaveBeenCalledWith(
        'Content directory does not exist',
        { dir: '/mock/content/content/blog' },
      );
    });

    it('should filter out non-markdown files', () => {
      mockFs.readdirSync.mockReturnValue([
        'post1.mdx',
        'post2.md',
        'image.jpg',
        'config.json',
        'readme.txt',
      ] as string[]);

      const result = getContentFiles(mockContentDir);

      expect(result).toEqual([
        '/mock/content/content/blog/post1.mdx',
        '/mock/content/content/blog/post2.md',
      ]);
    });

    it('should handle empty directory', () => {
      mockFs.readdirSync.mockReturnValue([] as string[]);

      const result = getContentFiles(mockContentDir);

      expect(result).toEqual([]);
    });

    it('should handle directory read errors', () => {
      mockFs.readdirSync.mockImplementation(() => {
        throw new Error('Directory read error');
      });

      expect(() => getContentFiles(mockContentDir)).toThrow(
        'Directory read error',
      );
    });

    it('should validate content directory path', () => {
      expect(() => getContentFiles('../invalid/path')).toThrow(
        'Invalid file path',
      );
    });
  });
});

import fs from 'fs';
import path from 'path';
import { afterEach, beforeEach, describe, expect, it, vi } from 'vitest';
import { ContentError } from '@/types/content';
import {
  TEST_COUNT_CONSTANTS,
  TEST_SAMPLE_CONSTANTS,
} from '@/constants/test-constants';
import {
  ALLOWED_EXTENSIONS,
  CONFIG_DIR,
  CONTENT_DIR,
  getContentConfig,
  PAGES_DIR,
  POSTS_DIR,
  validateFilePath,
} from '../content-utils';

// Mock dependencies
vi.mock('fs');
vi.mock('@/lib/logger', () => ({
  logger: {
    warn: vi.fn(),
  },
}));

const mockFs = vi.mocked(fs);

describe('Content Utils', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('Constants', () => {
    it('should define correct directory paths', () => {
      expect(CONTENT_DIR).toBe(path.join(process.cwd(), 'content'));
      expect(POSTS_DIR).toBe(path.join(CONTENT_DIR, 'posts'));
      expect(PAGES_DIR).toBe(path.join(CONTENT_DIR, 'pages'));
      expect(CONFIG_DIR).toBe(path.join(CONTENT_DIR, 'config'));
    });

    it('should define allowed file extensions', () => {
      expect(ALLOWED_EXTENSIONS).toEqual(['.md', '.mdx', '.json']);
      expect(Array.isArray(ALLOWED_EXTENSIONS)).toBe(true);
      expect(ALLOWED_EXTENSIONS.length).toBe(TEST_COUNT_CONSTANTS.TINY);
    });
  });

  describe('validateFilePath', () => {
    const baseDir = '/safe/directory';

    describe('input validation', () => {
      it('should throw error for empty file path', () => {
        expect(() => validateFilePath('', baseDir)).toThrow(ContentError);
        expect(() => validateFilePath('', baseDir)).toThrow(
          'Invalid file path: path must be a non-empty string',
        );
      });

      it('should throw error for null file path', () => {
        expect(() => validateFilePath(null as string, baseDir)).toThrow(
          ContentError,
        );
      });

      it('should throw error for undefined file path', () => {
        expect(() => validateFilePath(undefined as string, baseDir)).toThrow(
          ContentError,
        );
      });

      it('should throw error for non-string file path', () => {
        expect(() =>
          validateFilePath(
            TEST_SAMPLE_CONSTANTS.DECIMAL_SAMPLE as string,
            baseDir,
          ),
        ).toThrow(ContentError);
        expect(() => validateFilePath({} as string, baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath([] as string, baseDir)).toThrow(
          ContentError,
        );
      });
    });

    describe('directory traversal protection', () => {
      it('should throw error for directory traversal attempts', () => {
        expect(() => validateFilePath('../../../etc/passwd', baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath('../../../etc/passwd', baseDir)).toThrow(
          'Invalid file path: directory traversal detected',
        );
      });

      it('should throw error for subtle directory traversal attempts', () => {
        expect(() =>
          validateFilePath('safe/../../../etc/passwd', baseDir),
        ).toThrow(ContentError);
        expect(() => validateFilePath('./../../etc/passwd', baseDir)).toThrow(
          ContentError,
        );
        expect(() =>
          validateFilePath('..\\..\\windows\\system32', baseDir),
        ).toThrow(ContentError);
      });

      it('should allow legitimate relative paths', () => {
        expect(() =>
          validateFilePath('posts/article.md', baseDir),
        ).not.toThrow();
        expect(() =>
          validateFilePath('./posts/article.md', baseDir),
        ).not.toThrow();
        expect(() =>
          validateFilePath('pages/about.mdx', baseDir),
        ).not.toThrow();
      });
    });

    describe('path outside base directory protection', () => {
      it('should throw error when resolved path is outside base directory', () => {
        const outsidePath = '/completely/different/path/file.md';
        expect(() => validateFilePath(outsidePath, baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath(outsidePath, baseDir)).toThrow(
          'File path outside allowed directory',
        );
      });

      it('should allow paths within base directory', () => {
        const safePath = path.join(baseDir, 'posts', 'article.md');
        expect(() => validateFilePath(safePath, baseDir)).not.toThrow();
      });
    });

    describe('file extension validation', () => {
      it('should allow files with permitted extensions', () => {
        expect(() => validateFilePath('article.md', baseDir)).not.toThrow();
        expect(() => validateFilePath('component.mdx', baseDir)).not.toThrow();
        expect(() => validateFilePath('config.json', baseDir)).not.toThrow();
      });

      it('should throw error for files with forbidden extensions', () => {
        expect(() => validateFilePath('script.js', baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath('script.js', baseDir)).toThrow(
          'File extension not allowed: .js',
        );

        expect(() => validateFilePath('executable.exe', baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath('document.pdf', baseDir)).toThrow(
          ContentError,
        );
        expect(() => validateFilePath('image.png', baseDir)).toThrow(
          ContentError,
        );
      });

      it('should allow files without extensions', () => {
        expect(() => validateFilePath('README', baseDir)).not.toThrow();
        expect(() => validateFilePath('LICENSE', baseDir)).not.toThrow();
      });

      it('should handle multiple dots in filename correctly', () => {
        expect(() => validateFilePath('file.backup.md', baseDir)).not.toThrow();
        expect(() =>
          validateFilePath('config.local.json', baseDir),
        ).not.toThrow();
        expect(() => validateFilePath('script.min.js', baseDir)).toThrow(
          ContentError,
        );
      });
    });

    describe('path normalization', () => {
      it('should normalize relative paths correctly', () => {
        const result = validateFilePath('posts/./article.md', baseDir);
        expect(result).toBe(path.join(baseDir, 'posts/article.md'));
      });

      it('should handle absolute paths correctly', () => {
        const absolutePath = path.join(baseDir, 'posts', 'article.md');
        const result = validateFilePath(absolutePath, baseDir);
        expect(result).toBe(absolutePath);
      });

      it('should return normalized absolute path', () => {
        const result = validateFilePath('posts/article.md', baseDir);
        expect(path.isAbsolute(result)).toBe(true);
        expect(result).toBe(path.join(baseDir, 'posts/article.md'));
      });
    });

    describe('edge cases', () => {
      it('should handle empty base directory', () => {
        expect(() => validateFilePath('file.md', '')).not.toThrow();
      });

      it('should handle paths with special characters', () => {
        expect(() => validateFilePath('posts/æ–‡ç« .md', baseDir)).not.toThrow();
        expect(() =>
          validateFilePath('posts/article-with-dashes.md', baseDir),
        ).not.toThrow();
        expect(() =>
          validateFilePath('posts/article_with_underscores.md', baseDir),
        ).not.toThrow();
      });

      it('should handle very long paths', () => {
        const longPath = `${'a'.repeat(200)}.md`;
        expect(() => validateFilePath(longPath, baseDir)).not.toThrow();
      });
    });
  });

  describe('getContentConfig', () => {
    // const mockConfigPath = path.join(CONFIG_DIR, 'content.json');

    beforeEach(() => {
      vi.clearAllMocks();
    });

    it('should return default config when config file does not exist', () => {
      mockFs.existsSync.mockReturnValue(false);

      const config = getContentConfig();

      expect(config).toEqual({
        defaultLocale: 'en',
        supportedLocales: ['en', 'zh'],
        postsPerPage: 10,
        enableDrafts: process.env.NODE_ENV === 'development',
        enableSearch: true,
        enableComments: false,
        autoGenerateExcerpt: true,
        excerptLength: 160,
        dateFormat: 'YYYY-MM-DD',
        timeZone: 'UTC',
      });
    });

    it('should load and merge custom config when file exists', () => {
      const customConfig = {
        postsPerPage: 20,
        enableComments: true,
        customField: 'custom value',
      };

      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue(JSON.stringify(customConfig));

      const config = getContentConfig();

      expect(config).toEqual({
        defaultLocale: 'en',
        supportedLocales: ['en', 'zh'],
        postsPerPage: 20, // Overridden
        enableDrafts: process.env.NODE_ENV === 'development',
        enableSearch: true,
        enableComments: true, // Overridden
        autoGenerateExcerpt: true,
        excerptLength: 160,
        dateFormat: 'YYYY-MM-DD',
        timeZone: 'UTC',
        customField: 'custom value', // Added
      });
    });

    it('should handle JSON parsing errors gracefully', async () => {
      const { logger } = await import('@/lib/logger');

      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue('invalid json content');

      const config = getContentConfig();

      expect(config).toEqual({
        defaultLocale: 'en',
        supportedLocales: ['en', 'zh'],
        postsPerPage: 10,
        enableDrafts: process.env.NODE_ENV === 'development',
        enableSearch: true,
        enableComments: false,
        autoGenerateExcerpt: true,
        excerptLength: 160,
        dateFormat: 'YYYY-MM-DD',
        timeZone: 'UTC',
      });

      expect(logger.warn).toHaveBeenCalledWith(
        'Failed to load content config, using defaults',
        { error: expect.any(Error) },
      );
    });

    it('should handle file system errors gracefully', async () => {
      const { logger } = await import('@/lib/logger');

      mockFs.existsSync.mockImplementation(() => {
        throw new Error('File system error');
      });

      const config = getContentConfig();

      expect(config).toEqual({
        defaultLocale: 'en',
        supportedLocales: ['en', 'zh'],
        postsPerPage: 10,
        enableDrafts: process.env.NODE_ENV === 'development',
        enableSearch: true,
        enableComments: false,
        autoGenerateExcerpt: true,
        excerptLength: 160,
        dateFormat: 'YYYY-MM-DD',
        timeZone: 'UTC',
      });

      expect(logger.warn).toHaveBeenCalledWith(
        'Failed to load content config, using defaults',
        { error: expect.any(Error) },
      );
    });

    it('should validate config file path for security', () => {
      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue('{}');

      expect(() => getContentConfig()).not.toThrow();
      expect(mockFs.existsSync).toHaveBeenCalledWith(
        expect.stringContaining('content.json'),
      );
    });

    it('should handle empty config file', () => {
      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue('{}');

      const config = getContentConfig();

      expect(config).toEqual({
        defaultLocale: 'en',
        supportedLocales: ['en', 'zh'],
        postsPerPage: 10,
        enableDrafts: process.env.NODE_ENV === 'development',
        enableSearch: true,
        enableComments: false,
        autoGenerateExcerpt: true,
        excerptLength: 160,
        dateFormat: 'YYYY-MM-DD',
        timeZone: 'UTC',
      });
    });

    it('should handle partial config override', () => {
      const partialConfig = {
        defaultLocale: 'zh',
        postsPerPage: TEST_COUNT_CONSTANTS.MEDIUM,
      };

      mockFs.existsSync.mockReturnValue(true);
      mockFs.readFileSync.mockReturnValue(JSON.stringify(partialConfig));

      const config = getContentConfig();

      expect(config.defaultLocale).toBe('zh');
      expect(config.postsPerPage).toBe(TEST_COUNT_CONSTANTS.MEDIUM);
      expect(config.enableSearch).toBe(true); // Should keep default
      expect(config.enableComments).toBe(false); // Should keep default
    });
  });
});

