          ...validMetadata,
          tags: [],
        };

        const result = validateContentMetadata(metadata, 'posts');

        // Empty tags array should be valid
        expect(result.isValid).toBe(true);
        expect(result.errors).toHaveLength(0);
      });
    });
  });
});

