  getTestReport(): string {
    const totalTests = this.results.length;
    const passedTests = this.results.filter((r) => r.passed).length;
    const failedTests = totalTests - passedTests;

    let report = `\n=== æ— éšœç¢æ€§æµ‹è¯•æŠ¥å‘Š ===\n`;
    report += `æ€»æµ‹è¯•æ•°: ${totalTests}\n`;
    report += `é€šè¿‡: ${passedTests}\n`;
    report += `å¤±è´¥: ${failedTests}\n`;
    report += `é€šè¿‡ç‡: ${((passedTests / totalTests) * PERCENTAGE_FULL).toFixed(ONE)}%\n\n`;

    this.results.forEach((result, index) => {
      report += `${index + ONE}. ${result.testName}\n`;
      report += `   çŠ¶æ€: ${result.passed ? 'âœ… é€šè¿‡' : 'âŒ å¤±è´¥'}\n`;
      report += `   è¯¦æƒ…: ${result.details}\n`;
      if (result.suggestions) {
        report += `   å»ºè®®: ${result.suggestions.join('; ')}\n`;
      }
      report += '\n';
    });

    return report;
  }

  // ç§æœ‰è¾…åŠ©æ–¹æ³•
  private getFocusableElements(): HTMLElement[] {
    const selector = [
      'a[href]',
      'button:not([disabled])',
      'input:not([disabled])',
      'select:not([disabled])',
      'textarea:not([disabled])',
      '[tabindex]:not([tabindex="-1"])',
      '[data-slot="navigation-menu-trigger"]',
      '[data-slot="navigation-menu-link"]',
    ].join(', ');

    return Array.from(document.querySelectorAll(selector)) as HTMLElement[];
  }

  private validateTabOrder(
    tabOrder: HTMLElement[],
    focusableElements: HTMLElement[],
  ): boolean {
    // ç®€å•éªŒè¯ï¼šæ£€æŸ¥æ˜¯å¦èƒ½éå†æ‰€æœ‰å¯èšç„¦å…ƒç´ 
    return tabOrder.length >= focusableElements.length * MAGIC_0_8; // å…è®¸80%çš„è¦†ç›–ç‡
  }

  private async testElementKeyActivation(
    element: HTMLElement,
    key: string,
  ): Promise<boolean> {
    try {
      const initialState = element.getAttribute('data-state');
      const initialExpanded = element.getAttribute('aria-expanded');

      const keyEvent = new KeyboardEvent('keydown', {
        key,
        bubbles: true,
        cancelable: true,
      });

      element.dispatchEvent(keyEvent);
      await new Promise((resolve) => setTimeout(resolve, PERCENTAGE_FULL));

      const newState = element.getAttribute('data-state');
      const newExpanded = element.getAttribute('aria-expanded');

      // æ£€æŸ¥çŠ¶æ€æ˜¯å¦å‘ç”Ÿå˜åŒ–ï¼ˆè¡¨ç¤ºæ¿€æ´»æˆåŠŸï¼‰
      return initialState !== newState || initialExpanded !== newExpanded;
    } catch {
      return false;
    }
  }
}

