  const zIndexClass = `z-[${config.zIndex}]`;

  return `${positionClass} ${sizeClass} ${zIndexClass}`;
}

/**
 * è·å–å¼€å‘å·¥å…·çš„ z-index å€¼
 */
export function getDevToolZIndex(toolId: string): number {
