      return fallback;
    }
  },
} as const;

/**
 * ç¼“å­˜äº‹ä»¶å·¥å…·å‡½æ•°
 * Cache event utility functions
 */
export const CacheEventUtils = {
  /**
   * åˆ›å»ºç¼“å­˜äº‹ä»¶
   * Create cache event
   */
  createEvent<T>(type: string, data?: T, key?: string): CacheEvent<T> {
    return {
      type: type as CacheEventType,
      timestamp: Date.now(),
      ...(key && { key }),
      ...(data && { data }),
    };
  },

  /**
   * è¿‡æ»¤äº‹ä»¶
   * Filter events
   */
  filterEvents<T>(
    events: CacheEvent<T>[],
    type?: string,
    timeRange?: { start: number; end: number },
  ): CacheEvent<T>[] {
    return events.filter((event) => {
      if (type && event.type !== type) return false;
      if (
        timeRange &&
        (event.timestamp < timeRange.start || event.timestamp > timeRange.end)
      )
        return false;
      return true;
    });
  },

  /**
   * èšåˆäº‹ä»¶ç»Ÿè®¡
   * Aggregate event statistics
   */
  aggregateEvents<T>(events: CacheEvent<T>[]): Record<string, number> {
    const stats: Record<string, number> = {};
    events.forEach((event) => {
      stats[event.type] = (stats[event.type] || ZERO) + ONE;
    });
    return stats;
  },
} as const;

/**
 * ç¼“å­˜è°ƒè¯•å·¥å…·å‡½æ•°
 * Cache debug utility functions
 */
export const CacheDebugUtils = {
  /**
   * ç”Ÿæˆè°ƒè¯•ä¿¡æ¯
   * Generate debug information
   */
  generateDebugInfo(cache: unknown): Record<string, unknown> {
    return {
      timestamp: Date.now(),
      environment: process.env.NODE_ENV,
      memoryUsage: process.memoryUsage?.(),
      cacheInfo: cache,
    };
  },

  /**
   * æ ¼å¼åŒ–è°ƒè¯•è¾“å‡º
   * Format debug output
   */
  formatDebugOutput(info: Record<string, unknown>): string {
    return JSON.stringify(info, null, COUNT_PAIR);
  },

  /**
   * åˆ›å»ºæ€§èƒ½æ ‡è®°
   * Create performance mark
   */
  mark(name: string): void {
    if (typeof performance !== 'undefined' && performance.mark) {
      performance.mark(name);
    }
  },

  /**
   * æµ‹é‡æ€§èƒ½
   * Measure performance
   */
  measure(name: string, startMark: string, endMark?: string): number {
    if (typeof performance !== 'undefined' && performance.measure) {
      performance.measure(name, startMark, endMark);
      const entries = performance.getEntriesByName(name);
      return entries.length > ZERO
        ? (entries[entries.length - ONE]?.duration ?? ZERO)
        : ZERO;
    }
    return ZERO;
  },
} as const;

/**
 * LRU (Least Recently Used) ç¼“å­˜å®ç°
 *
 * æä¾›é«˜æ€§èƒ½çš„ LRU ç¼“å­˜ï¼Œæ”¯æŒ TTLã€æŒä¹…åŒ–å­˜å‚¨å’Œæ€§èƒ½ç›‘æ§
 */

import { COUNT_4, MAGIC_32, MAGIC_8 } from "@/constants/count";
import { COUNT_PAIR, ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type {
  CacheConfig,
  CacheItem,
  CacheStats,
  CacheStorage,
  MetricsCollector
} from '@/lib/i18n-cache-types';

// LRU ç¼“å­˜å®ç°
export class LRUCache<T> implements CacheStorage<T> {
  private cache = new Map<string, CacheItem<T>>();
  private config: CacheConfig;
  private metricsCollector: MetricsCollector;

  constructor(config: CacheConfig, metricsCollector: MetricsCollector) {
    this.config = config;
    this.metricsCollector = metricsCollector;

    if (config.enablePersistence && typeof window !== 'undefined') {
      this.loadFromStorage();
    }
  }

  // è·å–ç¼“å­˜é¡¹
  get(key: string): T | null {
    const item = this.cache.get(key);

    if (!item) {
      this.metricsCollector.recordCacheMiss();
      this.emitEvent('miss', key);
      return null;
    }

    // æ£€æŸ¥æ˜¯å¦è¿‡æœŸ
    if (Date.now() - item.timestamp > item.ttl) {
      this.cache.delete(key);
      this.metricsCollector.recordCacheMiss();
      this.emitEvent('expire', key, item.data);
      return null;
    }

    // æ›´æ–°è®¿é—®é¡ºåº (LRU)
    this.cache.delete(key);
    item.hits += ONE;
    this.cache.set(key, item);

    this.metricsCollector.recordCacheHit();
    this.emitEvent('hit', key, item.data);
    return item.data;
  }

  // è®¾ç½®ç¼“å­˜é¡¹
  set(key: string, value: T, customTtl?: number): void {
    const ttl = customTtl || this.config.ttl;

    // å¦‚æœç¼“å­˜å·²æ»¡ï¼Œåˆ é™¤æœ€æ—§çš„é¡¹
    if (this.cache.size >= this.config.maxSize) {
      const firstKey = this.cache.keys().next().value;
      if (firstKey !== undefined) {
        const removedItem = this.cache.get(firstKey);
        this.cache.delete(firstKey);
        this.emitEvent('delete', firstKey, removedItem?.data);
      }
    }

    const item: CacheItem<T> = {
      data: value,
      timestamp: Date.now(),
      ttl,
      hits: ZERO,
    };

    this.cache.set(key, item);
    this.emitEvent('set', key, value);

    if (this.config.enablePersistence) {
      this.saveToStorage();
    }
  }

  // æ£€æŸ¥æ˜¯å¦å­˜åœ¨
  has(key: string): boolean {
    return this.get(key) !== null;
  }

  // åˆ é™¤ç¼“å­˜é¡¹
  delete(key: string): boolean {
    const item = this.cache.get(key);
    const deleted = this.cache.delete(key);

    if (deleted) {
      this.emitEvent('delete', key, item?.data);
      if (this.config.enablePersistence) {
        this.saveToStorage();
      }
    }

    return deleted;
  }

  // æ¸…ç©ºç¼“å­˜
  clear(): void {
    this.cache.clear();
    this.emitEvent('clear');

    if (this.config.enablePersistence && typeof window !== 'undefined') {
      localStorage.removeItem(this.config.storageKey);
    }
  }

  // è·å–ç¼“å­˜å¤§å°
  size(): number {
    return this.cache.size;
  }

  // è·å–æ‰€æœ‰é”®
  keys(): IterableIterator<string> {
    return this.cache.keys();
  }

  // è·å–æ‰€æœ‰å€¼
  values(): IterableIterator<T> {
    return Array.from(this.cache.values())
      .map((item) => item.data)
      [Symbol.iterator]();
  }

  // è·å–æ‰€æœ‰æ¡ç›®
  entries(): IterableIterator<[string, T]> {
    const entries = Array.from(this.cache.entries()).map(
      ([key, item]) => [key, item.data] as [string, T],
    );
    return entries[Symbol.iterator]();
  }

  // è·å–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
  getStats(): CacheStats {
    const items = Array.from(this.cache.values());
    return {
      size: this.cache.size,
      totalHits: items.reduce((sum, item) => sum + item.hits, ZERO),
      averageAge:
        items.length > ZERO
          ? items.reduce(
              (sum, item) => sum + (Date.now() - item.timestamp),
              0,
            ) / items.length
          : ZERO,
    };
  }

  // è·å–è¯¦ç»†ç»Ÿè®¡ä¿¡æ¯
  getDetailedStats() {
    const items = Array.from(this.cache.values());
    const now = Date.now();

    const ages = items.map((item) => now - item.timestamp);
    const hits = items.map((item) => item.hits);
    const ttls = items.map((item) => item.ttl);

    return {
      ...this.getStats(),
      memoryUsage: this.estimateMemoryUsage(),
      ageDistribution: {
        min: Math.min(...ages),
        max: Math.max(...ages),
        median: this.calculateMedian(ages),
        average:
          ages.length > ZERO ? ages.reduce((a, b) => a + b, ZERO) / ages.length : ZERO,
      },
      hitDistribution: {
        min: Math.min(...hits),
        max: Math.max(...hits),
        median: this.calculateMedian(hits),
        average:
          hits.length > ZERO ? hits.reduce((a, b) => a + b, ZERO) / hits.length : ZERO,
      },
      ttlDistribution: {
        min: Math.min(...ttls),
        max: Math.max(...ttls),
        median: this.calculateMedian(ttls),
        average:
          ttls.length > ZERO ? ttls.reduce((a, b) => a + b, ZERO) / ttls.length : ZERO,
      },
      expiredItems: items.filter((item) => now - item.timestamp > item.ttl)
        .length,
      utilizationRate: (this.cache.size / this.config.maxSize) * PERCENTAGE_FULL,
    };
  }

  // æ¸…ç†è¿‡æœŸé¡¹
  cleanup(): number {
    const now = Date.now();
    let cleanedCount = ZERO;

    for (const [key, item] of this.cache.entries()) {
      if (now - item.timestamp > item.ttl) {
        this.cache.delete(key);
        this.emitEvent('expire', key, item.data);
        cleanedCount += ONE;
      }
    }

    if (cleanedCount > ZERO && this.config.enablePersistence) {
      this.saveToStorage();
    }

    return cleanedCount;
  }

  // é¢„çƒ­ç¼“å­˜
  warmup(entries: Array<{ key: string; value: T; ttl?: number }>): void {
    entries.forEach(({ key, value, ttl }) => {
      this.set(key, value, ttl);
    });
  }

  // æ‰¹é‡è·å–
  getMultiple(keys: string[]): Map<string, T | null> {
    const result = new Map<string, T | null>();
    keys.forEach((key) => {
      result.set(key, this.get(key));
    });
    return result;
  }

  // æ‰¹é‡è®¾ç½®
  setMultiple(entries: Array<{ key: string; value: T; ttl?: number }>): void {
    entries.forEach(({ key, value, ttl }) => {
      this.set(key, value, ttl);
    });
  }

  // æ‰¹é‡åˆ é™¤
  deleteMultiple(keys: string[]): number {
    let deletedCount = ZERO;
    keys.forEach((key) => {
      if (this.delete(key)) {
        deletedCount += ONE;
      }
    });
    return deletedCount;
  }

  // ä»å­˜å‚¨åŠ è½½
  private loadFromStorage(): void {
    try {
      const stored = localStorage.getItem(this.config.storageKey);
      if (stored) {
        const data = JSON.parse(stored);
        if (data && typeof data === 'object') {
          Object.entries(data).forEach(([key, item]) => {
            if (this.isValidCacheItem(item)) {
              this.cache.set(key, item as CacheItem<T>);
            }
          });
        }
      }
    } catch (error) {
      // Use centralized logger to avoid console noise in production
      logger.warn('Failed to load cache from storage:', error as unknown);
    }
  }

  // ä¿å­˜åˆ°å­˜å‚¨
  private saveToStorage(): void {
    if (typeof window === 'undefined') return;

    try {
      const data = Object.fromEntries(this.cache.entries());
      localStorage.setItem(this.config.storageKey, JSON.stringify(data));
    } catch (error) {
      logger.warn('Failed to save cache to storage:', error as unknown);
    }
  }

  // éªŒè¯ç¼“å­˜é¡¹
  private isValidCacheItem(item: unknown): boolean {
    return (
      typeof item === 'object' &&
      item !== null &&
      'data' in item &&
      'timestamp' in item &&
      'ttl' in item &&
      'hits' in item
    );
  }

  // ä¼°ç®—å†…å­˜ä½¿ç”¨é‡
  private estimateMemoryUsage(): number {
    let totalSize = ZERO;

    for (const [key, item] of this.cache.entries()) {
      // ä¼°ç®—é”®çš„å¤§å°
      totalSize += key.length * COUNT_PAIR; // UTF-16 å­—ç¬¦

      // ä¼°ç®—å€¼çš„å¤§å°
      totalSize += this.estimateObjectSize(item.data);

      // ä¼°ç®—ç¼“å­˜é¡¹å…ƒæ•°æ®çš„å¤§å°
      totalSize += MAGIC_32; // timestamp, ttl, hits ç­‰
    }

    return totalSize;
  }

  // ä¼°ç®—å¯¹è±¡å¤§å°
  private estimateObjectSize(obj: unknown): number {
    if (obj === null || obj === undefined) return ZERO;

    if (typeof obj === 'string') {
      return obj.length * COUNT_PAIR; // UTF-16
    }

    if (typeof obj === 'number') {
      return MAGIC_8; // 64-bit number
    }

    if (typeof obj === 'boolean') {
      return COUNT_4;
    }

    if (Array.isArray(obj)) {
      return obj.reduce((sum, item) => sum + this.estimateObjectSize(item), ZERO);
    }

    if (typeof obj === 'object') {
      return Object.entries(obj).reduce((sum, [key, value]) => {
        return sum + key.length * COUNT_PAIR + this.estimateObjectSize(value);
      }, ZERO);
    }

    return ZERO;
  }

  // è®¡ç®—ä¸­ä½æ•°
  private calculateMedian(numbers: number[]): number {
    if (numbers.length === ZERO) return ZERO;

    const sorted = [...numbers].sort((a, b) => a - b);
    const mid = Math.floor(sorted.length / 2);

    return sorted.length % COUNT_PAIR === ZERO
      ? ((sorted[mid - ONE] || ZERO) + (sorted[mid] || ZERO)) / COUNT_PAIR
      : sorted[mid] || ZERO;
  }

  // å‘å‡ºäº‹ä»¶
  private emitEvent(type: string, key?: string, data?: T): void {
    // è¿™é‡Œå¯ä»¥æ·»åŠ äº‹ä»¶å‘å°„é€»è¾‘
    // ç”±äº MetricsCollector å·²ç»å¤„ç†äº†åŸºæœ¬äº‹ä»¶ï¼Œè¿™é‡Œä¸»è¦ç”¨äºæ‰©å±•
    // TODO: å®ç°äº‹ä»¶å‘å°„é€»è¾‘
    // å½“å‰æš‚æ—¶ä¸åˆ›å»ºäº‹ä»¶å¯¹è±¡ï¼Œé¿å…æœªä½¿ç”¨å˜é‡è­¦å‘Š
    void type;
    void key;
    void data; // é¿å…å‚æ•°æœªä½¿ç”¨è­¦å‘Š
  }
}

// åˆ›å»º LRU ç¼“å­˜å®ä¾‹çš„å·¥å‚å‡½æ•°
export function createLRUCache<T>(
  config: CacheConfig,
  metricsCollector: MetricsCollector,
): LRUCache<T> {
  return new LRUCache<T>(config, metricsCollector);
}

// å¯¼å‡ºç±»å‹åˆ«å
export type { LRUCache as Cache };

/**
 * å›½é™…åŒ–æ€§èƒ½æŒ‡æ ‡æ”¶é›†å™¨
 *
 * è´Ÿè´£æ”¶é›†å’Œç®¡ç† i18n ç³»ç»Ÿçš„æ€§èƒ½æŒ‡æ ‡ï¼ŒåŒ…æ‹¬ç¼“å­˜å‘½ä¸­ç‡ã€åŠ è½½æ—¶é—´ã€é”™è¯¯ç‡ç­‰
 */

import { COUNT_35, MAGIC_20, MAGIC_40, MAGIC_70, MAGIC_80, MAGIC_95 } from "@/constants/count";
import { MAGIC_0_1, MAGIC_0_5, MAGIC_0_9, MAGIC_0_95, MAGIC_0_99 } from "@/constants/decimal";
import { ANGLE_90_DEG, COUNT_PAIR, COUNT_TEN, DAYS_PER_MONTH, HTTP_OK, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, PERCENTAGE_QUARTER, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";
import { logger } from '@/lib/logger';
import type { I18nMetrics, Locale } from '@/types/i18n';
import type {
  CacheEvent,
  CacheEventListener,
  MetricsCollector,
} from '@/lib/i18n-cache-types';

// æ€§èƒ½æŒ‡æ ‡æ”¶é›†å™¨å®ç°
export class I18nMetricsCollector implements MetricsCollector {
  private metrics: I18nMetrics = {
    loadTime: ZERO,
    cacheHitRate: ZERO,
    errorRate: ZERO,
    translationCoverage: ZERO,
    localeUsage: { en: ZERO, zh: ZERO },
  };

  private totalRequests = ZERO;
  private cacheHits = ZERO;
  private errors = ZERO;
  private loadTimes: number[] = [];
  private localeUsageCount: Record<string, number> = {};
  private eventListeners: Map<string, CacheEventListener[]> = new Map();
  private startTime = Date.now();

  // è®°å½•åŠ è½½æ—¶é—´
  recordLoadTime(time: number): void {
    this.loadTimes.push(time);

    // ä¿æŒæœ€è¿‘ 100 æ¬¡è®°å½•ä»¥è®¡ç®—å¹³å‡å€¼
    if (this.loadTimes.length > PERCENTAGE_FULL) {
      this.loadTimes = this.loadTimes.slice(-PERCENTAGE_FULL);
    }

    this.metrics.loadTime =
      this.loadTimes.reduce((a, b) => a + b, ZERO) / this.loadTimes.length;

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: { loadTime: time },
    });
  }

  // è®°å½•ç¼“å­˜å‘½ä¸­
  recordCacheHit(): void {
    this.cacheHits += ONE;
    this.totalRequests += ONE;
    this.updateCacheHitRate();

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: {
        totalHits: this.cacheHits,
        totalRequests: this.totalRequests,
        hitRate: this.metrics.cacheHitRate,
      },
    });
  }

  // è®°å½•ç¼“å­˜æœªå‘½ä¸­
  recordCacheMiss(): void {
    this.totalRequests += ONE;
    this.updateCacheHitRate();

    this.emitEvent({
      type: 'miss',
      timestamp: Date.now(),
      metadata: {
        totalRequests: this.totalRequests,
        hitRate: this.metrics.cacheHitRate,
      },
    });
  }

  // è®°å½•é”™è¯¯
  recordError(): void {
    this.errors += ONE;
    this.updateErrorRate();

    this.emitEvent({
      type: 'preload_error',
      timestamp: Date.now(),
      metadata: {
        totalErrors: this.errors,
        errorRate: this.metrics.errorRate,
      },
    });
  }

  // è®°å½•è¯­è¨€ä½¿ç”¨æƒ…å†µ
  recordLocaleUsage(locale: Locale): void {
    this.localeUsageCount[locale] = (this.localeUsageCount[locale] || ZERO) + ONE;
    this.updateLocaleUsage();

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: {
        locale,
        usageCount: this.localeUsageCount[locale],
        totalUsage: this.getTotalLocaleUsage(),
      },
    });
  }

  // è·å–å½“å‰æŒ‡æ ‡
  getMetrics(): I18nMetrics {
    return { ...this.metrics };
  }

  // é‡ç½®æŒ‡æ ‡
  reset(): void {
    this.metrics = {
      loadTime: ZERO,
      cacheHitRate: ZERO,
      errorRate: ZERO,
      translationCoverage: ZERO,
      localeUsage: { en: ZERO, zh: ZERO },
    };

    this.totalRequests = ZERO;
    this.cacheHits = ZERO;
    this.errors = ZERO;
    this.loadTimes = [];
    this.localeUsageCount = {};
    this.startTime = Date.now();

    this.emitEvent({
      type: 'clear',
      timestamp: Date.now(),
      metadata: { reason: 'metrics_reset' },
    });
  }

  // è·å–è¯¦ç»†ç»Ÿè®¡ä¿¡æ¯
  getDetailedStats() {
    const uptime = Date.now() - this.startTime;
    const requestsPerMinute =
      this.totalRequests > ZERO ? this.totalRequests / (uptime / MINUTE_MS) : ZERO;

    return {
      uptime,
      totalRequests: this.totalRequests,
      cacheHits: this.cacheHits,
      cacheMisses: this.totalRequests - this.cacheHits,
      errors: this.errors,
      requestsPerMinute,
      averageLoadTime: this.metrics.loadTime,
      loadTimePercentiles: this.calculateLoadTimePercentiles(),
      localeDistribution: this.getLocaleDistribution(),
      performanceGrade: this.calculatePerformanceGrade(),
    };
  }

  // æ·»åŠ äº‹ä»¶ç›‘å¬å™¨
  addEventListener(eventType: string, listener: CacheEventListener): void {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  // ç§»é™¤äº‹ä»¶ç›‘å¬å™¨
  removeEventListener(eventType: string, listener: CacheEventListener): void {
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  // å‘å‡ºäº‹ä»¶
  private emitEvent(event: CacheEvent): void {
    const listeners = this.eventListeners.get(event.type);
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in cache event listener:', error);
        }
      });
    }

    // ä¹Ÿå‘é€ç»™é€šç”¨ç›‘å¬å™¨
    const allListeners = this.eventListeners.get('*');
    if (allListeners) {
      allListeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in cache event listener:', error);
        }
      });
    }
  }

  // æ›´æ–°ç¼“å­˜å‘½ä¸­ç‡
  private updateCacheHitRate(): void {
    this.metrics.cacheHitRate =
      this.totalRequests > ZERO ? (this.cacheHits / this.totalRequests) * PERCENTAGE_FULL : ZERO;
  }

  // æ›´æ–°é”™è¯¯ç‡
  private updateErrorRate(): void {
    this.metrics.errorRate =
      this.totalRequests > ZERO ? (this.errors / this.totalRequests) * PERCENTAGE_FULL : ZERO;
  }

  // æ›´æ–°è¯­è¨€ä½¿ç”¨æƒ…å†µ
  private updateLocaleUsage(): void {
    const total = this.getTotalLocaleUsage();
    if (total > ZERO) {
      this.metrics.localeUsage = Object.keys(this.localeUsageCount).reduce(
        (acc, locale) => {
          acc[locale as Locale] =
            ((this.localeUsageCount[locale] ?? ZERO) / total) * PERCENTAGE_FULL;
          return acc;
        },
        {} as Record<Locale, number>,
      );
    }
  }

  // è·å–æ€»è¯­è¨€ä½¿ç”¨æ¬¡æ•°
  private getTotalLocaleUsage(): number {
    return Object.values(this.localeUsageCount).reduce(
      (sum, count) => sum + count,
      ZERO,
    );
  }

  // è®¡ç®—åŠ è½½æ—¶é—´ç™¾åˆ†ä½æ•°
  private calculateLoadTimePercentiles() {
    if (this.loadTimes.length === ZERO) {
      return { p50: ZERO, p90: ZERO, p95: ZERO, p99: ZERO };
    }

    const sorted = [...this.loadTimes].sort((a, b) => a - b);
    const len = sorted.length;

    return {
      p50: sorted[Math.floor(len * MAGIC_0_5)],
      p90: sorted[Math.floor(len * MAGIC_0_9)],
      p95: sorted[Math.floor(len * MAGIC_0_95)],
      p99: sorted[Math.floor(len * MAGIC_0_99)],
    };
  }

  // è·å–è¯­è¨€åˆ†å¸ƒ
  private getLocaleDistribution() {
    const total = this.getTotalLocaleUsage();
    return Object.entries(this.localeUsageCount).map(([locale, count]) => ({
      locale,
      count,
      percentage: total > ZERO ? (count / total) * PERCENTAGE_FULL : ZERO,
    }));
  }

  // è®¡ç®—æ€§èƒ½ç­‰çº§
  private calculatePerformanceGrade(): 'A' | 'B' | 'C' | 'D' | 'F' {
    const hitRate = this.metrics.cacheHitRate;
    const {errorRate} = this.metrics;
    const avgLoadTime = this.metrics.loadTime;

    // åŸºäºå¤šä¸ªæŒ‡æ ‡è®¡ç®—ç»¼åˆè¯„åˆ†
    let score = ZERO;

    // ç¼“å­˜å‘½ä¸­ç‡è¯„åˆ† (40%)
    if (hitRate >= MAGIC_95) score += MAGIC_40;
    else if (hitRate >= ANGLE_90_DEG) score += COUNT_35;
    else if (hitRate >= MAGIC_80) score += DAYS_PER_MONTH;
    else if (hitRate >= MAGIC_70) score += MAGIC_20;
    else score += COUNT_TEN;

    // é”™è¯¯ç‡è¯„åˆ† (30%)
    if (errorRate <= MAGIC_0_1) score += DAYS_PER_MONTH;
    else if (errorRate <= MAGIC_0_5) score += PERCENTAGE_QUARTER;
    else if (errorRate <= ONE) score += MAGIC_20;
    else if (errorRate <= COUNT_PAIR) score += COUNT_TEN;
    else score += ZERO;

    // å¹³å‡åŠ è½½æ—¶é—´è¯„åˆ† (30%)
    if (avgLoadTime <= COUNT_TEN) score += DAYS_PER_MONTH;
    else if (avgLoadTime <= PERCENTAGE_HALF) score += PERCENTAGE_QUARTER;
    else if (avgLoadTime <= PERCENTAGE_FULL) score += MAGIC_20;
    else if (avgLoadTime <= HTTP_OK) score += COUNT_TEN;
    else score += ZERO;

    // æ ¹æ®æ€»åˆ†ç¡®å®šç­‰çº§
    if (score >= ANGLE_90_DEG) return 'A';
    if (score >= MAGIC_80) return 'B';
    if (score >= MAGIC_70) return 'C';
    if (score >= SECONDS_PER_MINUTE) return 'D';
    return 'F';
  }

  // ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
  generatePerformanceReport() {
    const stats = this.getDetailedStats();
    const percentiles = this.calculateLoadTimePercentiles();
    const grade = this.calculatePerformanceGrade();

    return {
      summary: {
        grade,
        uptime: stats.uptime,
        totalRequests: stats.totalRequests,
        averageLoadTime: stats.averageLoadTime,
        cacheHitRate: this.metrics.cacheHitRate,
        errorRate: this.metrics.errorRate,
      },
      performance: {
        requestsPerMinute: stats.requestsPerMinute,
        loadTimePercentiles: percentiles,
        cacheEfficiency: {
          hits: stats.cacheHits,
          misses: stats.cacheMisses,
          hitRate: this.metrics.cacheHitRate,
        },
      },
      usage: {
        localeDistribution: stats.localeDistribution,
        totalErrors: stats.errors,
      },
      recommendations: this.generateRecommendations(stats, grade),
    };
  }

  // ç”Ÿæˆæ€§èƒ½å»ºè®®
  private generateRecommendations(
    stats: Record<string, unknown>,
    grade: string,
  ): string[] {
    const recommendations: string[] = [];

    if (this.metrics.cacheHitRate < MAGIC_80) {
      recommendations.push('è€ƒè™‘å¢åŠ ç¼“å­˜å¤§å°æˆ–è°ƒæ•´ TTL ä»¥æé«˜ç¼“å­˜å‘½ä¸­ç‡');
    }

    if (this.metrics.errorRate > ONE) {
      recommendations.push('é”™è¯¯ç‡è¾ƒé«˜ï¼Œå»ºè®®æ£€æŸ¥ç½‘ç»œè¿æ¥å’Œç¿»è¯‘æ–‡ä»¶å®Œæ•´æ€§');
    }

    if (this.metrics.loadTime > PERCENTAGE_FULL) {
      recommendations.push(
        'å¹³å‡åŠ è½½æ—¶é—´è¾ƒé•¿ï¼Œè€ƒè™‘å¯ç”¨é¢„åŠ è½½æˆ–ä¼˜åŒ–ç¿»è¯‘æ–‡ä»¶å¤§å°',
      );
    }

    const requestsPerMinute =
      typeof stats.requestsPerMinute === 'number' ? stats.requestsPerMinute : ZERO;
    if (requestsPerMinute > PERCENTAGE_FULL) {
      recommendations.push('è¯·æ±‚é¢‘ç‡è¾ƒé«˜ï¼Œå»ºè®®å¢åŠ ç¼“å­˜å®¹é‡');
    }

    if (grade === 'D' || grade === 'F') {
      recommendations.push('æ•´ä½“æ€§èƒ½éœ€è¦æ”¹è¿›ï¼Œå»ºè®®å…¨é¢ä¼˜åŒ–ç¼“å­˜ç­–ç•¥');
    }

    return recommendations;
  }
}

