// æ€§èƒ½ç›‘æ§å™¨
export class PerformanceMonitor {
  private config: MonitoringConfig;
  private eventCollector: EventCollector;
  private performanceData: PerformanceData = {
    loadTimes: [],
    cacheHits: ZERO,
    cacheMisses: ZERO,
    errors: ZERO,
    totalRequests: ZERO,
  };

  constructor(config: MonitoringConfig, eventCollector: EventCollector) {
    this.config = config;
    this.eventCollector = eventCollector;
  }

  recordLoadTime(time: number, locale: Locale): void {
    if (!this.config.enablePerformanceTracking) return;

    this.performanceData.loadTimes.push(time);
    this.performanceData.totalRequests += ONE;

    // Check if load time exceeds threshold
    if (time > this.config.performanceThresholds.translationLoadTime) {
      this.eventCollector.addEvent({
        type: 'performance_issue' as MonitoringEventType,
        level: 'warning' as ErrorLevel,
        locale,
        message: `Translation load time exceeded threshold: ${time}ms`,
        metadata: {
          loadTime: time,
          threshold: this.config.performanceThresholds.translationLoadTime,
        },
      });
    }
  }

  recordCacheHit(locale: Locale): void {
    this.performanceData.cacheHits += ONE;
    this.performanceData.totalRequests += ONE;
    this.checkCacheHitRate(locale);
  }

  recordCacheMiss(locale: Locale): void {
    this.performanceData.cacheMisses += ONE;
    this.performanceData.totalRequests += ONE;
    this.checkCacheHitRate(locale);

    this.eventCollector.addEvent({
      type: 'cache_miss' as MonitoringEventType,
      level: 'info' as ErrorLevel,
      locale,
      message: 'Translation cache miss',
      metadata: {
        cacheHitRate: this.getCacheHitRate(),
        totalRequests: this.performanceData.totalRequests,
      },
    });
  }

  recordError(error: TranslationError, locale: Locale): void {
    this.performanceData.errors += ONE;
    this.performanceData.totalRequests += ONE;

    const level: ErrorLevel =
      error.code === 'MISSING_KEY' ? ErrorLevel.WARNING : ErrorLevel.ERROR;

    this.eventCollector.addEvent({
      type: 'translation_error' as MonitoringEventType,
      level,
      locale,
      message: error.message,
      metadata: {
        errorCode: error.code,
        key: error.key || '',
        params: error.params || {},
        errorRate: this.getErrorRate(),
      },
    });
  }

  private checkCacheHitRate(locale: Locale): void {
    const hitRate = this.getCacheHitRate();
    if (hitRate < this.config.performanceThresholds.cacheHitRate) {
      this.eventCollector.addEvent({
        type: 'performance_issue' as MonitoringEventType,
        level: 'warning' as ErrorLevel,
        locale,
        message: `Cache hit rate below threshold: ${hitRate.toFixed(COUNT_PAIR)}%`,
        metadata: {
          cacheHitRate: hitRate,
          threshold: this.config.performanceThresholds.cacheHitRate,
        },
      });
    }
  }

  private getCacheHitRate(): number {
    const total =
      this.performanceData.cacheHits + this.performanceData.cacheMisses;
    return total > ZERO
      ? (this.performanceData.cacheHits / total) *
          CACHE_LIMITS.MAX_CACHE_ENTRIES
      : ZERO;
  }

  private getErrorRate(): number {
    return this.performanceData.totalRequests > ZERO
      ? (this.performanceData.errors / this.performanceData.totalRequests) *
          CACHE_LIMITS.MAX_CACHE_ENTRIES
      : ZERO;
  }

  getMetrics(): I18nMetrics {
    const avgLoadTime =
      this.performanceData.loadTimes.length > ZERO
        ? this.performanceData.loadTimes.reduce((a, b) => a + b, ZERO) /
          this.performanceData.loadTimes.length
        : ZERO;

    return {
      loadTime: avgLoadTime,
      cacheHitRate: this.getCacheHitRate(),
      errorRate: this.getErrorRate(),
      translationCoverage: ZERO, // To be calculated by quality monitor
      localeUsage: { en: ZERO, zh: ZERO }, // To be calculated by usage tracker
    };
  }

  getPerformanceData(): PerformanceData {
    return { ...this.performanceData };
  }

  updateConfig(newConfig: Partial<MonitoringConfig>): void {
    this.config = { ...this.config, ...newConfig };
  }

  reset(): void {
    this.performanceData = {
      loadTimes: [],
      cacheHits: ZERO,
      cacheMisses: ZERO,
      errors: ZERO,
      totalRequests: ZERO,
    };
  }
}

import {
  CACHE_DURATIONS,
  CACHE_LIMITS,
  PERFORMANCE_THRESHOLDS,
} from '@/constants/i18n-constants';
import { ANIMATION_DURATION_SLOW, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, COUNT_TRIPLE, HTTP_OK, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import { cache } from 'react';

/**
 * ç¼“å­˜é¡¹æ¥å£
 */
interface CacheItem {
  value: unknown;
  timestamp: number;
}

// ç¿»è¯‘ç¼“å­˜ç®¡ç†å™¨
export class TranslationCache {
  private static instance: TranslationCache;
  private cache = new Map<string, CacheItem>();
  private readonly maxSize = CACHE_LIMITS.MAX_PERFORMANCE_DATA_POINTS;
  private readonly ttl = CACHE_DURATIONS.TRANSLATION_CACHE;

  static getInstance(): TranslationCache {
    if (!TranslationCache.instance) {
      TranslationCache.instance = new TranslationCache();
    }
    return TranslationCache.instance;
  }

  // ç¼“å­˜ç¿»è¯‘æ¶ˆæ¯
  set(key: string, value: unknown): void {
    if (this.cache.size >= this.maxSize) {
      // LRUæ¸…ç†
      const firstKey = this.cache.keys().next().value;
      if (firstKey) {
        this.cache.delete(firstKey);
      }
    }

    this.cache.set(key, {
      value,
      timestamp: Date.now(),
    });
  }

  // è·å–ç¼“å­˜çš„ç¿»è¯‘æ¶ˆæ¯
  get(key: string): unknown | null {
    const cached = this.cache.get(key);
    if (!cached) return null;

    // æ£€æŸ¥æ˜¯å¦è¿‡æœŸ
    if (Date.now() - cached.timestamp > this.ttl) {
      this.cache.delete(key);
      return null;
    }

    return cached.value;
  }

  // æ¸…ç†è¿‡æœŸç¼“å­˜
  cleanup(): void {
    const now = Date.now();
    for (const [key, cached] of this.cache.entries()) {
      if (now - cached.timestamp > this.ttl) {
        this.cache.delete(key);
      }
    }
  }

  // è·å–ç¼“å­˜ç»Ÿè®¡
  getStats() {
    return {
      size: this.cache.size,
      maxSize: this.maxSize,
      hitRate: this.calculateHitRate(),
    };
  }

  private calculateHitRate(): number {
    // ç®€åŒ–çš„å‘½ä¸­ç‡è®¡ç®—
    return this.cache.size > ZERO ? PERFORMANCE_THRESHOLDS.EXCELLENT : ZERO;
  }
}

// ç¼“å­˜çš„æ¶ˆæ¯åŠ è½½å™¨
export const getCachedMessages = cache(async (locale: string) => {
  const cacheInstance = TranslationCache.getInstance();
  const cacheKey = `messages-${locale}`;

  // æ£€æŸ¥ç¼“å­˜
  const cached = cacheInstance.get(cacheKey);
  if (cached) {
    return cached;
  }

  // åŠ è½½æ¶ˆæ¯
  try {
    const messages = (await import(`../../messages/${locale}.json`)).default;
    cacheInstance.set(cacheKey, messages);
    return messages;
  } catch (error) {
    logger.error(`Failed to load messages for locale ${locale}:`, error);
    return {};
  }
});

// Reactç¼“å­˜çš„ç¿»è¯‘å‡½æ•°
export const getCachedTranslations = cache(
  async (locale: string, namespace?: string) => {
    const messages = await getCachedMessages(locale);

    if (namespace) {
      return messages[namespace] || {};
    }

    return messages;
  },
);

// é¢„åŠ è½½ç¿»è¯‘æ¶ˆæ¯
export async function preloadTranslations(locales: string[]): Promise<void> {
  const promises = locales.map((locale) => getCachedMessages(locale));
  await Promise.all(promises);
}

// æ€§èƒ½ç›‘æ§
export class I18nPerformanceMonitor {
  private static metrics = {
    loadTime: [] as number[],
    cacheHits: ZERO,
    cacheMisses: ZERO,
    errors: ZERO,
  };

  static recordLoadTime(time: number): void {
    this.metrics.loadTime.push(time);
    // ä¿æŒæœ€è¿‘è®°å½•æ•°é‡é™åˆ¶
    if (this.metrics.loadTime.length > CACHE_LIMITS.MAX_CACHE_ENTRIES) {
      this.metrics.loadTime.shift();
    }
  }

  static recordCacheHit(): void {
    this.metrics.cacheHits += ONE;
  }

  static recordCacheMiss(): void {
    this.metrics.cacheMisses += ONE;
  }

  static recordError(): void {
    this.metrics.errors += ONE;
  }

  static getMetrics() {
    const loadTimes = this.metrics.loadTime;
    const avgLoadTime =
      loadTimes.length > ZERO
        ? loadTimes.reduce((a, b) => a + b, ZERO) / loadTimes.length
        : ZERO;

    const totalRequests = this.metrics.cacheHits + this.metrics.cacheMisses;
    const cacheHitRate =
      totalRequests > ZERO
        ? ((this.metrics.cacheHits / totalRequests) *
            PERFORMANCE_THRESHOLDS.MAX_RESPONSE_TIME) /
          COUNT_TEN
        : ZERO;

    return {
      averageLoadTime: avgLoadTime,
      cacheHitRate,
      totalErrors: this.metrics.errors,
      totalRequests,
    };
  }

  static reset(): void {
    this.metrics = {
      loadTime: [],
      cacheHits: ZERO,
      cacheMisses: ZERO,
      errors: ZERO,
    };
  }
}

// æ€§èƒ½åŸºå‡†ç›®æ ‡
export const PERFORMANCE_TARGETS = {
  TRANSLATION_LOAD_TIME: {
    excellent: PERCENTAGE_HALF, // < 50ms
    good: PERCENTAGE_FULL, // < 100ms
    acceptable: HTTP_OK, // < 200ms
    poor: ANIMATION_DURATION_SLOW, // > 500ms
  },

  CACHE_HIT_RATE: {
    excellent: PERFORMANCE_THRESHOLDS.EXCELLENT + COUNT_TRIPLE, // > 98%
    good: PERFORMANCE_THRESHOLDS.EXCELLENT, // > 95%
    acceptable: PERFORMANCE_THRESHOLDS.GOOD + COUNT_TEN, // > 90%
    poor: PERFORMANCE_THRESHOLDS.GOOD, // < 80%
  },
};

// æ€§èƒ½è¯„ä¼°å‡½æ•°
export function evaluatePerformance(
  metrics: ReturnType<typeof I18nPerformanceMonitor.getMetrics>,
) {
  const loadTimeScore = getPerformanceScore(
    metrics.averageLoadTime,
    PERFORMANCE_TARGETS.TRANSLATION_LOAD_TIME,
  );

  const cacheScore = getPerformanceScore(
    metrics.cacheHitRate,
    PERFORMANCE_TARGETS.CACHE_HIT_RATE,
    true,
  );

  const overallScore = (loadTimeScore + cacheScore) / COUNT_PAIR;

  return {
    loadTimeScore,
    cacheScore,
    overallScore,
    grade: getGrade(overallScore),
  };
}

function getPerformanceScore(
  value: number,
  targets: unknown,
  higherIsBetter = false,
): number {
  // ç±»å‹å®ˆå«ï¼šç¡®ä¿targetsæ˜¯ä¸€ä¸ªåŒ…å«æ€§èƒ½é˜ˆå€¼çš„å¯¹è±¡
  const safeTargets = targets as {
    excellent: number;
    good: number;
    acceptable: number;
  } | null;
  if (!safeTargets || typeof safeTargets !== 'object') {
    return PERFORMANCE_THRESHOLDS.POOR;
  }

  if (higherIsBetter) {
    if (value >= safeTargets.excellent) return CACHE_LIMITS.MAX_CACHE_ENTRIES;
    if (value >= safeTargets.good) return PERFORMANCE_THRESHOLDS.GOOD;
    if (value >= safeTargets.acceptable) return PERFORMANCE_THRESHOLDS.FAIR;
    return PERFORMANCE_THRESHOLDS.POOR;
  }

  if (value <= safeTargets.excellent) return CACHE_LIMITS.MAX_CACHE_ENTRIES;
  if (value <= safeTargets.good) return PERFORMANCE_THRESHOLDS.GOOD;
  if (value <= safeTargets.acceptable) return PERFORMANCE_THRESHOLDS.FAIR;
  return PERFORMANCE_THRESHOLDS.POOR;
}

function getGrade(score: number): string {
  if (score >= PERFORMANCE_THRESHOLDS.EXCELLENT - COUNT_FIVE) return 'A';
  if (score >= PERFORMANCE_THRESHOLDS.GOOD) return 'B';
  if (score >= PERFORMANCE_THRESHOLDS.GOOD - COUNT_TEN) return 'C';
  if (score >= PERFORMANCE_THRESHOLDS.FAIR) return 'D';
  return 'F';
}

/**
 * ç¿»è¯‘é¢„åŠ è½½å™¨æ ¸å¿ƒå®ç°
 * Translation Preloader Core Implementation
 *
 * æä¾›ç¿»è¯‘é¢„åŠ è½½çš„æ ¸å¿ƒåŠŸèƒ½å’Œä¸»è¦ç±»å®ç°
 */

import { MAGIC_0_1 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_FIVE, COUNT_TRIPLE, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, TEN_SECONDS_MS, ZERO } from "@/constants/magic-numbers";
import { MINUTE_MS } from "@/constants/time";
import { logger } from '@/lib/logger';
import type { Locale, Messages } from '@/types/i18n';
import type {
  CacheOperationResult,
  CacheStorage,
  MetricsCollector,
  Preloader,
} from '@/lib/i18n-cache-types';
import type {
  IPreloader,
  PreloaderConfig,
  PreloadOptions,
  PreloadState,
  PreloadStats,
} from '@/lib/i18n-preloader-types';
import {
  PreloaderError,
  PreloaderNetworkError,
  PreloaderTimeoutError,
} from '@/lib/i18n-preloader-types';

/**
 * ç¿»è¯‘é¢„åŠ è½½å™¨æ ¸å¿ƒå®ç°
 * Translation preloader core implementation
 */
export class TranslationPreloader implements Preloader, IPreloader {
  private cache: CacheStorage<Messages>;
  private metricsCollector: MetricsCollector;
  private preloadState: PreloadState = {
    isPreloading: false,
    progress: ZERO,
    totalLocales: ZERO,
    completedLocales: ZERO,
    errors: [],
  };
  private preloadConfig: PreloaderConfig;
  private abortController?: AbortController;

  constructor(
    cache: CacheStorage<Messages>,
    metricsCollector: MetricsCollector,
    config?: Partial<PreloaderConfig>,
  ) {
    this.cache = cache;
    this.metricsCollector = metricsCollector;
    this.preloadConfig = {
      enablePreload: true,
      preloadLocales: ['en', 'zh'],
      batchSize: COUNT_TRIPLE,
      delayBetweenBatches: PERCENTAGE_FULL,
      maxConcurrency: COUNT_FIVE,
      timeout: TEN_SECONDS_MS,
      retryCount: COUNT_TRIPLE,
      retryDelay: ANIMATION_DURATION_VERY_SLOW,
      smartPreload: {
        enabled: false,
        maxLocales: COUNT_TRIPLE,
        minUsageThreshold: MAGIC_0_1,
        usageWindow: HOURS_PER_DAY,
        preloadTrigger: 'idle',
      },
      memoryLimit: PERCENTAGE_HALF * BYTES_PER_KB * BYTES_PER_KB,
      networkThrottling: false,
      priorityQueue: false,
      cacheStrategy: 'adaptive',
      cacheTTL: HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW,
      maxCacheSize: PERCENTAGE_FULL,
      enableMetrics: true,
      metricsInterval: MINUTE_MS,
      enableLogging: false,
      logLevel: 'info',
      ...config,
    };
  }

  /**
   * é¢„åŠ è½½æŒ‡å®šè¯­è¨€
   * Preload specific locale
   */
  async preloadLocale(
    locale: Locale,
    options?: PreloadOptions,
  ): Promise<Messages> {
    const startTime = Date.now();

    try {
      // é¦–å…ˆæ£€æŸ¥ç¼“å­˜
      const cached = this.cache.get(locale);
      if (cached) {
        this.metricsCollector.recordLoadTime(Date.now() - startTime);
        options?.onSuccess?.(locale, cached);
        return cached;
      }

      // ä»æœåŠ¡å™¨åŠ è½½
      const messages = await this.loadMessagesFromServer(locale, options);

      // å­˜å‚¨åˆ°ç¼“å­˜
      this.cache.set(locale, messages);
      this.metricsCollector.recordLoadTime(Date.now() - startTime);

      options?.onSuccess?.(locale, messages);
      return messages;
    } catch (error) {
      const preloaderError =
        error instanceof Error
          ? new PreloaderNetworkError(locale, error)
          : new PreloaderError(`Failed to preload locale ${locale}`, locale);

      options?.onError?.(preloaderError, locale);
      throw preloaderError;
    }
  }

  /**
   * é¢„çƒ­ç¼“å­˜
   * Warmup cache
   */
  warmupCache(): void {
    if (!this.preloadConfig.enablePreload || this.preloadState.isPreloading) {
      return;
    }

    this.preloadMultipleLocales(this.preloadConfig.preloadLocales);
  }

  /**
   * æ£€æŸ¥æ˜¯å¦æ­£åœ¨é¢„åŠ è½½
   * Check if preloading
   */
  isPreloading(): boolean {
    return this.preloadState.isPreloading;
  }

  /**
   * è·å–é¢„åŠ è½½è¿›åº¦
   * Get preload progress
   */
  getPreloadProgress(): number {
    return this.preloadState.progress;
  }

  /**
   * è·å–é¢„åŠ è½½çŠ¶æ€
   * Get preload state
   */
  getPreloadState(): PreloadState {
    return { ...this.preloadState };
  }

  /**
   * æ‰¹é‡é¢„åŠ è½½å¤šä¸ªè¯­è¨€
   * Preload multiple locales in batches
   */
  async preloadMultipleLocales(
    locales: Locale[],
    options?: PreloadOptions,
  ): Promise<CacheOperationResult<Messages>[]> {
    if (this.preloadState.isPreloading) {
      throw new PreloaderError('Preloading is already in progress');
    }

    this.preloadState = {
      isPreloading: true,
      progress: ZERO,
      totalLocales: locales.length,
      completedLocales: ZERO,
      errors: [],
      startTime: Date.now(),
    };

    this.abortController = new AbortController();
    const results: CacheOperationResult<Messages>[] = [];

    try {
      // åˆ†æ‰¹å¤„ç†
      const batches = this.createBatches(locales, this.preloadConfig.batchSize);

      for (let i = ZERO; i < batches.length; i++) {
        if (this.abortController.signal.aborted) {
          break;
        }

        const batch = batches[i];
        if (!batch) continue;
        const batchResults = await this.processBatch(batch, options);
        results.push(...batchResults);

        // æ›´æ–°è¿›åº¦
        this.preloadState.completedLocales += batch.length;
        this.preloadState.progress =
          (this.preloadState.completedLocales /
            this.preloadState.totalLocales) *
          PERCENTAGE_FULL;

        options?.onProgress?.(this.preloadState.progress);

        // æ‰¹æ¬¡é—´å»¶è¿Ÿ
        if (
          i < batches.length - ONE &&
          this.preloadConfig.delayBetweenBatches > ZERO
        ) {
          await this.delay(this.preloadConfig.delayBetweenBatches);
        }
      }
    } catch (error) {
      logger.error('Preloading failed:', error);
    } finally {
      this.preloadState.isPreloading = false;
    }

    return results;
  }

  /**
   * åœæ­¢é¢„åŠ è½½
   * Stop preloading
   */
  stopPreloading(): void {
    if (this.abortController) {
      this.abortController.abort();
    }
    this.preloadState.isPreloading = false;
  }

  /**
   * æš‚åœé¢„åŠ è½½
   * Pause preloading
   */
  pausePreloading(): void {
    // å®ç°æš‚åœé€»è¾‘
    this.stopPreloading();
  }

  /**
   * æ¢å¤é¢„åŠ è½½
   * Resume preloading
   */
  resumePreloading(): void {
    // å®ç°æ¢å¤é€»è¾‘
    if (!this.preloadState.isPreloading) {
      this.warmupCache();
    }
  }

  /**
   * æ™ºèƒ½é¢„åŠ è½½ï¼ˆåŸºäºä½¿ç”¨æ¨¡å¼ï¼‰
   * Smart preload based on usage patterns
   */
  async smartPreload(): Promise<void> {
    // è·å–ä½¿ç”¨ç»Ÿè®¡
    const metrics = this.metricsCollector.getMetrics();
    const {localeUsage} = metrics;

    // æŒ‰ä½¿ç”¨é¢‘ç‡æ’åº
    const sortedLocales = Object.entries(localeUsage)
      .sort(([, a], [, b]) => b - a)
      .map(([locale]) => locale as Locale)
      .slice(ZERO, COUNT_TRIPLE); // åªé¢„åŠ è½½å‰3ä¸ªæœ€å¸¸ç”¨çš„è¯­è¨€

    if (sortedLocales.length > ZERO) {
      await this.preloadMultipleLocales(sortedLocales);
    }
  }

  /**
   * é¢„åŠ è½½ç›¸å…³è¯­è¨€
   * Preload related locales
   */
  async preloadRelatedLocales(currentLocale: Locale): Promise<void> {
    const relatedLocales = this.getRelatedLocales(currentLocale);
    if (relatedLocales.length > ZERO) {
      await this.preloadMultipleLocales(relatedLocales);
    }
  }

  /**
   * é¢„åŠ è½½ç¼ºå¤±çš„ç¿»è¯‘
   * Preload missing translations
   */
  async preloadMissingTranslations(
    locale: Locale,
    keys: string[],
  ): Promise<void> {
    try {
      const messages = await this.loadSpecificKeys(locale, keys);

      // åˆå¹¶åˆ°ç°æœ‰ç¼“å­˜
      const existing = this.cache.get(locale) || {};
      const merged = { ...existing, ...messages } as Messages;
      this.cache.set(locale, merged);
    } catch (error) {
      logger.error(
        `Failed to preload missing translations for ${locale}:`,
        error,
      );
    }
  }

  /**
   * è·å–é¢„åŠ è½½ç»Ÿè®¡
   * Get preload statistics
   */
  getPreloadStats(): PreloadStats {
    const state = this.preloadState;
    const duration = state.startTime ? Date.now() - state.startTime : ZERO;

    return {
      isActive: state.isPreloading,
      progress: state.progress,
      totalLocales: state.totalLocales,
      completedLocales: state.completedLocales,
      errorCount: state.errors.length,
      duration,
      averageLoadTime: duration / Math.max(state.completedLocales, ONE),
      successRate:
        state.totalLocales > ZERO
          ? (state.completedLocales - state.errors.length) / state.totalLocales
          : ZERO,
    };
  }

  /**
   * æ¸…é™¤ç¼“å­˜
   * Clear cache
   */
  clearCache(): void {
    this.cache.clear();
  }

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡
   * Get cache statistics
   */
  getCacheStats(): { size: number; keys: string[] } {
    return {
      size: this.cache.size(),
      keys: Array.from(this.cache.keys()),
      // å…¶ä»–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
    };
  }

  /**
   * ä»æœåŠ¡å™¨åŠ è½½æ¶ˆæ¯
   * Load messages from server
   */
  private async loadMessagesFromServer(
    locale: Locale,
    options?: PreloadOptions,
  ): Promise<Messages> {
    try {
      // è¿™é‡Œåº”è¯¥æ˜¯å®é™…çš„ API è°ƒç”¨
      const controller = new AbortController();
      const timeoutId = setTimeout(
        () => controller.abort(),
        options?.timeout || this.preloadConfig.timeout,
      );

      const response = await fetch(`/api/messages/${locale}`, {
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(
          `Failed to load messages for ${locale}: ${response.statusText}`,
        );
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error) {
        if (error.name === 'AbortError') {
          throw new PreloaderTimeoutError(
            locale,
            options?.timeout || this.preloadConfig.timeout,
          );
        }
        throw new PreloaderNetworkError(locale, error);
      }
      throw error;
    }
  }

  /**
   * åŠ è½½ç‰¹å®šé”®çš„ç¿»è¯‘
   * Load specific translation keys
   */
  private async loadSpecificKeys(
    locale: Locale,
    keys: string[],
  ): Promise<Partial<Messages>> {
    try {
      const response = await fetch(`/api/messages/${locale}/keys`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ keys }),
        signal: this.abortController?.signal,
      } as RequestInit);

      if (!response.ok) {
        throw new Error(
          `Failed to load specific keys for ${locale}: ${response.statusText}`,
        );
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new PreloaderError('Load was cancelled', locale);
      }
      throw error;
    }
  }

  /**
   * åˆ›å»ºæ‰¹æ¬¡
   * Create batches
   */
  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = ZERO; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  /**
   * å¤„ç†æ‰¹æ¬¡
   * Process batch
   */
  private async processBatch(
    batch: Locale[],
    options?: PreloadOptions,
  ): Promise<CacheOperationResult<Messages>[]> {
    const promises = batch.map(
      async (locale): Promise<CacheOperationResult<Messages>> => {
        try {
          const messages = await this.preloadLocale(locale, options);
          return {
            success: true,
            data: messages,
            fromCache: false,
            loadTime: Date.now(),
          };
        } catch (error) {
          this.preloadState.errors.push({
            locale,
            error: error instanceof Error ? error.message : 'Unknown error',
          });

          return {
            success: false,
            error: error instanceof Error ? error.message : 'Unknown error',
            fromCache: false,
          };
        }
      },
    );

    return Promise.all(promises);
  }

  /**
   * è·å–ç›¸å…³è¯­è¨€
   * Get related locales
   */
  private getRelatedLocales(locale: Locale): Locale[] {
    const relatedMap: Record<Locale, Locale[]> = {
      en: ['zh'],
      zh: ['en'],
    };

    return relatedMap[locale] || [];
  }

  /**
   * å»¶è¿Ÿå‡½æ•°
   * Delay function
   */
  private delay(ms: number): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  /**
   * æ¸…ç†èµ„æº
   * Cleanup resources
   */
  cleanup(): void {
    this.stopPreloading();
    this.preloadState = {
      isPreloading: false,
      progress: ZERO,
      totalLocales: ZERO,
      completedLocales: ZERO,
      errors: [],
    };
  }

  /**
   * è®¾ç½®é¢„åŠ è½½é…ç½®
   * Set preload configuration
   */
  setConfig(config: Partial<PreloaderConfig>): void {
    this.preloadConfig = { ...this.preloadConfig, ...config };
  }

  /**
   * è·å–é¢„åŠ è½½é…ç½®
   * Get preload configuration
   */
  getConfig(): PreloaderConfig {
    return { ...this.preloadConfig };
  }
}

