 */
import { ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { routing } from '@/i18n/routing';

export interface TranslationValidationResult {
  isValid: boolean;
  errors: TranslationError[];
  warnings: TranslationWarning[];
  coverage: number;
  missingKeys: string[];
  inconsistentKeys: string[];
}

export interface TranslationError {
  type: 'missing_key' | 'type_mismatch' | 'invalid_format' | 'empty_value';
  key: string;
  locale: string;
  message: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
}

export interface TranslationWarning {
  type:
    | 'untranslated'
    | 'length_mismatch'
    | 'format_inconsistency'
    | 'placeholder_mismatch';
  key: string;
  locale: string;
  message: string;
  suggestion?: string;
}

/**
 * åŠ è½½æ‰€æœ‰è¯­è¨€çš„ç¿»è¯‘æ–‡ä»¶
 */
async function loadTranslations(
  errors: TranslationError[],
): Promise<Record<string, Record<string, unknown>>> {
  const translations: Record<string, Record<string, unknown>> = {};

  for (const locale of routing.locales) {
    try {
      const messages = await import(`../../messages/${locale}.json`);
      translations[locale] = messages.default;
    } catch (_error) {
      errors.push({
        type: 'missing_key',
        key: 'translation_file',
        locale,
        message: `Translation file for locale ${locale} not found`,
        severity: 'critical',
      });
    }
  }

  return translations;
}

/**
 * éªŒè¯ç¿»è¯‘æ–‡ä»¶çš„å®Œæ•´æ€§å’Œè´¨é‡
 */
export async function validateTranslations(): Promise<TranslationValidationResult> {
  const errors: TranslationError[] = [];
  const warnings: TranslationWarning[] = [];
  const missingKeys: string[] = [];
  const inconsistentKeys: string[] = [];

  try {
    const translations = await loadTranslations(errors);

    // è·å–æ‰€æœ‰ç¿»è¯‘é”®
    const allKeys = new Set<string>();
    Object.values(translations).forEach((translation) => {
      extractKeys(translation).forEach((key) => allKeys.add(key));
    });

    // éªŒè¯æ¯ä¸ªè¯­è¨€çš„ç¿»è¯‘å®Œæ•´æ€§
    validateTranslationCompleteness(
      translations,
      allKeys,
      errors,
      warnings,
      missingKeys,
    );

    // è®¡ç®—è¦†ç›–ç‡
    const totalKeys = allKeys.size * routing.locales.length;
    const missingCount = missingKeys.length;
    const coverage =
      totalKeys > ZERO ? ((totalKeys - missingCount) / totalKeys) * PERCENTAGE_FULL : PERCENTAGE_FULL;

    return {
      isValid:
        errors.filter((e) => e.severity === 'critical' || e.severity === 'high')
          .length === ZERO,
      errors,
      warnings,
      coverage,
      missingKeys,
      inconsistentKeys,
    };
  } catch (error) {
    return {
      isValid: false,
      errors: [
        {
          type: 'invalid_format',
          key: 'validation',
          locale: 'all',
          message: `Validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
          severity: 'critical',
        },
      ],
      warnings: [],
      coverage: ZERO,
      missingKeys: [],
      inconsistentKeys: [],
    };
  }
}

/**
 * æå–å¯¹è±¡ä¸­çš„æ‰€æœ‰é”®è·¯å¾„
 */
function extractKeys(obj: Record<string, unknown>, prefix = ''): string[] {
  const keys: string[] = [];

  for (const [key, value] of Object.entries(obj)) {
    const fullKey = prefix ? `${prefix}.${key}` : key;

    if (value && typeof value === 'object' && !Array.isArray(value)) {
      keys.push(...extractKeys(value as Record<string, unknown>, fullKey));
    } else {
      keys.push(fullKey);
    }
  }

  return keys;
}

/**
 * è·å–åµŒå¥—å¯¹è±¡çš„å€¼
 */
function getNestedValue(obj: Record<string, unknown>, path: string): unknown {
  return path
    .split('.')
    .reduce(
      (current: Record<string, unknown> | unknown, key) =>
        current &&
        typeof current === 'object' &&
        current !== null &&
        !Array.isArray(current)
          ? (current as Record<string, unknown>)[key]
          : undefined,
      obj,
    );
}

/**
 * ç”Ÿæˆç¿»è¯‘è´¨é‡æŠ¥å‘Š
 */
export function generateTranslationReport(
  result: TranslationValidationResult,
): string {
  const { isValid, errors, warnings, coverage, missingKeys } = result;

  let report = '# ç¿»è¯‘è´¨é‡æŠ¥å‘Š\n\n';

  report += `## æ€»ä½“çŠ¶æ€: ${isValid ? 'âœ… é€šè¿‡' : 'âŒ å¤±è´¥'}\n`;
  report += `## è¦†ç›–ç‡: ${coverage.toFixed(ONE)}%\n\n`;

  if (errors.length > ZERO) {
    report += '## é”™è¯¯\n\n';
    errors.forEach((error) => {
      report += `- **${error.severity.toUpperCase()}**: ${error.message} (${error.locale}.${error.key})\n`;
    });
    report += '\n';
  }

  if (warnings.length > ZERO) {
    report += '## è­¦å‘Š\n\n';
    warnings.forEach((warning) => {
      report += `- **${warning.type}**: ${warning.message} (${warning.locale}.${warning.key})\n`;
      if (warning.suggestion) {
        report += `  å»ºè®®: ${warning.suggestion}\n`;
      }
    });
    report += '\n';
  }

  if (missingKeys.length > ZERO) {
    report += '## ç¼ºå¤±çš„ç¿»è¯‘é”®\n\n';
    missingKeys.forEach((key) => {
      report += `- ${key}\n`;
    });
  }

  return report;
}

/**
 * éªŒè¯ç¿»è¯‘å®Œæ•´æ€§
 */
function validateTranslationCompleteness(
  translations: Record<string, Record<string, unknown>>,
  allKeys: Set<string>,
  errors: TranslationError[],
  warnings: TranslationWarning[],
  missingKeys: string[],
): void {
  for (const locale of routing.locales) {
    const translation = translations[locale];
    if (!translation) continue;

    const localeKeys = new Set(extractKeys(translation));

    // æ£€æŸ¥ç¼ºå¤±çš„é”®
    for (const key of allKeys) {
      if (!localeKeys.has(key)) {
        missingKeys.push(`${locale}.${key}`);
        errors.push({
          type: 'missing_key',
          key,
          locale,
          message: `Missing translation for key: ${key}`,
          severity: 'high',
        });
      }
    }

    // æ£€æŸ¥ç¿»è¯‘è´¨é‡
    validateTranslationQuality(
      translation,
      locale,
      localeKeys,
      translations,
      errors,
      warnings,
    );
  }
}

/**
 * éªŒè¯ç¿»è¯‘è´¨é‡
 */
function validateTranslationQuality(
  translation: Record<string, unknown>,
  locale: string,
  localeKeys: Set<string>,
  translations: Record<string, Record<string, unknown>>,
  errors: TranslationError[],
  warnings: TranslationWarning[],
): void {
  for (const key of localeKeys) {
    const value = getNestedValue(translation, key);

    // æ£€æŸ¥ç©ºå€¼
    if (!value || (typeof value === 'string' && value.trim() === '')) {
      errors.push({
        type: 'empty_value',
        key,
        locale,
        message: `Empty translation value for key: ${key}`,
        severity: 'medium',
      });
    }

    // æ£€æŸ¥æ˜¯å¦æœªç¿»è¯‘ï¼ˆä¸å…¶ä»–è¯­è¨€ç›¸åŒï¼‰
    if (typeof value === 'string') {
      checkUntranslatedContent(value, key, locale, translations, warnings);
      checkPlaceholderConsistency(value, key, locale, translations, warnings);
    }
  }
}

/**
 * æ£€æŸ¥æœªç¿»è¯‘å†…å®¹
 */
function checkUntranslatedContent(
  value: string,
  key: string,
  locale: string,
  translations: Record<string, Record<string, unknown>>,
  warnings: TranslationWarning[],
): void {
  const otherLocales = routing.locales.filter((l) => l !== locale);
  for (const otherLocale of otherLocales) {
    const otherValue = getNestedValue(translations[otherLocale] || {}, key);
    if (value === otherValue && key !== 'home.title') {
      warnings.push({
        type: 'untranslated',
        key,
        locale,
        message: `Possibly untranslated: same value as ${otherLocale}`,
        suggestion: `Consider translating "${value}" to ${locale}`,
      });
    }
  }
}

/**
 * æ£€æŸ¥å ä½ç¬¦ä¸€è‡´æ€§
 */
function checkPlaceholderConsistency(
  value: string,
  key: string,
  locale: string,
  translations: Record<string, Record<string, unknown>>,
  warnings: TranslationWarning[],
): void {
  const placeholders = value.match(/\{[^}]+\}/g) || [];
  const referencePlaceholders = getNestedValue(
    translations[routing.locales[ZERO]] || {},
    key,
  );
  if (typeof referencePlaceholders === 'string') {
    const refPlaceholders = referencePlaceholders.match(/\{[^}]+\}/g) || [];
    if (placeholders.length !== refPlaceholders.length) {
      warnings.push({
        type: 'placeholder_mismatch',
        key,
        locale,
        message: `Placeholder count mismatch: expected ${refPlaceholders.length}, got ${placeholders.length}`,
        suggestion: `Ensure all placeholders are present: ${refPlaceholders.join(', ')}`,
      });
    }
  }
}

/**
 * å®æ—¶ç¿»è¯‘åŒæ­¥æ£€æŸ¥
 */
export function createTranslationSyncChecker() {
  let lastValidation: TranslationValidationResult | null = null;

  return {
    async check(): Promise<TranslationValidationResult> {
      const result = await validateTranslations();
      lastValidation = result;
      return result;
    },

    getLastResult(): TranslationValidationResult | null {
      return lastValidation;
    },

    isHealthy(): boolean {
      return lastValidation?.isValid ?? false;
    },

    getCoverage(): number {
      return lastValidation?.coverage ?? ZERO;
    },
  };
}

