/**
 * è®¡ç®—å¥åº·æ£€æŸ¥ç»“æœ
 * Calculate health check results
 */
export function calculateHealthCheck(): StorageHealthCheck {
  const stats = calculateStorageStats();
  const availability = checkStorageAvailability();

  // è®¡ç®—å¥åº·åˆ†æ•° (0-1)
  let healthScore = ONE;
  const issues: Array<{
    type: ErrorType;
    severity: PriorityLevel;
    message: string;
    timestamp: number;
  }> = [];

  // æ£€æŸ¥å­˜å‚¨å¯ç”¨æ€§
  if (!availability.localStorageAvailable) {
    healthScore -= DEC_0_4;
    issues.push({
      type: 'access_denied',
      severity: 'high',
      message: 'localStorageä¸å¯ç”¨',
      timestamp: Date.now(),
    });
  }

  if (!availability.cookiesAvailable) {
    healthScore -= MAGIC_0_2;
    issues.push({
      type: 'access_denied',
      severity: 'medium',
      message: 'Cookiesä¸å¯ç”¨',
      timestamp: Date.now(),
    });
  }

  // æ£€æŸ¥æ•°æ®æ–°é²œåº¦
  if (stats.freshness < MAGIC_0_5) {
    healthScore -= MAGIC_0_2;
    issues.push({
      type: 'validation_error',
      severity: 'medium',
      message: 'æ•°æ®è¿‡æœŸ',
      timestamp: Date.now(),
    });
  }

  // æ£€æŸ¥å­˜å‚¨å¤§å°
  const maxSize = COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB; // 5MB
  if (stats.totalSize > maxSize * MAGIC_0_8) {
    healthScore -= MAGIC_0_1;
    issues.push({
      type: 'storage_full',
      severity: 'medium',
      message: 'å­˜å‚¨ç©ºé—´ä½¿ç”¨è¿‡å¤š',
      timestamp: Date.now(),
    });
  }

  // æ£€æŸ¥å†å²è®°å½•æ•°é‡
  if (stats.historyStats.totalEntries > ANIMATION_DURATION_VERY_SLOW) {
    healthScore -= MAGIC_0_1;
    issues.push({
      type: 'validation_error',
      severity: 'low',
      message: 'å†å²è®°å½•è¿‡å¤š',
      timestamp: Date.now(),
    });
  }

  // ç¡®å®šå¥åº·çŠ¶æ€
  let status: 'healthy' | 'warning' | 'error';
  if (healthScore >= MAGIC_0_8) {
    status = 'healthy';
  } else if (healthScore >= MAGIC_0_5) {
    status = 'warning';
  } else {
    status = 'error';
  }

  return {
    isHealthy: healthScore >= MAGIC_0_8,
    status,
    issues,
    performance: {
      readLatency: ZERO, // éœ€è¦å®é™…æµ‹é‡
      writeLatency: ZERO, // éœ€è¦å®é™…æµ‹é‡
      errorRate: ZERO, // éœ€è¦ä»é”™è¯¯ç»Ÿè®¡ä¸­è·å–
      availability: availability.localStorageAvailable ? ONE : ZERO,
    },
    storage: {
      used: stats.totalSize,
      available: COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB - stats.totalSize, // å‡è®¾5MBé™åˆ¶
      quota: COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB,
      utilization: stats.totalSize / (COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB),
    },
    lastCheck: Date.now(),
  };
}

