    };

    this.errorLog.unshift(errorEntry);

    // é™åˆ¶é”™è¯¯æ—¥å¿—æ¡ç›®æ•°é‡
    if (this.errorLog.length > this.MAX_ERROR_ENTRIES) {
      this.errorLog = this.errorLog.slice(ZERO, this.MAX_ERROR_ENTRIES);
    }

    // è§¦å‘é”™è¯¯äº‹ä»¶
    EventManager.emitEvent({
      type: 'error_occurred',
