  }

  if (metrics.successRate < MAGIC_95) {
    recommendations.push('æˆåŠŸç‡åä½ï¼Œå»ºè®®å¢åŠ é”™è¯¯å¤„ç†å’Œé‡è¯•æœºåˆ¶');
  }

  if (metrics.efficiency < MAGIC_0_7) {
    recommendations.push('æ•´ä½“æ•ˆç‡åä½ï¼Œå»ºè®®å…¨é¢ä¼˜åŒ–å­˜å‚¨ç­–ç•¥');
  }

  if (recommendations.length === ZERO) {
    recommendations.push('æ€§èƒ½è¡¨ç°è‰¯å¥½ï¼Œå»ºè®®ç»§ç»­ç›‘æ§');
  }

  return recommendations;
}

