/**
 * éªŒè¯åˆ†ææ•°æ®å®Œæ•´æ€§
 * Validate analytics data integrity
 */
export function validateAnalyticsData(): {
  isValid: boolean;
  issues: string[];
  recommendations: string[];
} {
  const issues: string[] = [];
  const recommendations: string[] = [];

  try {
    // æ£€æŸ¥åŸºç¡€æ•°æ®
    const stats = getStorageStats();
    if (!stats.success) {
      issues.push('æ— æ³•è·å–å­˜å‚¨ç»Ÿè®¡ä¿¡æ¯');
    }

    const healthCheck = performHealthCheck();
    if (!healthCheck.success) {
      issues.push('æ— æ³•æ‰§è¡Œå¥åº·æ£€æŸ¥');
    }

    // æ£€æŸ¥æ—¥å¿—æ•°æ®
    const accessLog = AccessLogger.getAccessLog();
    const errorLog = ErrorLogger.getErrorLog();

    if (accessLog.length === ZERO) {
      issues.push('è®¿é—®æ—¥å¿—ä¸ºç©º');
      recommendations.push('å¼€å§‹è®°å½•å­˜å‚¨æ“ä½œä»¥æ”¶é›†åˆ†ææ•°æ®');
    }

    if (errorLog.length > accessLog.length * MAGIC_0_1) {
      issues.push('é”™è¯¯ç‡è¿‡é«˜');
      recommendations.push('æ£€æŸ¥å­˜å‚¨æ“ä½œé€»è¾‘ï¼Œå‡å°‘é”™è¯¯å‘ç”Ÿ');
    }

    // æ£€æŸ¥æ•°æ®æ—¶æ•ˆæ€§
    const now = Date.now();
    const oneHourAgo = now - SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;
    const recentAccess = accessLog.filter(
      (entry) => entry.timestamp > oneHourAgo,
    );

    if (recentAccess.length === ZERO && accessLog.length > ZERO) {
      issues.push('æœ€è¿‘ä¸€å°æ—¶æ— æ´»åŠ¨è®°å½•');
      recommendations.push('æ£€æŸ¥åº”ç”¨æ˜¯å¦æ­£å¸¸è¿è¡Œ');
    }

    // æ£€æŸ¥ç¼“å­˜çŠ¶æ€
    const cacheStats = CacheManager.getCacheStats();
    if (cacheStats.expiredEntries > cacheStats.validEntries) {
      recommendations.push('æ¸…ç†è¿‡æœŸç¼“å­˜ä»¥æé«˜æ€§èƒ½');
    }
  } catch (error) {
    issues.push(
      `æ•°æ®éªŒè¯è¿‡ç¨‹ä¸­å‘ç”Ÿé”™è¯¯: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`,
    );
  }

  return {
    isValid: issues.length === ZERO,
    issues,
    recommendations,
  };
}

