export class LocaleStorageAnalytics {
  /**
   * è·å–å­˜å‚¨ç»Ÿè®¡ä¿¡æ¯
   * Get storage statistics
   */
  static getStorageStats(): StorageOperationResult<StorageStats> {
    const startTime = Date.now();

    try {
      // æ£€æŸ¥ç¼“å­˜
      const cached = CacheManager.getCachedMetrics('storage_stats');
      if (cached) {
        return {
          success: true,
          data: cached as StorageStats,
          source: 'memory',
          timestamp: Date.now(),
          responseTime: Date.now() - startTime,
        };
      }

      const stats = calculateStorageStats();

      // ç¼“å­˜ç»“æœ
      CacheManager.setCachedMetrics('storage_stats', stats);

      // è®°å½•è®¿é—®
      AccessLogger.logAccess(
        'storage_stats',
        'read',
        true,
        Date.now() - startTime,
      );

      return {
        success: true,
        data: stats,
        source: 'localStorage',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    } catch (error) {
      const errorMessage =
        error instanceof Error ? error.message : 'Unknown error';
      ErrorLogger.logError(errorMessage, 'getStorageStats');
      AccessLogger.logAccess(
        'storage_stats',
        'read',
        false,
        Date.now() - startTime,
        errorMessage,
      );

      return {
        success: false,
        error: errorMessage,
        source: 'localStorage',
        timestamp: Date.now(),
        responseTime: Date.now() - startTime,
      };
    }
  }

  /**
