/**
 * Cookie é…ç½®å¸¸é‡
 * Cookie configuration constants
 */
export const COOKIE_CONFIG = {
  maxAge: CACHE_DURATIONS.COOKIE_MAX_AGE / ANIMATION_DURATION_VERY_SLOW, // è½¬æ¢ä¸ºç§’
  sameSite: 'lax' as const,
  secure:
    typeof window !== 'undefined' && window.location.protocol === 'https:',
  path: '/',
} as const;

/**
 * Cookie æ“ä½œå·¥å…·ç±»
 * Cookie management utility class
 */
export class CookieManager {
  /**
   * è®¾ç½® Cookie
   * Set cookie with options
   */
  static set(name: string, value: string, options = COOKIE_CONFIG): void {
    if (typeof document === 'undefined') return;

    const optionsStr = Object.entries(options)
      .map(([key, val]) => {
        if (key === 'maxAge') return `max-age=${val}`;
        if (key === 'sameSite') return `SameSite=${val}`;
        if (key === 'secure' && val) return 'Secure';
        if (key === 'path') return `Path=${val}`;
        return '';
      })
      .filter(Boolean)
      .join('; ');

    document.cookie = `${name}=${encodeURIComponent(value)}; ${optionsStr}`;
  }

  /**
   * è·å– Cookie å€¼
   * Get cookie value by name
   */
  static get(name: string): string | null {
    if (typeof document === 'undefined') return null;

    // ä½¿ç”¨æ›´å®‰å…¨çš„cookieè§£ææ–¹æ³•
    const cookies = document.cookie.split(';');
    for (const cookie of cookies) {
      const [cookieName, ...cookieValueParts] = cookie.trim().split('=');
      if (cookieName !== name) continue;

      const cookieValue = cookieValueParts.join('=');
      if (!cookieValue) return null;

      try {
        return decodeURIComponent(cookieValue);
      } catch {
        // é™é»˜å¤„ç†URIè§£ç é”™è¯¯
        if (process.env.NODE_ENV === 'development') {
          // console.warn('Failed to decode cookie value:', error);
        }
        return null;
      }
    }
    return null;
  }

  /**
   * åˆ é™¤ Cookie
   * Remove cookie by name
   */
  static remove(name: string): void {
    if (typeof document === 'undefined') return;
    document.cookie = `${name}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
  }

  /**
   * æ£€æŸ¥ Cookie æ˜¯å¦å­˜åœ¨
   * Check if cookie exists
   */
  static exists(name: string): boolean {
    return this.get(name) !== null;
  }

  /**
   * è·å–æ‰€æœ‰ Cookie
   * Get all cookies as key-value pairs
   */
  static getAll(): Record<string, string> {
    if (typeof document === 'undefined') return {};

    const cookies: Record<string, string> = {};
    const cookieStrings = document.cookie.split(';');

    for (const cookie of cookieStrings) {
      const [name, ...valueParts] = cookie.trim().split('=');
      if (!name) continue;

      const value = valueParts.join('=');
      if (!value) continue;

      try {
        cookies[name] = decodeURIComponent(value);
      } catch {
        // é™é»˜å¤„ç†è§£ç é”™è¯¯
        if (process.env.NODE_ENV === 'development') {
          // console.warn(`Failed to decode cookie ${name}:`, error);
        }
      }
    }

    return cookies;
  }

  /**
   * æ¸…é™¤æ‰€æœ‰ Cookie
   * Clear all cookies
   */
  static clearAll(): void {
    if (typeof document === 'undefined') return;

    const cookies = this.getAll();
    Object.keys(cookies).forEach((name) => {
      this.remove(name);
    });
  }

  /**
   * è®¾ç½®å¸¦æœ‰è¿‡æœŸæ—¶é—´çš„ Cookie
   * Set cookie with expiration date
   */
  static setWithExpiry(
    name: string,
    value: string,
    expiryDays: number,
    options: Partial<typeof COOKIE_CONFIG> = {},
  ): void {
    const expiryDate = new Date();
    expiryDate.setTime(expiryDate.getTime() + expiryDays * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);

    const cookieOptions = {
      ...COOKIE_CONFIG,
      ...options,
      expires: expiryDate.toUTCString(),
    };

    const optionsStr = Object.entries(cookieOptions)
      .map(([key, val]) => {
        if (key === 'maxAge') return `max-age=${val}`;
        if (key === 'sameSite') return `SameSite=${val}`;
        if (key === 'secure' && val) return 'Secure';
        if (key === 'path') return `Path=${val}`;
        if (key === 'expires') return `expires=${val}`;
        return '';
      })
      .filter(Boolean)
      .join('; ');

    document.cookie = `${name}=${encodeURIComponent(value)}; ${optionsStr}`;
  }

  /**
   * è·å– Cookie çš„è¿‡æœŸæ—¶é—´
   * Get cookie expiration time (if available)
   */
  static getExpiry(_name: string): Date | null {
    // Note: JavaScriptæ— æ³•ç›´æ¥è·å–Cookieçš„è¿‡æœŸæ—¶é—´
    // è¿™ä¸ªæ–¹æ³•ä¸»è¦ç”¨äºæ–‡æ¡£å®Œæ•´æ€§ï¼Œå®é™…ä½¿ç”¨ä¸­éœ€è¦åœ¨è®¾ç½®æ—¶è®°å½•
    if (process.env.NODE_ENV === 'development') {
      console.warn(
        'Cookie expiry cannot be retrieved directly from JavaScript. ' +
          'Consider storing expiry information separately if needed.',
      );
    }
    return null;
  }

  /**
   * æ£€æŸ¥ Cookie æ˜¯å¦æ”¯æŒ
   * Check if cookies are supported
   */
  static isSupported(): boolean {
    if (typeof document === 'undefined') return false;

    try {
      const testKey = '__cookie_test__';
      const testValue = 'test';

      this.set(testKey, testValue);
      const retrieved = this.get(testKey);
      this.remove(testKey);

      return retrieved === testValue;
    } catch {
      return false;
    }
  }

  /**
   * è·å– Cookie å¤§å°ï¼ˆå­—èŠ‚ï¼‰
   * Get cookie size in bytes
   */
  static getSize(name: string): number {
    const value = this.get(name);
    if (!value) return ZERO;

    // è®¡ç®— Cookie çš„æ€»å¤§å°ï¼ˆåç§° + å€¼ + åˆ†éš”ç¬¦ï¼‰
    return new Blob([`${name}=${value}`]).size;
  }

  /**
   * è·å–æ‰€æœ‰ Cookie çš„æ€»å¤§å°
   * Get total size of all cookies
   */
  static getTotalSize(): number {
    if (typeof document === 'undefined') return ZERO;

    return new Blob([document.cookie]).size;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦æ¥è¿‘ Cookie å¤§å°é™åˆ¶
   * Check if approaching cookie size limit
   */
  static isNearLimit(threshold: number = MAGIC_0_8): boolean {
    const maxSize = MAGIC_4096; // æ ‡å‡† Cookie å¤§å°é™åˆ¶
    const currentSize = this.getTotalSize();

    return currentSize / maxSize > threshold;
  }
}

/**
 * è¯­è¨€æ£€æµ‹å†å²æ ¸å¿ƒç®¡ç†
 * Locale Detection History Core Management
 *
 * è´Ÿè´£å†å²è®°å½•çš„åŸºç¡€æ“ä½œï¼šæ·»åŠ ã€è·å–ã€æ›´æ–°å’Œç¼“å­˜ç®¡ç†
 */

'use client';

import { CACHE_LIMITS } from '@/constants/i18n-constants';
import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, DAYS_PER_MONTH, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { isLocaleDetectionHistory } from '@/lib/locale-storage-types';
import type { Locale } from '@/types/i18n';
import type {
  LocaleDetectionHistory,
