}

/**
 * è·å–å†å²è®°å½•æ‘˜è¦
 * Get history summary
 */
export function getHistorySummary(): {
  totalRecords: number;
  lastUpdated: number;
  oldestRecord: number;
  newestRecord: number;
  cacheStatus: ReturnType<typeof HistoryCacheManager.getCacheStatus>;
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      totalRecords: ZERO,
      lastUpdated: ZERO,
      oldestRecord: ZERO,
      newestRecord: ZERO,
      cacheStatus: HistoryCacheManager.getCacheStatus(),
    };
  }

  const history = historyResult.data;
  const records = history.history;

  return {
    totalRecords: records.length,
    lastUpdated: history.lastUpdated,
    oldestRecord:
      records.length > ZERO ? records[records.length - ONE]!.timestamp : ZERO,
    newestRecord: records.length > ZERO ? records[ZERO]!.timestamp : ZERO,
    cacheStatus: HistoryCacheManager.getCacheStatus(),
  };
}

/**
 * æ£€æŸ¥å†å²è®°å½•æ˜¯å¦éœ€è¦æ¸…ç†
 * Check if history needs cleanup
 */
export function needsCleanup(maxAge: number = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW): {
  needsCleanup: boolean;
  expiredCount: number;
  totalCount: number;
  recommendations: string[];
} {
  const historyResult = getDetectionHistory();

  if (!historyResult.success || !historyResult.data) {
    return {
      needsCleanup: false,
      expiredCount: ZERO,
      totalCount: ZERO,
      recommendations: ['æ— æ³•è·å–å†å²è®°å½•'],
    };
  }

  const history = historyResult.data;
  const cutoffTime = Date.now() - maxAge;
  const expiredRecords = history.history.filter(
    (record: any) => record.timestamp < cutoffTime,
  );
  const totalCount = history.history.length;
  const expiredCount = expiredRecords.length;

  const recommendations: string[] = [];

  if (expiredCount > ZERO) {
    recommendations.push(`å‘ç° ${expiredCount} æ¡è¿‡æœŸè®°å½•ï¼Œå»ºè®®æ¸…ç†`);
  }

  if (totalCount > PERCENTAGE_FULL) {
    recommendations.push(`å†å²è®°å½•è¿‡å¤š (${totalCount} æ¡)ï¼Œå»ºè®®æ¸…ç†æ—§è®°å½•`);
  }

  const maxRecords = CACHE_LIMITS.MAX_DETECTION_HISTORY || PERCENTAGE_FULL;
  if (totalCount > maxRecords) {
    recommendations.push(`è¶…å‡ºæœ€å¤§è®°å½•é™åˆ¶ (${maxRecords})ï¼Œå°†è‡ªåŠ¨æˆªæ–­`);
  }

  if (recommendations.length === ZERO) {
    recommendations.push('å†å²è®°å½•çŠ¶æ€è‰¯å¥½ï¼Œæ— éœ€æ¸…ç†');
  }

  return {
    needsCleanup: expiredCount > ZERO || totalCount > maxRecords,
    expiredCount,
    totalCount,
    recommendations,
  };
}

/**
 * è¯­è¨€æ£€æµ‹å†å²äº‹ä»¶ç®¡ç†
 * Locale Detection History Event Management
 *
 * è´Ÿè´£å†å²è®°å½•ç›¸å…³çš„äº‹ä»¶ç›‘å¬ã€å‘å¸ƒå’Œç®¡ç†åŠŸèƒ½
 */

'use client';

import { COUNT_TEN, ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { logger } from '@/lib/logger';
import type {
  StorageEvent,
  StorageEventListener,
} from '@/lib/locale-storage-types';

// ==================== äº‹ä»¶ç®¡ç†å™¨ ====================

/**
 * å†å²äº‹ä»¶ç®¡ç†å™¨
 * History event manager
 */
export class HistoryEventManager {
  private static eventListeners: Map<string, StorageEventListener[]> =
    new Map();
  private static eventHistory: StorageEvent[] = [];
  private static readonly MAX_EVENT_HISTORY = PERCENTAGE_FULL;

  /**
   * æ·»åŠ äº‹ä»¶ç›‘å¬å™¨
   * Add event listener
   */
  static addEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  /**
   * ç§»é™¤äº‹ä»¶ç›‘å¬å™¨
   * Remove event listener
   */
  static removeEventListener(
    eventType: string,
    listener: StorageEventListener,
  ): void {
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  /**
   * ç§»é™¤æ‰€æœ‰äº‹ä»¶ç›‘å¬å™¨
   * Remove all event listeners
   */
  static removeAllListeners(eventType?: string): void {
    if (eventType) {
      this.eventListeners.delete(eventType);
    } else {
      this.eventListeners.clear();
    }
  }

  /**
   * å‘å‡ºäº‹ä»¶
   * Emit event
   */
  static emitEvent(event: StorageEvent): void {
    // è®°å½•äº‹ä»¶å†å²
    this.recordEvent(event);

    // å‘é€ç»™ç‰¹å®šç±»å‹çš„ç›‘å¬å™¨
    const listeners = this.eventListeners.get(event.type);
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error(`Error in history event listener for ${event.type}`, {
            error: error as Error,
          });
        }
      });
    }

    // å‘é€ç»™é€šç”¨ç›‘å¬å™¨
    const allListeners = this.eventListeners.get('*');
    if (allListeners) {
      allListeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in universal history event listener', {
            error: error as Error,
          });
        }
      });
    }
  }

  /**
   * è®°å½•äº‹ä»¶å†å²
   * Record event history
   */
  private static recordEvent(event: StorageEvent): void {
    this.eventHistory.unshift(event);

    // é™åˆ¶äº‹ä»¶å†å²é•¿åº¦
    if (this.eventHistory.length > this.MAX_EVENT_HISTORY) {
      this.eventHistory = this.eventHistory.slice(ZERO, this.MAX_EVENT_HISTORY);
    }
  }

  /**
   * è·å–äº‹ä»¶å†å²
   * Get event history
   */
  static getEventHistory(limit?: number): StorageEvent[] {
    return limit ? this.eventHistory.slice(ZERO, limit) : [...this.eventHistory];
  }

  /**
   * æ¸…é™¤äº‹ä»¶å†å²
   * Clear event history
   */
  static clearEventHistory(): void {
    this.eventHistory = [];
  }

  /**
   * è·å–ç›‘å¬å™¨ç»Ÿè®¡
   * Get listener statistics
   */
  static getListenerStats(): {
    totalListeners: number;
    eventTypes: string[];
    listenersByType: Record<string, number>;
  } {
    let totalListeners = ZERO;
    const listenersByType: Record<string, number> = {};

    for (const [eventType, listeners] of this.eventListeners.entries()) {
      const count = listeners.length;
      totalListeners += count;
      listenersByType[eventType] = count;
    }

    return {
      totalListeners,
      eventTypes: Array.from(this.eventListeners.keys()),
      listenersByType,
    };
  }
}

