/**
 * æ§åˆ¶å°æ—¥å¿—ç›‘å¬å™¨
 * Console log listener
 */
export const consoleLogListener: StorageEventListener = (
  event: StorageEvent,
) => {
  const timestamp = new Date(event.timestamp).toLocaleTimeString();

  switch (event.type as string) {
    case 'history_record_added':
      logger.info('å†å²è®°å½•å·²æ·»åŠ ', { timestamp, data: event.data });
      break;
    case 'history_cleanup':
      logger.info('å†å²è®°å½•å·²æ¸…ç†', { timestamp, data: event.data });
      break;
    case 'history_export':
      logger.info('å†å²è®°å½•å·²å¯¼å‡º', { timestamp, data: event.data });
      break;
    case 'history_import':
      logger.info('å†å²è®°å½•å·²å¯¼å…¥', { timestamp, data: event.data });
      break;
    case 'history_error':
      logger.error('å†å²è®°å½•é”™è¯¯', { timestamp, data: event.data as unknown });
      break;
    default:
      logger.info(`å†å²è®°å½•äº‹ä»¶ (${event.type})`, {
        timestamp,
        data: event.data,
      });
  }
};

/**
 * æ€§èƒ½ç›‘æ§ç›‘å¬å™¨
 * Performance monitoring listener
 */
export const performanceListener: StorageEventListener = (
  event: StorageEvent,
) => {
  // è®°å½•æ€§èƒ½ç›¸å…³çš„äº‹ä»¶
  if ((event.type as string) === 'history_cleanup' && event.data) {
    const { cleanupType, removedCount } = event.data as {
      cleanupType: string;
      removedCount: number;
    };

    if (removedCount > PERCENTAGE_FULL) {
      logger.warn('å¤§é‡å†å²è®°å½•æ¸…ç†', { cleanupType, removedCount });
    }
  }

  if (event.type === 'history_error') {
    logger.error('å†å²è®°å½•æ“ä½œå¤±è´¥', { data: event.data as unknown });
  }
};

