  // è¶‹åŠ¿åˆ†æ
  if (trends.trendDirection === 'increasing') {
    insights.push('è¯­è¨€æ£€æµ‹æ´»åŠ¨å‘ˆä¸Šå‡è¶‹åŠ¿');
  } else if (trends.trendDirection === 'decreasing') {
    insights.push('è¯­è¨€æ£€æµ‹æ´»åŠ¨å‘ˆä¸‹é™è¶‹åŠ¿');
    recommendations.push('åˆ†æç”¨æˆ·è¡Œä¸ºå˜åŒ–ï¼Œè€ƒè™‘æ”¹è¿›ç”¨æˆ·ä½“éªŒ');
  }

  // é¢‘ç‡åˆ†æ
  if (stats.detectionFrequency > COUNT_TEN) {
    insights.push('ç”¨æˆ·è¯­è¨€æ£€æµ‹æ´»åŠ¨é¢‘ç¹ï¼Œè¡¨æ˜å¤šè¯­è¨€éœ€æ±‚è¾ƒé«˜');
  } else if (stats.detectionFrequency < ONE) {
    recommendations.push('è€ƒè™‘å¢åŠ è¯­è¨€æ£€æµ‹çš„è§¦å‘åœºæ™¯');
  }

  // å¤šæ ·æ€§åˆ†æ
  if (stats.uniqueLocales > COUNT_FIVE) {
    insights.push('ç”¨æˆ·ç¾¤ä½“è¯­è¨€å¤šæ ·æ€§è¾ƒé«˜');
    recommendations.push('ç¡®ä¿æ‰€æœ‰æ£€æµ‹åˆ°çš„è¯­è¨€éƒ½æœ‰è‰¯å¥½çš„æœ¬åœ°åŒ–æ”¯æŒ');
