      }

      const originalCount = historyData.detections.length;

      // æŒ‰æ—¶é—´æ’åºï¼ˆæœ€æ–°çš„åœ¨å‰ï¼‰
      const sortedDetections = historyData.detections.sort(
        (a, b) => b.timestamp - a.timestamp,
