      if (recommendations.length === ZERO) {
        recommendations.push('å­˜å‚¨çŠ¶æ€è‰¯å¥½ï¼Œæ— éœ€ç‰¹æ®Šç»´æŠ¤');
      }

      // ä¼°ç®—ç»´æŠ¤æ—¶é—´
      let estimatedTime = '< 1åˆ†é’Ÿ';
      if (recommendations.length > COUNT_TRIPLE) {
        estimatedTime = '1-2åˆ†é’Ÿ';
      }
      if (priority === 'high') {
