    } catch (error) {
      return {
        recommendations: ['è·å–ç»´æŠ¤å»ºè®®æ—¶å‡ºé”™ï¼Œå»ºè®®æ‰§è¡ŒåŸºç¡€ç»´æŠ¤'],
