    } catch (error) {
      return {
        success: false,
        error: `ä¿®å¤åŒæ­¥é—®é¢˜å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`,
        timestamp: Date.now(),
