import { ONE, ZERO } from "@/constants/magic-numbers";
import { STORAGE_CONSTANTS } from '@/lib/locale-storage-types-base';

/**
 * èŠ‚æµå‡½æ•°
 * Throttle function
 */
export function throttle<T extends (...args: unknown[]) => unknown>(
  func: T,
  delay: number,
): (...args: Parameters<T>) => void {
  let timeoutId: NodeJS.Timeout | null = null;
  let lastExecTime = ZERO;

  return (...args: Parameters<T>) => {
    const currentTime = Date.now();

    if (currentTime - lastExecTime > delay) {
      func(...args);
      lastExecTime = currentTime;
    } else {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      timeoutId = setTimeout(
        () => {
          func(...args);
          lastExecTime = Date.now();
        },
        delay - (currentTime - lastExecTime),
      );
    }
  };
}

/**
 * é˜²æŠ–å‡½æ•°
 * Debounce function
 */
export function debounce<T extends (...args: unknown[]) => unknown>(
  func: T,
  delay: number,
): (...args: Parameters<T>) => void {
  let timeoutId: NodeJS.Timeout | null = null;

  return (...args: Parameters<T>) => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }
    timeoutId = setTimeout(() => func(...args), delay);
  };
}

/**
 * é‡è¯•å‡½æ•°
 * Retry function
 */
export async function retry<T>(
  func: () => Promise<T>,
  maxAttempts: number = STORAGE_CONSTANTS.MAX_RETRY_ATTEMPTS,
  delay: number = STORAGE_CONSTANTS.RETRY_DELAY,
): Promise<T> {
  let lastError: Error;

  for (let attempt = ONE; attempt <= maxAttempts; attempt++) {
    try {
      return await func();
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));

      if (attempt === maxAttempts) {
        throw lastError;
      }

      await new Promise((resolve) => setTimeout(resolve, delay * attempt));
    }
  }

  throw lastError!;
}

import { ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_PAIR, HOURS_PER_DAY, MAGIC_36, MAGIC_9, ONE, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";

/**
 * è¯­è¨€å­˜å‚¨ç³»ç»Ÿæ ¼å¼åŒ–å·¥å…·å‡½æ•°
 * Locale Storage System Format Utility Functions
 */

/**
 * æ ¼å¼åŒ–å­—èŠ‚å¤§å°
 * Format byte size
 */
export function formatByteSize(bytes: number): string {
  const units = ['B', 'KB', 'MB', 'GB', 'TB'];
  let size = bytes;
  let unitIndex = ZERO;

  while (size >= BYTES_PER_KB && unitIndex < units.length - ONE) {
    size /= BYTES_PER_KB;
    unitIndex += ONE;
  }

  return `${size.toFixed(COUNT_PAIR)} ${units[unitIndex]}`;
}

/**
 * æ ¼å¼åŒ–æ—¶é—´é—´éš”
 * Format time duration
 */
export function formatDuration(milliseconds: number): string {
  const seconds = Math.floor(milliseconds / ANIMATION_DURATION_VERY_SLOW);
  const minutes = Math.floor(seconds / SECONDS_PER_MINUTE);
  const hours = Math.floor(minutes / SECONDS_PER_MINUTE);
  const days = Math.floor(hours / HOURS_PER_DAY);

  if (days > ZERO) {
    return `${days}d ${hours % HOURS_PER_DAY}h`;
  }
  if (hours > ZERO) {
    return `${hours}h ${minutes % SECONDS_PER_MINUTE}m`;
  }
  if (minutes > ZERO) {
    return `${minutes}m ${seconds % SECONDS_PER_MINUTE}s`;
  }
  return `${seconds}s`;
}

/**
 * ç”Ÿæˆå”¯ä¸€ID
 * Generate unique ID
 */
export function generateUniqueId(): string {
  return `${Date.now()}_${Math.random().toString(MAGIC_36).substr(COUNT_PAIR, MAGIC_9)}`;
}

/**
 * è¯­è¨€å­˜å‚¨ç³»ç»Ÿå¯¹è±¡æ“ä½œå·¥å…·å‡½æ•°
 * Locale Storage System Object Utility Functions
 */

/**
 * æ·±åº¦å…‹éš†å¯¹è±¡
 * Deep clone object
 */
export function deepClone<T>(obj: T): T {
  if (obj === null || typeof obj !== 'object') {
    return obj;
  }

  if (obj instanceof Date) {
    return new Date(obj.getTime()) as unknown as T;
  }

  if (obj instanceof Array) {
    return obj.map((item) => deepClone(item)) as unknown as T;
  }

  if (typeof obj === 'object') {
    const cloned = {} as T;
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        cloned[key] = deepClone(obj[key]);
      }
    }
    return cloned;
  }

  return obj;
}

/**
 * åˆå¹¶å¯¹è±¡
 * Merge objects
 */
export function mergeObjects<T extends Record<string, unknown>>(
  target: T,
  source: Partial<T>,
): T {
  const result = { ...target };

  for (const key in source) {
    if (source.hasOwnProperty(key)) {
      const sourceValue = source[key];
      const targetValue = result[key];

      if (sourceValue !== undefined) {
        if (
          typeof sourceValue === 'object' &&
          sourceValue !== null &&
          !Array.isArray(sourceValue) &&
          typeof targetValue === 'object' &&
          targetValue !== null &&
          !Array.isArray(targetValue)
        ) {
          result[key] = mergeObjects(
            targetValue as Record<string, unknown>,
            sourceValue as Record<string, unknown>,
          ) as T[Extract<keyof T, string>];
        } else {
          result[key] = sourceValue as T[Extract<keyof T, string>];
        }
      }
    }
  }

  return result;
}

/**
 * æ¯”è¾ƒå¯¹è±¡
 * Compare objects
 */
export function compareObjects(obj1: unknown, obj2: unknown): boolean {
  if (obj1 === obj2) {
    return true;
  }

  if (obj1 === null || obj2 === null || typeof obj1 !== typeof obj2) {
    return false;
  }

  if (typeof obj1 !== 'object') {
    return obj1 === obj2;
  }

  if (Array.isArray(obj1) !== Array.isArray(obj2)) {
    return false;
  }

  if (Array.isArray(obj1) && Array.isArray(obj2)) {
    if (obj1.length !== obj2.length) {
      return false;
    }
    return obj1.every((item, index) => compareObjects(item, obj2[index]));
  }

  const keys1 = Object.keys(obj1 as Record<string, unknown>);
  const keys2 = Object.keys(obj2 as Record<string, unknown>);

  if (keys1.length !== keys2.length) {
    return false;
  }

  return keys1.every((key) => {
    const val1 = (obj1 as Record<string, unknown>)[key];
    const val2 = (obj2 as Record<string, unknown>)[key];
    return compareObjects(val1, val2);
  });
}

