import { COUNT_FIVE, COUNT_PAIR, COUNT_TRIPLE, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, ZERO } from "@/constants/magic-numbers";

/**
 * æ€§èƒ½ç›‘æ§æ ¸å¿ƒå·¥å…·å†²çªæ£€æŸ¥
 * Performance Monitoring Core Tool Conflict Detection
 *
 * è´Ÿè´£æ£€æµ‹å’Œè§£å†³æ€§èƒ½ç›‘æ§å·¥å…·ä¹‹é—´çš„å†²çªé—®é¢˜
 */

/**
 * å·¥å…·å†²çªæ£€æŸ¥ç»“æœ
 * Tool conflict check result
 */
export interface ToolConflictResult {
  hasConflicts: boolean;
  conflicts: string[];
  warnings: string[];
  recommendations: string[];
  detectedTools: string[];
}

/**
 * å·¥å…·å†²çªæ£€æŸ¥å™¨
 * Tool conflict checker
 */
export class PerformanceToolConflictChecker {
  private _knownTools = [
    'React DevTools',
    'Redux DevTools',
    'Vue DevTools',
    'Angular DevTools',
    'Lighthouse',
    'Web Vitals',
    'Performance Observer',
    'User Timing API',
    'Navigation Timing API',
    'Resource Timing API',
    'Paint Timing API',
    'Long Tasks API',
    'Intersection Observer',
    'Mutation Observer',
    'ResizeObserver',
    'PerformanceObserver',
    'Chrome DevTools',
    'Firefox DevTools',
    'Safari DevTools',
    'Edge DevTools',
    'Sentry',
    'LogRocket',
    'FullStory',
    'Hotjar',
    'Google Analytics',
    'Adobe Analytics',
    'Mixpanel',
    'Amplitude',
    'New Relic',
    'DataDog',
    'AppDynamics',
    'Dynatrace',
  ];

  /**
   * æ£€æŸ¥å·¥å…·å†²çª
   * Check tool conflicts
   */
  checkToolConflicts(): ToolConflictResult {
    const conflicts: string[] = [];
    const warnings: string[] = [];
    const recommendations: string[] = [];
    const detectedTools: string[] = [];

    // æ£€æŸ¥å…¨å±€å¯¹è±¡ä¸­çš„å·¥å…·
    this.detectGlobalTools(detectedTools);

    // æ£€æŸ¥æ€§èƒ½APIå†²çª
    this.checkPerformanceAPIConflicts(conflicts, warnings);

    // æ£€æŸ¥å¼€å‘è€…å·¥å…·å†²çª
    this.checkDevToolsConflicts(conflicts, warnings);

    // æ£€æŸ¥ç¬¬ä¸‰æ–¹ç›‘æ§å·¥å…·å†²çª
    this.checkThirdPartyToolConflicts(detectedTools, conflicts, warnings);

    // ç”Ÿæˆå»ºè®®
    this.generateRecommendations(detectedTools, conflicts, recommendations);

    return {
      hasConflicts: conflicts.length > ZERO,
      conflicts,
      warnings,
      recommendations,
      detectedTools,
    };
  }

  /**
   * æ£€æµ‹å…¨å±€å·¥å…·
   * Detect global tools
   */
  private detectGlobalTools(detectedTools: string[]): void {
    if (typeof window === 'undefined') return;

    // æ£€æŸ¥React DevTools
    if (
      (window as { __REACT_DEVTOOLS_GLOBAL_HOOK__?: unknown })
        .__REACT_DEVTOOLS_GLOBAL_HOOK__
    ) {
      detectedTools.push('React DevTools');
    }

    // æ£€æŸ¥Redux DevTools
    if (
      (window as { __REDUX_DEVTOOLS_EXTENSION__?: unknown })
        .__REDUX_DEVTOOLS_EXTENSION__
    ) {
      detectedTools.push('Redux DevTools');
    }

    // æ£€æŸ¥Vue DevTools
    if (
      (window as { __VUE_DEVTOOLS_GLOBAL_HOOK__?: unknown })
        .__VUE_DEVTOOLS_GLOBAL_HOOK__
    ) {
      detectedTools.push('Vue DevTools');
    }

    // æ£€æŸ¥Angular DevTools
    if ((window as { ng?: unknown }).ng) {
      detectedTools.push('Angular DevTools');
    }

    // æ£€æŸ¥Sentry
    if (
      (window as { Sentry?: unknown; __SENTRY__?: unknown }).Sentry ||
      (window as { Sentry?: unknown; __SENTRY__?: unknown }).__SENTRY__
    ) {
      detectedTools.push('Sentry');
    }

    // æ£€æŸ¥Google Analytics
    if (
      (window as { gtag?: unknown; ga?: unknown; dataLayer?: unknown }).gtag ||
      (window as { gtag?: unknown; ga?: unknown; dataLayer?: unknown }).ga ||
      (window as { gtag?: unknown; ga?: unknown; dataLayer?: unknown })
        .dataLayer
    ) {
      detectedTools.push('Google Analytics');
    }

    // æ£€æŸ¥å…¶ä»–ç›‘æ§å·¥å…·
    if ((window as { LogRocket?: unknown }).LogRocket) {
      detectedTools.push('LogRocket');
    }

    if ((window as { FS?: unknown }).FS) {
      detectedTools.push('FullStory');
    }

    if ((window as { hj?: unknown }).hj) {
      detectedTools.push('Hotjar');
    }

    if ((window as { mixpanel?: unknown }).mixpanel) {
      detectedTools.push('Mixpanel');
    }

    if ((window as { amplitude?: unknown }).amplitude) {
      detectedTools.push('Amplitude');
    }
  }

  /**
   * æ£€æŸ¥æ€§èƒ½APIå†²çª
   * Check Performance API conflicts
   */
  private checkPerformanceAPIConflicts(
    conflicts: string[],
    warnings: string[],
  ): void {
    if (typeof window === 'undefined' || !window.performance) return;

    // æ£€æŸ¥PerformanceObserveræ˜¯å¦è¢«å¤šæ¬¡ä½¿ç”¨
    const performanceObserverCount = this.countPerformanceObservers();
    if (performanceObserverCount > COUNT_TRIPLE) {
      warnings.push(
        `æ£€æµ‹åˆ° ${performanceObserverCount} ä¸ª PerformanceObserver å®ä¾‹ï¼Œå¯èƒ½å½±å“æ€§èƒ½`,
      );
    }

    // æ£€æŸ¥User Timing APIä½¿ç”¨æƒ…å†µ
    const userTimingMarks = performance.getEntriesByType('mark').length;
    const userTimingMeasures = performance.getEntriesByType('measure').length;

    if (userTimingMarks > PERCENTAGE_FULL) {
      warnings.push(`æ£€æµ‹åˆ° ${userTimingMarks} ä¸ªæ€§èƒ½æ ‡è®°ï¼Œå»ºè®®å®šæœŸæ¸…ç†`);
    }

    if (userTimingMeasures > PERCENTAGE_HALF) {
      warnings.push(`æ£€æµ‹åˆ° ${userTimingMeasures} ä¸ªæ€§èƒ½æµ‹é‡ï¼Œå»ºè®®å®šæœŸæ¸…ç†`);
    }

    // æ£€æŸ¥æ˜¯å¦æœ‰å…¶ä»–å·¥å…·åœ¨ç›‘å¬æ€§èƒ½äº‹ä»¶
    if (this.hasMultiplePerformanceListeners()) {
      conflicts.push('æ£€æµ‹åˆ°å¤šä¸ªå·¥å…·åœ¨ç›‘å¬æ€§èƒ½äº‹ä»¶ï¼Œå¯èƒ½å¯¼è‡´æ•°æ®é‡å¤æˆ–å†²çª');
    }
  }

  /**
   * æ£€æŸ¥å¼€å‘è€…å·¥å…·å†²çª
   * Check DevTools conflicts
   */
  private checkDevToolsConflicts(
    conflicts: string[],
    warnings: string[],
  ): void {
    if (typeof window === 'undefined') return;

    // æ£€æŸ¥æ˜¯å¦åœ¨å¼€å‘æ¨¡å¼
    const isDevelopment = process.env.NODE_ENV === 'development';

    if (isDevelopment) {
      let devToolsCount = ZERO;

      if (
        (window as { __REACT_DEVTOOLS_GLOBAL_HOOK__?: unknown })
          .__REACT_DEVTOOLS_GLOBAL_HOOK__
      )
        devToolsCount += ONE;
      if (
        (window as { __REDUX_DEVTOOLS_EXTENSION__?: unknown })
          .__REDUX_DEVTOOLS_EXTENSION__
      )
        devToolsCount += ONE;
      if (
        (window as { __VUE_DEVTOOLS_GLOBAL_HOOK__?: unknown })
          .__VUE_DEVTOOLS_GLOBAL_HOOK__
      )
        devToolsCount += ONE;

      if (devToolsCount > COUNT_PAIR) {
        warnings.push(
          `å¼€å‘ç¯å¢ƒä¸­æ£€æµ‹åˆ° ${devToolsCount} ä¸ªå¼€å‘è€…å·¥å…·ï¼Œå¯èƒ½å½±å“æ€§èƒ½æµ‹è¯•å‡†ç¡®æ€§`,
        );
      }
    }

    // æ£€æŸ¥ç”Ÿäº§ç¯å¢ƒä¸­çš„å¼€å‘å·¥å…·
    if (!isDevelopment) {
      const prodDevTools: string[] = [];

      if (
        (window as { __REACT_DEVTOOLS_GLOBAL_HOOK__?: unknown })
          .__REACT_DEVTOOLS_GLOBAL_HOOK__
      )
        prodDevTools.push('React DevTools');
      if (
        (window as { __REDUX_DEVTOOLS_EXTENSION__?: unknown })
          .__REDUX_DEVTOOLS_EXTENSION__
      )
        prodDevTools.push('Redux DevTools');

      if (prodDevTools.length > ZERO) {
        conflicts.push(`ç”Ÿäº§ç¯å¢ƒä¸­æ£€æµ‹åˆ°å¼€å‘å·¥å…·: ${prodDevTools.join(', ')}`);
      }
    }
  }

  /**
   * æ£€æŸ¥ç¬¬ä¸‰æ–¹ç›‘æ§å·¥å…·å†²çª
   * Check third-party monitoring tool conflicts
   */
  private checkThirdPartyToolConflicts(
    detectedTools: string[],
    conflicts: string[],
    warnings: string[],
  ): void {
    const monitoringTools = detectedTools.filter((tool) =>
      ['Sentry', 'LogRocket', 'FullStory', 'New Relic', 'DataDog'].includes(
        tool,
      ),
    );

    if (monitoringTools.length > COUNT_PAIR) {
      warnings.push(
        `æ£€æµ‹åˆ°å¤šä¸ªç›‘æ§å·¥å…·: ${monitoringTools.join(', ')}ï¼Œå¯èƒ½å¯¼è‡´æ€§èƒ½å¼€é”€`,
      );
    }

    const analyticsTools = detectedTools.filter((tool) =>
      ['Google Analytics', 'Adobe Analytics', 'Mixpanel', 'Amplitude'].includes(
        tool,
      ),
    );

    if (analyticsTools.length > COUNT_PAIR) {
      warnings.push(
        `æ£€æµ‹åˆ°å¤šä¸ªåˆ†æå·¥å…·: ${analyticsTools.join(', ')}ï¼Œå»ºè®®æ•´åˆæ•°æ®æ”¶é›†`,
      );
    }

    // æ£€æŸ¥å·¥å…·é—´çš„å·²çŸ¥å†²çª
    this.checkKnownConflicts(detectedTools, conflicts);
  }

  /**
   * æ£€æŸ¥å·²çŸ¥å†²çª
   * Check known conflicts
   */
  private checkKnownConflicts(
    detectedTools: string[],
    conflicts: string[],
  ): void {
    // LogRocket å’Œ FullStory å¯èƒ½å†²çª
    if (
      detectedTools.includes('LogRocket') &&
      detectedTools.includes('FullStory')
    ) {
      conflicts.push('LogRocket å’Œ FullStory åŒæ—¶ä½¿ç”¨å¯èƒ½å¯¼è‡´ä¼šè¯å½•åˆ¶å†²çª');
    }

    // å¤šä¸ªæ€§èƒ½ç›‘æ§å·¥å…·å¯èƒ½å†²çª
    const performanceTools = detectedTools.filter((tool) =>
      ['New Relic', 'DataDog', 'AppDynamics', 'Dynatrace'].includes(tool),
    );

    if (performanceTools.length > ONE) {
      conflicts.push(`å¤šä¸ªAPMå·¥å…·å¯èƒ½å†²çª: ${performanceTools.join(', ')}`);
    }
  }

  /**
   * ç”Ÿæˆå»ºè®®
   * Generate recommendations
   */
  private generateRecommendations(
    detectedTools: string[],
    conflicts: string[],
    recommendations: string[],
  ): void {
    if (conflicts.length > ZERO) {
      recommendations.push('å»ºè®®å®¡æŸ¥å’Œæ•´åˆç›‘æ§å·¥å…·ï¼Œé¿å…åŠŸèƒ½é‡å¤');
      recommendations.push('è€ƒè™‘ä½¿ç”¨ç»Ÿä¸€çš„ç›‘æ§å¹³å°æ¥å‡å°‘å·¥å…·å†²çª');
    }

    if (detectedTools.length > COUNT_FIVE) {
      recommendations.push('æ£€æµ‹åˆ°è¾ƒå¤šç›‘æ§å·¥å…·ï¼Œå»ºè®®è¯„ä¼°æ¯ä¸ªå·¥å…·çš„å¿…è¦æ€§');
      recommendations.push('è€ƒè™‘ä½¿ç”¨æ¡ä»¶åŠ è½½æ¥å‡å°‘ç”Ÿäº§ç¯å¢ƒçš„å·¥å…·æ•°é‡');
    }

    const isDevelopment = process.env.NODE_ENV === 'development';
    if (
      !isDevelopment &&
      detectedTools.some((tool) => tool.includes('DevTools'))
    ) {
      recommendations.push('ç”Ÿäº§ç¯å¢ƒä¸­åº”ç§»é™¤å¼€å‘è€…å·¥å…·ä»¥æé«˜æ€§èƒ½');
    }

    if (detectedTools.length === ZERO) {
      recommendations.push('æœªæ£€æµ‹åˆ°æ˜æ˜¾çš„å·¥å…·å†²çªï¼Œå½“å‰é…ç½®è‰¯å¥½');
    }
  }

  /**
   * è®¡ç®—PerformanceObserveræ•°é‡
   * Count PerformanceObserver instances
   */
  private countPerformanceObservers(): number {
    // è¿™æ˜¯ä¸€ä¸ªä¼°ç®—ï¼Œå®é™…å®ç°å¯èƒ½éœ€è¦æ›´å¤æ‚çš„æ£€æµ‹é€»è¾‘
    if (typeof window === 'undefined' || !window.PerformanceObserver) return ZERO;

    // æ£€æŸ¥å¸¸è§çš„æ€§èƒ½ç›‘æ§åº“æ˜¯å¦åœ¨ä½¿ç”¨PerformanceObserver
    let count = ZERO;

    // æ£€æŸ¥Web Vitals
    if ((window as { webVitals?: unknown }).webVitals) count += ONE;

    // æ£€æŸ¥å…¶ä»–å¯èƒ½çš„ç›‘æ§å·¥å…·
    if (
      (window as { __PERFORMANCE_OBSERVERS__?: unknown[] })
        .__PERFORMANCE_OBSERVERS__
    ) {
      count += (window as { __PERFORMANCE_OBSERVERS__?: unknown[] })
        .__PERFORMANCE_OBSERVERS__!.length;
    }

    return count;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦æœ‰å¤šä¸ªæ€§èƒ½ç›‘å¬å™¨
   * Check if there are multiple performance listeners
   */
  private hasMultiplePerformanceListeners(): boolean {
    if (typeof window === 'undefined') return false;

    // æ£€æŸ¥æ˜¯å¦æœ‰å¤šä¸ªå·¥å…·åœ¨ç›‘å¬loadäº‹ä»¶
    const loadListeners = this.getEventListenerCount('load');
    const performanceListeners = this.getEventListenerCount('DOMContentLoaded');

    return loadListeners > COUNT_PAIR || performanceListeners > COUNT_PAIR;
  }

  /**
   * è·å–äº‹ä»¶ç›‘å¬å™¨æ•°é‡
   * Get event listener count
   */
  private getEventListenerCount(eventType: string): number {
    // è¿™æ˜¯ä¸€ä¸ªç®€åŒ–çš„å®ç°ï¼Œå®é™…å¯èƒ½éœ€è¦æ›´å¤æ‚çš„æ£€æµ‹
    if (typeof window === 'undefined') return ZERO;

    try {
      // å°è¯•è·å–äº‹ä»¶ç›‘å¬å™¨ä¿¡æ¯ï¼ˆä»…åœ¨å¼€å‘ç¯å¢ƒä¸­å¯ç”¨ï¼‰
      const listeners = (
        window as {
          getEventListeners?: (target: unknown) => Record<string, unknown[]>;
        }
      ).getEventListeners?.(window)?.[eventType];
      return listeners ? listeners.length : ZERO;
    } catch {
      return ZERO;
    }
  }

  /**
   * è·å–å·¥å…·è¯¦ç»†ä¿¡æ¯
   * Get tool details
   */
  getToolDetails(toolName: string): {
    name: string;
    detected: boolean;
    version?: string;
    impact: 'low' | 'medium' | 'high';
    description: string;
  } | null {
    if (typeof window === 'undefined') return null;

    const toolDetails = {
      'React DevTools': {
        detected: Boolean((window as { __REACT_DEVTOOLS_GLOBAL_HOOK__?: unknown })
          .__REACT_DEVTOOLS_GLOBAL_HOOK__),
        impact: 'low' as const,
        description: 'React å¼€å‘è€…å·¥å…·ï¼Œç”¨äºè°ƒè¯• React ç»„ä»¶',
      },
      'Redux DevTools': {
        detected: Boolean((window as { __REDUX_DEVTOOLS_EXTENSION__?: unknown })
          .__REDUX_DEVTOOLS_EXTENSION__),
        impact: 'low' as const,
        description: 'Redux å¼€å‘è€…å·¥å…·ï¼Œç”¨äºè°ƒè¯•çŠ¶æ€ç®¡ç†',
      },
      'Sentry': {
        detected: Boolean((window as { Sentry?: unknown }).Sentry),
        impact: 'medium' as const,
        description: 'é”™è¯¯ç›‘æ§å’Œæ€§èƒ½ç›‘æ§å¹³å°',
      },
      'Google Analytics': {
        detected:
          Boolean((window as { gtag?: unknown; ga?: unknown }).gtag) ||
          Boolean((window as { gtag?: unknown; ga?: unknown }).ga),
        impact: 'medium' as const,
        description: 'ç½‘ç«™åˆ†æå’Œç”¨æˆ·è¡Œä¸ºè·Ÿè¸ªå·¥å…·',
      },
    };

    const tool = toolDetails[toolName as keyof typeof toolDetails];
    if (!tool) return null;

    return {
      name: toolName,
      ...tool,
    };
  }

  /**
   * ç”Ÿæˆå†²çªè§£å†³æ–¹æ¡ˆ
   * Generate conflict resolution
   */
  generateResolutionPlan(conflicts: string[]): {
    priority: 'high' | 'medium' | 'low';
    actions: string[];
    timeline: string;
  }[] {
    return conflicts.map((conflict) => {
      if (conflict.includes('ç”Ÿäº§ç¯å¢ƒ') || conflict.includes('APMå·¥å…·')) {
        return {
          priority: 'high' as const,
          actions: [
            'ç«‹å³å®¡æŸ¥ç”Ÿäº§ç¯å¢ƒé…ç½®',
            'ç§»é™¤ä¸å¿…è¦çš„å¼€å‘å·¥å…·',
            'æ•´åˆç›‘æ§å·¥å…·åŠŸèƒ½',
          ],
          timeline: '1-COUNT_PAIR å¤©å†…å®Œæˆ',
        };
      }

      if (conflict.includes('å¤šä¸ªå·¥å…·') || conflict.includes('æ€§èƒ½äº‹ä»¶')) {
        return {
          priority: 'medium' as const,
          actions: ['è¯„ä¼°å·¥å…·çš„å¿…è¦æ€§', 'å®æ–½æ¡ä»¶åŠ è½½ç­–ç•¥', 'ä¼˜åŒ–å·¥å…·é…ç½®'],
          timeline: '1 å‘¨å†…å®Œæˆ',
        };
      }

      return {
        priority: 'low' as const,
        actions: ['ç›‘æ§å·¥å…·ä½¿ç”¨æƒ…å†µ', 'å®šæœŸæ£€æŸ¥å·¥å…·å†²çª', 'ä¼˜åŒ–å·¥å…·é…ç½®'],
        timeline: 'COUNT_PAIR å‘¨å†…å®Œæˆ',
      };
    });
  }
}

