/**
 * æ€§èƒ½ç›‘æ§æ ¸å¿ƒæŒ‡æ ‡ç®¡ç†
 * Performance Monitoring Core Metrics Management
 *
 * è´Ÿè´£æ€§èƒ½æŒ‡æ ‡çš„è®°å½•ã€å­˜å‚¨ã€æ¸…ç†å’Œå®æ—¶åˆ†æåŠŸèƒ½
 */

import { ANIMATION_DURATION_VERY_SLOW, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, MAGIC_1_1, MAGIC_36, MAGIC_9, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import { MB } from '@/constants/units';
import { logger } from '@/lib/logger';
import type {
  BundlePerformanceData,
  ComponentPerformanceData,
  NetworkPerformanceData,
  PerformanceConfig,
  PerformanceMetrics,
  PerformanceMetricSource,
  PerformanceMetricType,
} from '@/lib/performance-monitoring-types';

/**
 * æŒ‡æ ‡ç®¡ç†å™¨
 * Metrics manager
 */
export class PerformanceMetricsManager {
  private metrics: PerformanceMetrics[] = [];
  private cleanupInterval: NodeJS.Timeout | null = null;
  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
    this.setupPeriodicCleanup();
  }

  /**
   * è®¾ç½®å®šæœŸæ¸…ç†
   * Setup periodic cleanup
   */
  private setupPeriodicCleanup(): void {
    const cleanupInterval =
      this.config.global?.dataRetentionTime || COUNT_FIVE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // COUNT_FIVEåˆ†é’Ÿ

    this.cleanupInterval = setInterval(() => {
      this.cleanupOldMetrics();
    }, cleanupInterval);
  }

  /**
   * æ¸…ç†æ—§æŒ‡æ ‡
   * Cleanup old metrics
   */
  private cleanupOldMetrics(): void {
    const now = Date.now();
    const retentionTime =
      this.config.global?.dataRetentionTime || COUNT_FIVE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW;
    const maxMetrics = this.config.global?.maxMetrics || ANIMATION_DURATION_VERY_SLOW;

    // æŒ‰æ—¶é—´æ¸…ç†
    this.metrics = this.metrics.filter(
      (metric) => now - metric.timestamp < retentionTime,
    );

    // æŒ‰æ•°é‡æ¸…ç†ï¼ˆä¿ç•™æœ€æ–°çš„ï¼‰
    if (this.metrics.length > maxMetrics) {
      this.metrics = this.metrics
        .sort((a, b) => b.timestamp - a.timestamp)
        .slice(ZERO, maxMetrics);
    }
  }

  /**
   * ç”ŸæˆæŒ‡æ ‡ID
   * Generate metric ID
   */
  private generateMetricId(): string {
    return `metric_${Date.now()}_${Math.random().toString(MAGIC_36).substr(COUNT_PAIR, MAGIC_9)}`;
  }

  /**
   * è®°å½•æ€§èƒ½æŒ‡æ ‡
   * Record performance metric
   */
  recordMetric(metric: Omit<PerformanceMetrics, 'timestamp'>): void {
    if (!this.config.global?.enabled) {
      return;
    }

    // æ£€æŸ¥ç‰¹å®šç±»å‹æ˜¯å¦å¯ç”¨
    const moduleConfig = this.getModuleConfig(metric.type);
    if (!moduleConfig?.enabled) {
      return;
    }

    const fullMetric: PerformanceMetrics = {
      ...metric,
      id: metric.id || this.generateMetricId(),
      timestamp: Date.now(),
    };

    this.metrics.push(fullMetric);

    // å®æ—¶åˆ†æ
    this.analyzeRealtimeMetric(fullMetric);

    // ç«‹å³æ¸…ç†å¦‚æœè¶…å‡ºé™åˆ¶
    const maxMetrics = this.config.global?.maxMetrics || ANIMATION_DURATION_VERY_SLOW;
    if (this.metrics.length > maxMetrics * MAGIC_1_1) {
      // 10%ç¼“å†²
      this.cleanupOldMetrics();
    }
  }

  /**
   * è·å–æ¨¡å—é…ç½®
   * Get module configuration
   */
  private getModuleConfig(type: PerformanceMetricType) {
    switch (type) {
      case 'component':
        return this.config.component;
      case 'network':
        return this.config.network;
      case 'bundle':
        return this.config.bundle;
      default:
        return null;
    }
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºç»„ä»¶æ€§èƒ½æ•°æ®
   * Type guard: check if data is component performance data
   */
  private isComponentData(data: unknown): data is ComponentPerformanceData {
    return typeof data === 'object' && data !== null && 'renderTime' in data;
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºç½‘ç»œæ€§èƒ½æ•°æ®
   * Type guard: check if data is network performance data
   */
  private isNetworkData(data: unknown): data is NetworkPerformanceData {
    return typeof data === 'object' && data !== null && 'responseTime' in data;
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºæ‰“åŒ…æ€§èƒ½æ•°æ®
   * Type guard: check if data is bundle performance data
   */
  private isBundleData(data: unknown): data is BundlePerformanceData {
    return typeof data === 'object' && data !== null && 'size' in data;
  }

  /**
   * å®æ—¶åˆ†ææŒ‡æ ‡
   * Analyze realtime metric
   */
  private analyzeRealtimeMetric(metric: PerformanceMetrics): void {
    // æ£€æŸ¥æ˜¯å¦æœ‰æ€§èƒ½é—®é¢˜éœ€è¦ç«‹å³å…³æ³¨
    let isSlowPerformance = false;
    let threshold = ZERO;

    switch (metric.type) {
      case 'component':
        if (
          this.isComponentData(metric.data) &&
          this.config.component?.thresholds
        ) {
          threshold = this.config.component.thresholds.renderTime || PERCENTAGE_FULL;
          isSlowPerformance = (Number(metric.data.renderTime) || ZERO) > threshold;
        }
        break;
      case 'network':
        if (
          this.isNetworkData(metric.data) &&
          this.config.network?.thresholds
        ) {
          threshold = this.config.network.thresholds.responseTime || 1000;
          isSlowPerformance =
            (Number(metric.data.responseTime) || ZERO) > threshold;
        }
        break;
      case 'bundle':
        if (this.isBundleData(metric.data) && this.config.bundle?.thresholds) {
          threshold = this.config.bundle.thresholds.size || MB; // 1MB
          isSlowPerformance = (Number(metric.data.size) || ZERO) > threshold;
        }
        break;
    }

    if (isSlowPerformance) {
      // å®‰å…¨è·å–å€¼ç”¨äºæ—¥å¿—è®°å½•
      let value: number | undefined;
      if (this.isComponentData(metric.data)) {
        value = metric.data.renderTime;
      } else if (this.isNetworkData(metric.data)) {
        value = metric.data.responseTime;
      } else if (this.isBundleData(metric.data)) {
        value = metric.data.size;
      }

      logger.warn(
        `Performance warning: ${metric.type} metric exceeded threshold`,
        {
          metric: metric.id || metric.type,
          value: value || ZERO,
          threshold,
          source: metric.source,
        },
      );
    }
  }

  /**
   * è·å–æ‰€æœ‰æŒ‡æ ‡
   * Get all metrics
   */
  getAllMetrics(): PerformanceMetrics[] {
    return [...this.metrics];
  }

  /**
   * è·å–æŒ‡å®šæ—¶é—´çª—å£å†…çš„æŒ‡æ ‡
   * Get metrics within time window
   */
  getMetricsInTimeWindow(timeWindow: number): PerformanceMetrics[] {
    const now = Date.now();
    return this.metrics.filter(
      (metric) => now - metric.timestamp <= timeWindow,
    );
  }

  /**
   * æŒ‰ç±»å‹è·å–æŒ‡æ ‡
   * Get metrics by type
   */
  getMetricsByType(type: PerformanceMetricType): PerformanceMetrics[] {
    return this.metrics.filter((metric) => metric.type === type);
  }

  /**
   * æŒ‰æ¥æºè·å–æŒ‡æ ‡
   * Get metrics by source
   */
  getMetricsBySource(source: PerformanceMetricSource): PerformanceMetrics[] {
    return this.metrics.filter((metric) => metric.source === source);
  }

  /**
   * è·å–æŒ‡æ ‡ç»Ÿè®¡
   * Get metrics statistics
   */
  getMetricsStats(): {
    total: number;
    byType: Record<PerformanceMetricType, number>;
    bySource: Record<PerformanceMetricSource, number>;
    timeRange: {
      oldest: number;
      newest: number;
      span: number;
    };
    averageValue: number;
  } {
    const stats = {
      total: this.metrics.length,
      byType: {} as Record<PerformanceMetricType, number>,
      bySource: {} as Record<PerformanceMetricSource, number>,
      timeRange: {
        oldest: ZERO,
        newest: ZERO,
        span: ZERO,
      },
      averageValue: ZERO,
    };

    if (this.metrics.length === ZERO) {
      return stats;
    }

    // ç»Ÿè®¡ç±»å‹åˆ†å¸ƒ
    this.metrics.forEach((metric) => {
      stats.byType[metric.type] = (stats.byType[metric.type] || ZERO) + ONE;
      stats.bySource[metric.source] = (stats.bySource[metric.source] || ZERO) + ONE;
    });

    // è®¡ç®—æ—¶é—´èŒƒå›´
    const timestamps = this.metrics.map((m) => m.timestamp);
    stats.timeRange.oldest = Math.min(...timestamps);
    stats.timeRange.newest = Math.max(...timestamps);
    stats.timeRange.span = stats.timeRange.newest - stats.timeRange.oldest;

    // è®¡ç®—å¹³å‡å€¼
    const totalValue = this.metrics.reduce((sum, metric) => {
      let value = ZERO;
      if (this.isComponentData(metric.data)) {
        value = Number(metric.data.renderTime) || ZERO;
      } else if (this.isNetworkData(metric.data)) {
        value = Number(metric.data.responseTime) || ZERO;
      } else if (this.isBundleData(metric.data)) {
        value = Number(metric.data.size) || ZERO;
      }
      return sum + value;
    }, ZERO);
    stats.averageValue = totalValue / this.metrics.length;

    return stats;
  }

  /**
   * è®¡ç®—æ¯åˆ†é’Ÿå¹³å‡æŒ‡æ ‡æ•°
   * Calculate average metrics per minute
   */
  calculateAverageMetricsPerMinute(): number {
    if (this.metrics.length === ZERO) {
      return ZERO;
    }

    const stats = this.getMetricsStats();
    const timeSpanMinutes = stats.timeRange.span / (SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);

    return timeSpanMinutes > ZERO ? this.metrics.length / timeSpanMinutes : ZERO;
  }

  /**
   * è·å–æœ€è¿‘çš„æŒ‡æ ‡
   * Get recent metrics
   */
  getRecentMetrics(count = COUNT_TEN): PerformanceMetrics[] {
    return this.metrics
      .sort((a, b) => b.timestamp - a.timestamp)
      .slice(ZERO, count);
  }

  /**
   * æŸ¥æ‰¾ç‰¹å®šæŒ‡æ ‡
   * Find specific metric
   */
  findMetric(id: string): PerformanceMetrics | undefined {
    return this.metrics.find((metric) => metric.id === id);
  }

  /**
   * åˆ é™¤æŒ‡æ ‡
   * Remove metric
   */
  removeMetric(id: string): boolean {
    const index = this.metrics.findIndex((metric) => metric.id === id);
    if (index > -ONE) {
      this.metrics.splice(index, ONE);
      return true;
    }
    return false;
  }

  /**
   * æ¸…ç©ºæ‰€æœ‰æŒ‡æ ‡
   * Clear all metrics
   */
  clearAllMetrics(): void {
    this.metrics = [];
  }

  /**
   * æ›´æ–°é…ç½®
   * Update configuration
   */
  updateConfig(newConfig: PerformanceConfig): void {
    this.config = newConfig;

    // é‡æ–°è®¾ç½®æ¸…ç†é—´éš”
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }
    this.setupPeriodicCleanup();
  }

  /**
   * å¯¼å‡ºæŒ‡æ ‡æ•°æ®
   * Export metrics data
   */
  exportMetrics(): {
    metrics: PerformanceMetrics[];
    exportTime: number;
    stats: any;
  } {
    return {
      metrics: [...this.metrics],
      exportTime: Date.now(),
      stats: this.getMetricsStats(),
    };
  }

  /**
   * å¯¼å…¥æŒ‡æ ‡æ•°æ®
   * Import metrics data
   */
  importMetrics(data: PerformanceMetrics[], replace = false): number {
    if (replace) {
      this.metrics = [];
    }

    let importedCount = ZERO;
    data.forEach((metric) => {
      // éªŒè¯æŒ‡æ ‡æ•°æ®
      if (this.isValidMetric(metric)) {
        this.metrics.push(metric);
        importedCount += ONE;
      }
    });

    // æ¸…ç†é‡å¤å’Œè¿‡æœŸæ•°æ®
    this.cleanupOldMetrics();

    return importedCount;
  }

  /**
   * éªŒè¯æŒ‡æ ‡æ•°æ®
   * Validate metric data
   */
  private isValidMetric(metric: unknown): metric is PerformanceMetrics {
    if (!metric || typeof metric !== 'object') {
      return false;
    }

    const m = metric as PerformanceMetrics;
    return Boolean(m.type &&
      m.source &&
      typeof m.timestamp === 'number' &&
      m.timestamp > ZERO &&
      m.data &&
      typeof m.data === 'object');
  }

  /**
   * é”€æ¯ç®¡ç†å™¨
   * Destroy manager
   */
  destroy(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = null;
    }
    this.metrics = [];
  }
}

/**
 * åˆ›å»ºæŒ‡æ ‡ç®¡ç†å™¨
 * Create metrics manager
 */
export function createMetricsManager(
  config: PerformanceConfig,
): PerformanceMetricsManager {
  return new PerformanceMetricsManager(config);
}

/**
 * æ€§èƒ½ç›‘æ§æ ¸å¿ƒæŠ¥å‘Šç”Ÿæˆ
 * Performance Monitoring Core Report Generation
 *
 * è´Ÿè´£æ€§èƒ½æŠ¥å‘Šçš„ç”Ÿæˆã€è¯„åˆ†è®¡ç®—å’Œå»ºè®®æä¾›åŠŸèƒ½
 */

import { DEC_0_4, MAGIC_0_2 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_FIVE, COUNT_PAIR, COUNT_TRIPLE, MAGIC_0_3, MAGIC_0_5, MAGIC_0_6, MAGIC_1_5, MAGIC_20, MAGIC_40, MAGIC_80, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, ZERO } from "@/constants/magic-numbers";
import type {
  BundlePerformanceData,
  ComponentPerformanceData,
  NetworkPerformanceData,
  PerformanceConfig,
  PerformanceMetrics,
  PerformanceMetricSource,
  PerformanceMetricType,
} from '@/lib/performance-monitoring-types';

/**
 * æ€§èƒ½æŠ¥å‘Šæ¥å£
 * Performance report interface
 */
export interface PerformanceReport {
  /** æŠ¥å‘Šæ‘˜è¦ */
  summary: {
    totalMetrics: number;
    recentMetrics: number;
    sources: PerformanceMetricSource[];
    types: PerformanceMetricType[];
    timeRange: {
      start: number;
      end: number;
    };
    averageMetricsPerMinute: number;
  };
  /** è¯¦ç»†æŒ‡æ ‡ */
  details: PerformanceMetrics[];
  /** æ€§èƒ½å»ºè®® */
  recommendations: string[];
  /** æ€§èƒ½è¯„åˆ† */
  score?: {
    overall: number;
    component: number;
    network: number;
    bundle: number;
  };
}

/**
 * æŠ¥å‘Šç”Ÿæˆå™¨
 * Report generator
 */
export class PerformanceReportGenerator {
  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºç»„ä»¶æ€§èƒ½æ•°æ®
   */
  private isComponentData(data: unknown): data is ComponentPerformanceData {
    return typeof data === 'object' && data !== null && 'renderTime' in data;
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºç½‘ç»œæ€§èƒ½æ•°æ®
   */
  private isNetworkData(data: unknown): data is NetworkPerformanceData {
    return typeof data === 'object' && data !== null && 'responseTime' in data;
  }

  /**
   * ç±»å‹å®ˆå«ï¼šæ£€æŸ¥æ˜¯å¦ä¸ºæ‰“åŒ…æ€§èƒ½æ•°æ®
   */
  private isBundleData(data: unknown): data is BundlePerformanceData {
    return typeof data === 'object' && data !== null && 'size' in data;
  }

  /**
   * ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
   * Generate performance report
   */
  generateReport(
    metrics: PerformanceMetrics[],
    timeWindow = SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW,
  ): PerformanceReport {
    const now = Date.now();
    const windowStart = now - timeWindow;

    // è¿‡æ»¤æ—¶é—´çª—å£å†…çš„æŒ‡æ ‡
    const windowMetrics = metrics.filter(
      (metric) => metric.timestamp >= windowStart,
    );

    // ç”ŸæˆæŠ¥å‘Šæ‘˜è¦
    const summary = this.generateSummary(windowMetrics, windowStart, now);

    // ç”Ÿæˆå»ºè®®
    const recommendations = this.generateRecommendations(windowMetrics);

    // è®¡ç®—æ€§èƒ½è¯„åˆ†
    const score = this.calculatePerformanceScore(windowMetrics);

    return {
      summary,
      details: windowMetrics,
      recommendations,
      ...(score !== undefined && { score }),
    };
  }

  /**
   * ç”ŸæˆæŠ¥å‘Šæ‘˜è¦
   * Generate report summary
   */
  private generateSummary(
    metrics: PerformanceMetrics[],
    windowStart: number,
    windowEnd: number,
  ): PerformanceReport['summary'] {
    const sources = [...new Set(metrics.map((m) => m.source))];
    const types = [...new Set(metrics.map((m) => m.type))];

    const timeSpanMinutes = (windowEnd - windowStart) / (SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW);
    const averageMetricsPerMinute =
      timeSpanMinutes > ZERO ? metrics.length / timeSpanMinutes : ZERO;

    return {
      totalMetrics: metrics.length,
      recentMetrics: metrics.filter((m) => windowEnd - m.timestamp < SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW)
        .length, // æœ€è¿‘1åˆ†é’Ÿ
      sources,
      types,
      timeRange: {
        start: windowStart,
        end: windowEnd,
      },
      averageMetricsPerMinute,
    };
  }

  /**
   * ç”Ÿæˆæ€§èƒ½å»ºè®®
   * Generate performance recommendations
   */
  private generateRecommendations(metrics: PerformanceMetrics[]): string[] {
    const recommendations: string[] = [];

    // ç»„ä»¶æ€§èƒ½å»ºè®®
    const componentMetrics = metrics.filter((m) => m.type === 'component');
    if (componentMetrics.length > ZERO) {
      const slowComponents = componentMetrics.filter((m) => {
        if (this.isComponentData(m.data)) {
          return (
            (Number(m.data.renderTime) || ZERO) >
            (this.config.component?.thresholds?.renderTime || PERCENTAGE_FULL)
          );
        }
        return false;
      });

      if (slowComponents.length > ZERO) {
        recommendations.push(
          `å‘ç° ${slowComponents.length} ä¸ªç»„ä»¶æ¸²æŸ“æ—¶é—´è¶…è¿‡é˜ˆå€¼ï¼Œå»ºè®®ä¼˜åŒ–ç»„ä»¶æ€§èƒ½`,
        );
      }

      const avgRenderTime =
        componentMetrics.reduce((sum, m) => {
          if (this.isComponentData(m.data)) {
            return sum + (Number(m.data.renderTime) || ZERO);
          }
          return sum;
        }, ZERO) / componentMetrics.length;
      if (avgRenderTime > PERCENTAGE_HALF) {
        recommendations.push(
          'ç»„ä»¶å¹³å‡æ¸²æŸ“æ—¶é—´è¾ƒé«˜ï¼Œè€ƒè™‘ä½¿ç”¨ React.memo æˆ– useMemo ä¼˜åŒ–',
        );
      }
    }

    // ç½‘ç»œæ€§èƒ½å»ºè®®
    const networkMetrics = metrics.filter((m) => m.type === 'network');
    if (networkMetrics.length > ZERO) {
      const slowRequests = networkMetrics.filter((m) => {
        if (this.isNetworkData(m.data)) {
          return (
            (Number(m.data.responseTime) || ZERO) >
            (this.config.network?.thresholds?.responseTime || 1000)
          );
        }
        return false;
      });

      if (slowRequests.length > ZERO) {
        recommendations.push(
          `å‘ç° ${slowRequests.length} ä¸ªç½‘ç»œè¯·æ±‚å“åº”æ—¶é—´è¶…è¿‡é˜ˆå€¼ï¼Œå»ºè®®ä¼˜åŒ–APIæ€§èƒ½`,
        );
      }

      const avgResponseTime =
        networkMetrics.reduce((sum, m) => {
          if (this.isNetworkData(m.data)) {
            return sum + (Number(m.data.responseTime) || ZERO);
          }
          return sum;
        }, ZERO) / networkMetrics.length;
      if (avgResponseTime > 500) {
        recommendations.push('ç½‘ç»œè¯·æ±‚å¹³å‡å“åº”æ—¶é—´è¾ƒé«˜ï¼Œè€ƒè™‘ä½¿ç”¨ç¼“å­˜æˆ–CDNä¼˜åŒ–');
      }
    }

    // æ‰“åŒ…å¤§å°å»ºè®®
    const bundleMetrics = metrics.filter((m) => m.type === 'bundle');
    if (bundleMetrics.length > ZERO) {
      const largeBundles = bundleMetrics.filter((m) => {
        if (this.isBundleData(m.data)) {
          return (
            (Number(m.data.size) || ZERO) >
            (this.config.bundle?.thresholds?.size || BYTES_PER_KB * BYTES_PER_KB)
          );
        }
        return false;
      });

      if (largeBundles.length > ZERO) {
        recommendations.push(
          `å‘ç° ${largeBundles.length} ä¸ªæ‰“åŒ…æ–‡ä»¶å¤§å°è¶…è¿‡é˜ˆå€¼ï¼Œå»ºè®®è¿›è¡Œä»£ç åˆ†å‰²`,
        );
      }

      const totalBundleSize = bundleMetrics.reduce((sum, m) => {
        if (this.isBundleData(m.data)) {
          return sum + (Number(m.data.size) || ZERO);
        }
        return sum;
      }, ZERO);
      if (totalBundleSize > COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) {
        // 5MB
        recommendations.push('æ€»æ‰“åŒ…å¤§å°è¾ƒå¤§ï¼Œå»ºè®®ä½¿ç”¨åŠ¨æ€å¯¼å…¥å’Œæ‡’åŠ è½½ä¼˜åŒ–');
      }
    }

    // é€šç”¨å»ºè®®
    if (metrics.length > PERCENTAGE_FULL) {
      recommendations.push('æ€§èƒ½æŒ‡æ ‡æ•°é‡è¾ƒå¤šï¼Œå»ºè®®è°ƒæ•´ç›‘æ§é¢‘ç‡æˆ–å¢åŠ æ•°æ®æ¸…ç†');
    }

    if (recommendations.length === ZERO) {
      recommendations.push('å½“å‰æ€§èƒ½è¡¨ç°è‰¯å¥½ï¼Œç»§ç»­ä¿æŒï¼');
    }

    return recommendations;
  }

  /**
   * è®¡ç®—æ€§èƒ½è¯„åˆ†
   * Calculate performance score
   */
  private calculatePerformanceScore(
    metrics: PerformanceMetrics[],
  ): PerformanceReport['score'] {
    const componentScore = this.calculateComponentScore(metrics);
    const networkScore = this.calculateNetworkScore(metrics);
    const bundleScore = this.calculateBundleScore(metrics);

    // è®¡ç®—æ€»ä½“è¯„åˆ†ï¼ˆåŠ æƒå¹³å‡ï¼‰
    const weights = { component: DEC_0_4, network: DEC_0_4, bundle: MAGIC_0_2 };
    const overall =
      componentScore * weights.component +
      networkScore * weights.network +
      bundleScore * weights.bundle;

    return {
      overall: Math.round(overall),
      component: componentScore,
      network: networkScore,
      bundle: bundleScore,
    };
  }

  /**
   * è®¡ç®—ç»„ä»¶æ€§èƒ½è¯„åˆ†
   * Calculate component performance score
   */
  private calculateComponentScore(metrics: PerformanceMetrics[]): number {
    const componentMetrics = metrics.filter((m) => m.type === 'component');
    if (componentMetrics.length === ZERO) return PERCENTAGE_FULL;

    const threshold = this.config.component?.thresholds?.renderTime || PERCENTAGE_FULL;
    const avgRenderTime =
      componentMetrics.reduce((sum, m) => {
        if (this.isComponentData(m.data)) {
          return sum + (Number(m.data.renderTime) || ZERO);
        }
        return sum;
      }, ZERO) / componentMetrics.length;

    // è¯„åˆ†ç®—æ³•ï¼šåŸºäºå¹³å‡æ¸²æŸ“æ—¶é—´ä¸é˜ˆå€¼çš„æ¯”è¾ƒ
    if (avgRenderTime <= threshold * MAGIC_0_5) return PERCENTAGE_FULL;
    if (avgRenderTime <= threshold) return MAGIC_80;
    if (avgRenderTime <= threshold * MAGIC_1_5) return SECONDS_PER_MINUTE;
    if (avgRenderTime <= threshold * COUNT_PAIR) return MAGIC_40;
    return MAGIC_20;
  }

  /**
   * è®¡ç®—ç½‘ç»œæ€§èƒ½è¯„åˆ†
   * Calculate network performance score
   */
  private calculateNetworkScore(metrics: PerformanceMetrics[]): number {
    const networkMetrics = metrics.filter((m) => m.type === 'network');
    if (networkMetrics.length === ZERO) return PERCENTAGE_FULL;

    const threshold = this.config.network?.thresholds?.responseTime || 1000;
    const avgResponseTime =
      networkMetrics.reduce((sum, m) => {
        if (this.isNetworkData(m.data)) {
          return sum + (Number(m.data.responseTime) || ZERO);
        }
        return sum;
      }, ZERO) / networkMetrics.length;

    // è¯„åˆ†ç®—æ³•ï¼šåŸºäºå¹³å‡å“åº”æ—¶é—´ä¸é˜ˆå€¼çš„æ¯”è¾ƒ
    if (avgResponseTime <= threshold * MAGIC_0_3) return PERCENTAGE_FULL;
    if (avgResponseTime <= threshold * MAGIC_0_6) return MAGIC_80;
    if (avgResponseTime <= threshold) return SECONDS_PER_MINUTE;
    if (avgResponseTime <= threshold * MAGIC_1_5) return MAGIC_40;
    return MAGIC_20;
  }

  /**
   * è®¡ç®—æ‰“åŒ…æ€§èƒ½è¯„åˆ†
   * Calculate bundle performance score
   */
  private calculateBundleScore(metrics: PerformanceMetrics[]): number {
    const bundleMetrics = metrics.filter((m) => m.type === 'bundle');
    if (bundleMetrics.length === ZERO) return PERCENTAGE_FULL;

    const threshold = this.config.bundle?.thresholds?.size || BYTES_PER_KB * BYTES_PER_KB; // 1MB
    const totalSize = bundleMetrics.reduce((sum, m) => {
      if (this.isBundleData(m.data)) {
        return sum + (Number(m.data.size) || ZERO);
      }
      return sum;
    }, ZERO);

    // è¯„åˆ†ç®—æ³•ï¼šåŸºäºæ€»æ‰“åŒ…å¤§å°ä¸é˜ˆå€¼çš„æ¯”è¾ƒ
    if (totalSize <= threshold * MAGIC_0_5) return PERCENTAGE_FULL;
    if (totalSize <= threshold) return MAGIC_80;
    if (totalSize <= threshold * COUNT_PAIR) return SECONDS_PER_MINUTE;
    if (totalSize <= threshold * COUNT_TRIPLE) return MAGIC_40;
    return MAGIC_20;
  }

  /**
   * ç”Ÿæˆè¯¦ç»†çš„æ€§èƒ½åˆ†ææŠ¥å‘Š
   * Generate detailed performance analysis report
   */
  generateDetailedReport(metrics: PerformanceMetrics[]): {
    overview: PerformanceReport;
    analysis: {
      componentAnalysis: {
        slowestComponents: Array<{
          name: string;
          value: number;
          threshold: number;
        }>;
        renderTimeDistribution: Record<string, number>;
        averageRenderTime: number;
      };
      networkAnalysis: {
        slowestRequests: Array<{
          name: string;
          value: number;
          threshold: number;
        }>;
        responseTimeDistribution: Record<string, number>;
        averageResponseTime: number;
      };
      bundleAnalysis: {
        largestBundles: Array<{
          name: string;
          value: number;
          threshold: number;
        }>;
        sizeDistribution: Record<string, number>;
        totalSize: number;
      };
    };
    trends: {
      componentTrend: 'improving' | 'stable' | 'degrading';
      networkTrend: 'improving' | 'stable' | 'degrading';
      bundleTrend: 'improving' | 'stable' | 'degrading';
    };
  } {
    const overview = this.generateReport(metrics);

    // ç»„ä»¶åˆ†æ
    const componentMetrics = metrics.filter((m) => m.type === 'component');
    const componentThreshold =
      this.config.component?.thresholds?.renderTime || PERCENTAGE_FULL;
    const slowestComponents = componentMetrics
      .filter((m) => {
        if (this.isComponentData(m.data)) {
          return (Number(m.data.renderTime) || ZERO) > componentThreshold;
        }
        return false;
      })
      .map((m) => {
        if (this.isComponentData(m.data)) {
          return {
            name: m.id || m.type,
            value: Number(m.data.renderTime) || ZERO,
            threshold: componentThreshold,
          };
        }
        return {
          name: m.id || m.type,
          value: ZERO,
          threshold: componentThreshold,
        };
      })
      .sort((a, b) => b.value - a.value)
      .slice(ZERO, COUNT_FIVE);

    // ç½‘ç»œåˆ†æ
    const networkMetrics = metrics.filter((m) => m.type === 'network');
    const networkThreshold =
      this.config.network?.thresholds?.responseTime || 1000;
    const slowestRequests = networkMetrics
      .filter((m) => {
        if (this.isNetworkData(m.data)) {
          return (Number(m.data.responseTime) || ZERO) > networkThreshold;
        }
        return false;
      })
      .map((m) => {
        if (this.isNetworkData(m.data)) {
          return {
            name: m.id || m.type,
            value: Number(m.data.responseTime) || ZERO,
            threshold: networkThreshold,
          };
        }
        return { name: m.id || m.type, value: ZERO, threshold: networkThreshold };
      })
      .sort((a, b) => b.value - a.value)
      .slice(ZERO, COUNT_FIVE);

    // æ‰“åŒ…åˆ†æ
    const bundleMetrics = metrics.filter((m) => m.type === 'bundle');
    const bundleThreshold = this.config.bundle?.thresholds?.size || BYTES_PER_KB * BYTES_PER_KB;
    const largestBundles = bundleMetrics
      .filter((m) => {
        if (this.isBundleData(m.data)) {
          return (Number(m.data.size) || ZERO) > bundleThreshold;
        }
        return false;
      })
      .map((m) => {
        if (this.isBundleData(m.data)) {
          return {
            name: m.id || m.type,
            value: Number(m.data.size) || ZERO,
            threshold: bundleThreshold,
          };
        }
        return { name: m.id || m.type, value: ZERO, threshold: bundleThreshold };
      })
      .sort((a, b) => b.value - a.value)
      .slice(ZERO, COUNT_FIVE);

    return {
      overview,
      analysis: {
        componentAnalysis: {
          slowestComponents,
          renderTimeDistribution: this.calculateDistribution(componentMetrics),
          averageRenderTime:
            componentMetrics.reduce((sum, m) => {
              if (this.isComponentData(m.data)) {
                return sum + (Number(m.data.renderTime) || ZERO);
              }
              return sum;
            }, ZERO) / componentMetrics.length || ZERO,
        },
        networkAnalysis: {
          slowestRequests,
          responseTimeDistribution: this.calculateDistribution(networkMetrics),
          averageResponseTime:
            networkMetrics.reduce((sum, m) => {
              if (this.isNetworkData(m.data)) {
                return sum + (Number(m.data.responseTime) || ZERO);
              }
              return sum;
            }, ZERO) / networkMetrics.length || ZERO,
        },
        bundleAnalysis: {
          largestBundles,
          sizeDistribution: this.calculateDistribution(bundleMetrics),
          totalSize: bundleMetrics.reduce((sum, m) => {
            if (this.isBundleData(m.data)) {
              return sum + (Number(m.data.size) || ZERO);
            }
            return sum;
          }, ZERO),
        },
      },
      trends: {
        componentTrend: this.calculateTrend(componentMetrics),
        networkTrend: this.calculateTrend(networkMetrics),
        bundleTrend: this.calculateTrend(bundleMetrics),
      },
    };
  }

  /**
   * è®¡ç®—åˆ†å¸ƒ
   * Calculate distribution
   */
  private calculateDistribution(
    metrics: PerformanceMetrics[],
  ): Record<string, number> {
    const distribution: Record<string, number> = {};

    metrics.forEach((metric) => {
      const key = metric.id || metric.type;
      distribution[key] = (distribution[key] || ZERO) + ONE;
    });

    return distribution;
  }

  /**
   * è®¡ç®—è¶‹åŠ¿
   * Calculate trend
   */
  private calculateTrend(
    metrics: PerformanceMetrics[],
  ): 'improving' | 'stable' | 'degrading' {
    if (metrics.length < COUNT_PAIR) return 'stable';

    // æŒ‰æ—¶é—´æ’åº
    const sortedMetrics = metrics.sort((a, b) => a.timestamp - b.timestamp);
    const midPoint = Math.floor(sortedMetrics.length / COUNT_PAIR);

    const firstHalf = sortedMetrics.slice(ZERO, midPoint);
    const secondHalf = sortedMetrics.slice(midPoint);

    const firstAvg =
      firstHalf.reduce((sum, m) => {
        if (this.isComponentData(m.data)) {
          return sum + (Number(m.data.renderTime) || ZERO);
        } else if (this.isNetworkData(m.data)) {
          return sum + (Number(m.data.responseTime) || ZERO);
        } else if (this.isBundleData(m.data)) {
          return sum + (Number(m.data.size) || ZERO);
        }
        return sum;
      }, ZERO) / firstHalf.length;

    const secondAvg =
      secondHalf.reduce((sum, m) => {
        if (this.isComponentData(m.data)) {
          return sum + (Number(m.data.renderTime) || ZERO);
        } else if (this.isNetworkData(m.data)) {
          return sum + (Number(m.data.responseTime) || ZERO);
        } else if (this.isBundleData(m.data)) {
          return sum + (Number(m.data.size) || ZERO);
        }
        return sum;
      }, ZERO) / secondHalf.length;

    const changePercent = ((secondAvg - firstAvg) / firstAvg) * PERCENTAGE_FULL;

    if (changePercent < -COUNT_FIVE) return 'improving'; // æ€§èƒ½æå‡
    if (changePercent > COUNT_FIVE) return 'degrading'; // æ€§èƒ½ä¸‹é™
    return 'stable'; // æ€§èƒ½ç¨³å®š
  }

  /**
   * æ›´æ–°é…ç½®
   * Update configuration
   */
  updateConfig(newConfig: PerformanceConfig): void {
    this.config = newConfig;
  }
}

/**
 * åˆ›å»ºæŠ¥å‘Šç”Ÿæˆå™¨
 * Create report generator
 */
export function createReportGenerator(
  config: PerformanceConfig,
): PerformanceReportGenerator {
  return new PerformanceReportGenerator(config);
}

/**
 * Bundle Analyzer æ€§èƒ½ç›‘æ§é›†æˆ
 * Bundle Analyzer Performance Monitoring Integration
 *
 * æä¾›ä¸Bundle Analyzerå·¥å…·çš„é›†æˆé’©å­ï¼Œç”¨äºç›‘æ§æ‰“åŒ…å¤§å°å’Œä¼˜åŒ–
 */

import { ANIMATION_DURATION_SLOW, ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_FIVE, COUNT_PAIR, COUNT_TEN, MAGIC_0_8, MAGIC_512, MAGIC_8, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, PERCENTAGE_QUARTER, ZERO } from "@/constants/magic-numbers";
import { KB, MB } from '@/constants/units';
import { logger } from '@/lib/logger';
import type {
  PerformanceConfig,
  PerformanceMetrics,
} from '@/lib/performance-monitoring-types';

/**
 * Bundle Analyzer é›†æˆé’©å­è¿”å›ç±»å‹
 * Bundle Analyzer integration hook return type
 */
export interface BundleAnalyzerIntegration {
  enabled: boolean;
  recordBundleSize: (
    bundleName: string,
    size: number,
    gzipSize?: number,
  ) => void;
  recordChunkInfo: (chunkName: string, size: number, modules: string[]) => void;
  generateSizeReport: () => Record<string, unknown>;
}

/**
 * Bundle Analyzer é›†æˆé’©å­
 * Bundle Analyzer integration hook
 */
export function useBundleAnalyzerIntegration(
  config: PerformanceConfig,
  recordMetric: (metric: Omit<PerformanceMetrics, 'timestamp'>) => void,
): BundleAnalyzerIntegration {
  const bundleData = new Map<string, { size: number; gzipSize?: number }>();

  return {
    enabled: config.bundleAnalyzer.enabled,

    recordBundleSize: (bundleName: string, size: number, gzipSize?: number) => {
      if (!config.bundleAnalyzer.enabled) return;

      bundleData.set(bundleName, {
        size,
        ...(gzipSize !== undefined && { gzipSize }),
      });

      recordMetric({
        source: 'bundle-analyzer',
        type: 'bundle',
        data: {
          bundleName,
          size,
          gzipSize,
          timestamp: Date.now(),
        },
        tags: ['bundle-analyzer', 'bundle-size'],
        priority: 'medium',
      });
    },

    recordChunkInfo: (chunkName: string, size: number, modules: string[]) => {
      if (!config.bundleAnalyzer.enabled) return;

      recordMetric({
        source: 'bundle-analyzer',
        type: 'bundle',
        data: {
          chunkName,
          size,
          moduleCount: modules.length,
          modules: modules.slice(ZERO, COUNT_TEN), // åªè®°å½•å‰COUNT_TENä¸ªæ¨¡å—
          timestamp: Date.now(),
        },
        tags: ['bundle-analyzer', 'chunk-info'],
        priority: 'low',
      });
    },

    generateSizeReport: () => {
      return Object.fromEntries(bundleData);
    },
  };
}

/**
 * Bundle Analyzer é…ç½®éªŒè¯
 * Bundle Analyzer configuration validation
 */
export function validateBundleAnalyzerConfig(config: PerformanceConfig): {
  isValid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  if (!config.bundleAnalyzer) {
    errors.push('Bundle Analyzer configuration is missing');
    return { isValid: false, errors, warnings };
  }

  if (typeof config.bundleAnalyzer.enabled !== 'boolean') {
    errors.push('Bundle Analyzer enabled must be a boolean');
  }

  if (config.bundleAnalyzer.enabled) {
    if (
      config.bundleAnalyzer.port &&
      (typeof config.bundleAnalyzer.port !== 'number' ||
        config.bundleAnalyzer.port <= ZERO)
    ) {
      warnings.push('Bundle Analyzer port should be a positive number');
    }

    if (
      config.bundleAnalyzer.reportDir &&
      typeof config.bundleAnalyzer.reportDir !== 'string'
    ) {
      warnings.push('Bundle Analyzer reportDir should be a string');
    }
  }

  return {
    isValid: errors.length === ZERO,
    errors,
    warnings,
  };
}

/**
 * Bundle Analyzer æ€§èƒ½åˆ†æå™¨
 * Bundle Analyzer performance analyzer
 */
export class BundleAnalyzerAnalyzer {
  private bundles = new Map<
    string,
    {
      size: number;
      gzipSize?: number;
      timestamp: number;
    }
  >();

  private chunks = new Map<
    string,
    {
      size: number;
      modules: string[];
      timestamp: number;
    }
  >();

  private config: PerformanceConfig;

  constructor(config: PerformanceConfig) {
    this.config = config;
  }

  /**
   * è®°å½•Bundleå¤§å°
   * Record bundle size
   */
  recordBundleSize(bundleName: string, size: number, gzipSize?: number): void {
    if (!this.config.bundleAnalyzer.enabled) return;

    this.bundles.set(bundleName, {
      size,
      ...(gzipSize !== undefined && { gzipSize }),
      timestamp: Date.now(),
    });

    // æ£€æŸ¥æ˜¯å¦è¶…è¿‡å¤§å°é™åˆ¶ (ä½¿ç”¨bundleé…ç½®çš„é˜ˆå€¼)
    const maxSize = this.config.bundle?.thresholds?.size || MB; // 1MB
    if (size > maxSize) {
      logger.warn(`Bundle ${bundleName} exceeds size limit`, {
        size: this.formatSize(size),
        maxSize: this.formatSize(maxSize),
      });
    }
  }

  /**
   * è®°å½•Chunkä¿¡æ¯
   * Record chunk information
   */
  recordChunkInfo(chunkName: string, size: number, modules: string[]): void {
    if (!this.config.bundleAnalyzer.enabled) return;

    this.chunks.set(chunkName, {
      size,
      modules,
      timestamp: Date.now(),
    });

    // æ£€æŸ¥æ˜¯å¦è¶…è¿‡å¤§å°é™åˆ¶ (ä½¿ç”¨bundleé…ç½®çš„é˜ˆå€¼)
    const maxSize = this.config.bundle?.thresholds?.size || MAGIC_512 * KB; // 512KB
    if (size > maxSize) {
      logger.warn(`Chunk ${chunkName} exceeds size limit`, {
        size: this.formatSize(size),
        maxSize: this.formatSize(maxSize),
      });
    }
  }

  /**
   * ç”ŸæˆBundleåˆ†ææŠ¥å‘Š
   * Generate bundle analysis report
   */
  generateBundleReport(): {
    totalBundles: number;
    totalSize: number;
    totalGzipSize: number;
    largestBundles: Array<{
      name: string;
      size: number;
      gzipSize?: number;
      compressionRatio?: number;
    }>;
    recommendations: string[];
  } {
    const bundles = Array.from(this.bundles.entries());
    const totalSize = bundles.reduce((sum, [, bundle]) => sum + bundle.size, ZERO);
    const totalGzipSize = bundles.reduce(
      (sum, [, bundle]) => sum + (bundle.gzipSize || ZERO),
      ZERO,
    );

    const largestBundles = bundles
      .map(([name, bundle]) => ({
        name,
        size: bundle.size,
        ...(bundle.gzipSize !== undefined && { gzipSize: bundle.gzipSize }),
        ...(bundle.gzipSize !== undefined && {
          compressionRatio: bundle.gzipSize / bundle.size,
        }),
      }))
      .sort((a, b) => b.size - a.size)
      .slice(ZERO, COUNT_TEN);

    const recommendations: string[] = [];

    // ç”Ÿæˆä¼˜åŒ–å»ºè®®
    if (totalSize > COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) {
      // 5MB
      recommendations.push(
        'Total bundle size is large. Consider code splitting and lazy loading.',
      );
    }

    const largeBundles = largestBundles.filter((b) => b.size > BYTES_PER_KB * BYTES_PER_KB); // 1MB
    if (largeBundles.length > ZERO) {
      recommendations.push(
        `${largeBundles.length} bundles are larger than 1MB. Consider splitting them.`,
      );
    }

    const poorCompressionBundles = largestBundles.filter(
      (b) => b.compressionRatio && b.compressionRatio > MAGIC_0_8,
    );
    if (poorCompressionBundles.length > ZERO) {
      recommendations.push(
        `${poorCompressionBundles.length} bundles have poor compression ratios. Check for duplicate code.`,
      );
    }

    return {
      totalBundles: bundles.length,
      totalSize,
      totalGzipSize,
      largestBundles,
      recommendations,
    };
  }

  /**
   * ç”ŸæˆChunkåˆ†ææŠ¥å‘Š
   * Generate chunk analysis report
   */
  generateChunkReport(): {
    totalChunks: number;
    totalSize: number;
    largestChunks: Array<{
      name: string;
      size: number;
      moduleCount: number;
      topModules: string[];
    }>;
    duplicateModules: Array<{
      module: string;
      chunks: string[];
      totalSize: number;
    }>;
  } {
    const chunks = Array.from(this.chunks.entries());
    const totalSize = chunks.reduce((sum, [, chunk]) => sum + chunk.size, ZERO);

    const largestChunks = chunks
      .map(([name, chunk]) => ({
        name,
        size: chunk.size,
        moduleCount: chunk.modules.length,
        topModules: chunk.modules.slice(ZERO, COUNT_FIVE),
      }))
      .sort((a, b) => b.size - a.size)
      .slice(ZERO, COUNT_TEN);

    // æŸ¥æ‰¾é‡å¤æ¨¡å—
    const moduleChunkMap = new Map<string, string[]>();
    chunks.forEach(([chunkName, chunk]) => {
      chunk.modules.forEach((module) => {
        if (!moduleChunkMap.has(module)) {
          moduleChunkMap.set(module, []);
        }
        moduleChunkMap.get(module)!.push(chunkName);
      });
    });

    const duplicateModules = Array.from(moduleChunkMap.entries())
      .filter(([, chunks]) => chunks.length > ONE)
      .map(([module, chunks]) => ({
        module,
        chunks,
        totalSize: chunks.length * ANIMATION_DURATION_VERY_SLOW, // ä¼°ç®—å¤§å°
      }))
      .sort((a, b) => b.totalSize - a.totalSize)
      .slice(ZERO, COUNT_TEN);

    return {
      totalChunks: chunks.length,
      totalSize,
      largestChunks,
      duplicateModules,
    };
  }

  /**
   * æ ¼å¼åŒ–æ–‡ä»¶å¤§å°
   * Format file size
   */
  private formatSize(bytes: number): string {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = ZERO;

    while (size >= BYTES_PER_KB && unitIndex < units.length - ONE) {
      size /= BYTES_PER_KB;
      unitIndex += ONE;
    }

    return `${size.toFixed(COUNT_PAIR)} ${units[unitIndex]}`;
  }

  /**
   * è·å–ä¼˜åŒ–å»ºè®®
   * Get optimization suggestions
   */
  getOptimizationSuggestions(): string[] {
    const suggestions: string[] = [];
    const bundleReport = this.generateBundleReport();
    const chunkReport = this.generateChunkReport();

    // Bundleä¼˜åŒ–å»ºè®®
    if (bundleReport.totalSize > COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) {
      suggestions.push(
        'Consider implementing code splitting to reduce initial bundle size',
      );
    }

    if (bundleReport.largestBundles.length > ZERO) {
      const largest = bundleReport.largestBundles[ZERO];
      if (largest && largest.size > COUNT_PAIR * BYTES_PER_KB * BYTES_PER_KB) {
        suggestions.push(
          `Bundle "${largest.name}" is very large (${this.formatSize(largest.size)}). Consider splitting it.`,
        );
      }
    }

    // Chunkä¼˜åŒ–å»ºè®®
    if (chunkReport.duplicateModules.length > ZERO) {
      suggestions.push(
        `Found ${chunkReport.duplicateModules.length} duplicate modules. Consider creating shared chunks.`,
      );
    }

    if (chunkReport.totalChunks > PERCENTAGE_HALF) {
      suggestions.push(
        'Large number of chunks detected. Consider consolidating smaller chunks.',
      );
    }

    return suggestions;
  }

  /**
   * é‡ç½®æ‰€æœ‰æ•°æ®
   * Reset all data
   */
  reset(): void {
    this.bundles.clear();
    this.chunks.clear();
  }
}

/**
 * Bundle Analyzer å·¥å…·å‡½æ•°
 * Bundle Analyzer utility functions
 */
export const BundleAnalyzerUtils = {
  /**
   * è®¡ç®—å‹ç¼©æ¯”
   * Calculate compression ratio
   */
  calculateCompressionRatio(
    originalSize: number,
    compressedSize: number,
  ): number {
    return compressedSize / originalSize;
  },

  /**
   * è·å–å¤§å°ç­‰çº§
   * Get size rating
   */
  getSizeRating(size: number): 'small' | 'medium' | 'large' | 'huge' {
    if (size < PERCENTAGE_FULL * BYTES_PER_KB) return 'small'; // < 100KB
    if (size < ANIMATION_DURATION_SLOW * BYTES_PER_KB) return 'medium'; // < 500KB
    if (size < BYTES_PER_KB * BYTES_PER_KB) return 'large'; // < 1MB
    return 'huge'; // >= 1MB
  },

  /**
   * ä¼°ç®—åŠ è½½æ—¶é—´
   * Estimate loading time
   */
  estimateLoadingTime(
    size: number,
    connectionSpeed: 'slow' | 'fast' | 'average' = 'average',
  ): number {
    const speeds = {
      slow: (BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // 1 Mbps in bytes per second
      average: (COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // COUNT_FIVE Mbps
      fast: (PERCENTAGE_QUARTER * BYTES_PER_KB * BYTES_PER_KB) / MAGIC_8, // PERCENTAGE_QUARTER Mbps
    };

    return (size / speeds[connectionSpeed]) * ANIMATION_DURATION_VERY_SLOW; // Return in milliseconds
  },
} as const;

/**
 * Web Vitals å’Œç¯å¢ƒæ£€æŸ¥æ€§èƒ½ç›‘æ§é›†æˆ
 * Web Vitals and Environment Check Performance Monitoring Integration
