import { COUNT_100000, COUNT_256 } from "@/constants/count";
import { COUNT_PAIR, MAGIC_12, MAGIC_16, ZERO } from "@/constants/magic-numbers";

/**
 * åŠ å¯†å’Œå¯†ç å“ˆå¸Œå·¥å…·
 * Cryptography and password hashing utilities
 */

/**
 * Crypto constants
 */
const CRYPTO_CONSTANTS = {
  SALT_BYTE_LENGTH: MAGIC_16,
  HEX_CHARS_PER_BYTE: COUNT_PAIR,
  HEX_BASE: MAGIC_16,
  HEX_PAD_LENGTH: COUNT_PAIR,
} as const;

/**
 * Hash password using Web Crypto API
 */
export async function hashPassword(
  password: string,
  salt?: string,
): Promise<string> {
  const encoder = new TextEncoder();
  const saltBytes = salt
    ? encoder.encode(salt)
    : crypto.getRandomValues(new Uint8Array(CRYPTO_CONSTANTS.SALT_BYTE_LENGTH));
  const passwordBytes = encoder.encode(password);

  const combined = new Uint8Array(saltBytes.length + passwordBytes.length);
  combined.set(saltBytes);
  combined.set(passwordBytes, saltBytes.length);

  const hashBuffer = await crypto.subtle.digest('SHA-256', combined);
  const hashArray = Array.from(new Uint8Array(hashBuffer));
  const hashHex = hashArray
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');

  const saltHex = Array.from(saltBytes)
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');

  return `${saltHex}:${hashHex}`;
}

/**
 * Verify password against hash
 */
export async function verifyPassword(
  password: string,
  hash: string,
): Promise<boolean> {
  try {
    const [saltHex, expectedHash] = hash.split(':');
    if (!saltHex || !expectedHash) {
      return false;
    }

    const salt = saltHex.match(/.{COUNT_PAIR}/g)?.map((byte) => parseInt(byte, MAGIC_16));
    if (!salt) {
      return false;
    }

    const saltBytes = new Uint8Array(salt);
    const actualHash = await hashPassword(
      password,
      new TextDecoder().decode(saltBytes),
    );

    return actualHash === hash;
  } catch {
    return false;
  }
}

/**
 * Generate a cryptographically secure random salt
 */
export function generateSalt(
  length: number = CRYPTO_CONSTANTS.SALT_BYTE_LENGTH,
): string {
  const saltBytes = crypto.getRandomValues(new Uint8Array(length));
  return Array.from(saltBytes)
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');
}

/**
 * Hash data using SHA-256
 */
export async function sha256Hash(data: string): Promise<string> {
  const encoder = new TextEncoder();
  const dataBytes = encoder.encode(data);
  const hashBuffer = await crypto.subtle.digest('SHA-256', dataBytes);
  const hashArray = Array.from(new Uint8Array(hashBuffer));

  return hashArray
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');
}

/**
 * Hash data using SHA-512
 */
export async function sha512Hash(data: string): Promise<string> {
  const encoder = new TextEncoder();
  const dataBytes = encoder.encode(data);
  const hashBuffer = await crypto.subtle.digest('SHA-512', dataBytes);
  const hashArray = Array.from(new Uint8Array(hashBuffer));

  return hashArray
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');
}

/**
 * Generate HMAC signature
 */
export async function generateHMAC(
  data: string,
  secret: string,
  algorithm: 'SHA-256' | 'SHA-512' = 'SHA-256',
): Promise<string> {
  const encoder = new TextEncoder();
  const keyData = encoder.encode(secret);
  const messageData = encoder.encode(data);

  const key = await crypto.subtle.importKey(
    'raw',
    keyData,
    { name: 'HMAC', hash: algorithm },
    false,
    ['sign'],
  );

  const signature = await crypto.subtle.sign('HMAC', key, messageData);
  const signatureArray = Array.from(new Uint8Array(signature));

  return signatureArray
    .map((b) =>
      b
        .toString(CRYPTO_CONSTANTS.HEX_BASE)
        .padStart(CRYPTO_CONSTANTS.HEX_PAD_LENGTH, '0'),
    )
    .join('');
}

/**
 * Verify HMAC signature
 */
export async function verifyHMAC(
  data: string,
  signature: string,
  secret: string,
  algorithm: 'SHA-256' | 'SHA-512' = 'SHA-256',
): Promise<boolean> {
  try {
    const expectedSignature = await generateHMAC(data, secret, algorithm);
    return expectedSignature === signature;
  } catch {
    return false;
  }
}

/**
 * Encrypt data using AES-GCM
 */
export async function encryptData(
  data: string,
  password: string,
): Promise<{ encrypted: string; iv: string; salt: string }> {
  const encoder = new TextEncoder();
  const dataBytes = encoder.encode(data);

  // Generate salt and IV
  const salt = crypto.getRandomValues(new Uint8Array(MAGIC_16));
  const iv = crypto.getRandomValues(new Uint8Array(MAGIC_12));

  // Derive key from password
  const passwordKey = await crypto.subtle.importKey(
    'raw',
    encoder.encode(password),
    'PBKDF2',
    false,
    ['deriveKey'],
  );

  const key = await crypto.subtle.deriveKey(
    {
      name: 'PBKDF2',
      salt,
      iterations: COUNT_100000,
      hash: 'SHA-256',
    },
    passwordKey,
    { name: 'AES-GCM', length: COUNT_256 },
    false,
    ['encrypt'],
  );

  // Encrypt data
  const encrypted = await crypto.subtle.encrypt(
    { name: 'AES-GCM', iv },
    key,
    dataBytes,
  );

  return {
    encrypted: Array.from(new Uint8Array(encrypted))
      .map((b) => b.toString(MAGIC_16).padStart(COUNT_PAIR, '0'))
      .join(''),
    iv: Array.from(iv)
      .map((b) => b.toString(MAGIC_16).padStart(COUNT_PAIR, '0'))
      .join(''),
    salt: Array.from(salt)
      .map((b) => b.toString(MAGIC_16).padStart(COUNT_PAIR, '0'))
      .join(''),
  };
}

/**
 * Decrypt data using AES-GCM
 */
export async function decryptData(
  encryptedHex: string,
  ivHex: string,
  saltHex: string,
  password: string,
): Promise<string> {
  const encoder = new TextEncoder();

  // Convert hex strings back to bytes
  const encrypted = new Uint8Array(
    encryptedHex.match(/.{COUNT_PAIR}/g)?.map((byte) => parseInt(byte, MAGIC_16)) || [],
  );
  const iv = new Uint8Array(
    ivHex.match(/.{COUNT_PAIR}/g)?.map((byte) => parseInt(byte, MAGIC_16)) || [],
  );
  const salt = new Uint8Array(
    saltHex.match(/.{COUNT_PAIR}/g)?.map((byte) => parseInt(byte, MAGIC_16)) || [],
  );

  // Derive key from password
  const passwordKey = await crypto.subtle.importKey(
    'raw',
    encoder.encode(password),
    'PBKDF2',
    false,
    ['deriveKey'],
  );

  const key = await crypto.subtle.deriveKey(
    {
      name: 'PBKDF2',
      salt,
      iterations: COUNT_100000,
      hash: 'SHA-256',
    },
    passwordKey,
    { name: 'AES-GCM', length: COUNT_256 },
    false,
    ['decrypt'],
  );

  // Decrypt data
  const decrypted = await crypto.subtle.decrypt(
    { name: 'AES-GCM', iv },
    key,
    encrypted,
  );

  return new TextDecoder().decode(decrypted);
}

/**
 * Generate a secure random key for encryption
 */
export async function generateEncryptionKey(): Promise<CryptoKey> {
  return await crypto.subtle.generateKey(
    { name: 'AES-GCM', length: COUNT_256 },
    true,
    ['encrypt', 'decrypt'],
  );
}

/**
 * Export encryption key to raw format
 */
export async function exportKey(key: CryptoKey): Promise<string> {
  const exported = await crypto.subtle.exportKey('raw', key);
  return Array.from(new Uint8Array(exported))
    .map((b) => b.toString(MAGIC_16).padStart(COUNT_PAIR, '0'))
    .join('');
}

/**
 * Import encryption key from raw format
 */
export async function importKey(keyHex: string): Promise<CryptoKey> {
  const keyBytes = new Uint8Array(
    keyHex.match(/.{COUNT_PAIR}/g)?.map((byte) => parseInt(byte, MAGIC_16)) || [],
  );

  return await crypto.subtle.importKey(
    'raw',
    keyBytes,
    { name: 'AES-GCM' },
    true,
    ['encrypt', 'decrypt'],
  );
}

/**
 * Constant-time string comparison to prevent timing attacks
 */
export function constantTimeCompare(a: string, b: string): boolean {
  if (a.length !== b.length) {
    return false;
  }

  let result = ZERO;
  for (let i = ZERO; i < a.length; i++) {
    result |= a.charCodeAt(i) ^ b.charCodeAt(i);
  }

  return result === ZERO;
}

import { BYTES_PER_KB, COUNT_PAIR, COUNT_TEN, HEX_BYTE_MAX, HEX_JPEG_MARKER_1, HEX_PDF_MARKER, HEX_PDF_SIGNATURE_1, HEX_PNG_SIGNATURE_1, HEX_PNG_SIGNATURE_2, HEX_PNG_SIGNATURE_3, HEX_PNG_SIGNATURE_4, HEX_PNG_SIGNATURE_5, HEX_PNG_SIGNATURE_6, HEX_ZIP_SIGNATURE, MAGIC_255, MAGIC_HEX_03, MAGIC_HEX_04, ONE, ZERO } from "@/constants/magic-numbers";

/**
 * æ–‡ä»¶ä¸Šä¼ å®‰å…¨éªŒè¯å·¥å…·
 * File upload security validation utilities
 */

/**
 * File upload constants
 */
const FILE_UPLOAD_CONSTANTS = {
  MAX_FILE_SIZE_MB: COUNT_TEN,
  BYTES_PER_MB: BYTES_PER_KB,
  KB_TO_BYTES: BYTES_PER_KB,
} as const;

/**
 * File validation result interface
 */
export interface FileValidationResult {
  valid: boolean;
  error?: string;
  warnings?: string[];
}

/**
 * Allowed file types configuration
 */
export const ALLOWED_FILE_TYPES = {
  images: [
    'image/jpeg',
    'image/png',
    'image/gif',
    'image/webp',
    'image/svg+xml',
  ],
  documents: [
    'application/pdf',
    'text/plain',
    'text/csv',
    'application/msword',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    'application/vnd.ms-excel',
    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  ],
  archives: [
    'application/zip',
    'application/x-rar-compressed',
    'application/x-7z-compressed',
  ],
} as const;

/**
 * Dangerous file extensions that should never be allowed
 */
const DANGEROUS_EXTENSIONS = [
  '.exe',
  '.bat',
  '.cmd',
  '.scr',
  '.pif',
  '.js',
  '.vbs',
  '.jar',
  '.com',
  '.msi',
  '.dll',
  '.app',
  '.deb',
  '.rpm',
  '.dmg',
  '.pkg',
  '.sh',
  '.ps1',
  '.php',
  '.asp',
  '.aspx',
  '.jsp',
] as const;

/**
 * Validate file upload security
 */
export function validateFileUpload(
  file: File,
  options: {
    maxSizeMB?: number;
    allowedTypes?: string[];
    allowedCategories?: Array<keyof typeof ALLOWED_FILE_TYPES>;
  } = {},
): FileValidationResult {
  const warnings: string[] = [];

  // Check file size
  const maxSize =
    (options.maxSizeMB || FILE_UPLOAD_CONSTANTS.MAX_FILE_SIZE_MB) *
    FILE_UPLOAD_CONSTANTS.BYTES_PER_MB *
    FILE_UPLOAD_CONSTANTS.KB_TO_BYTES;

  if (file.size > maxSize) {
    return {
      valid: false,
      error: `File size exceeds ${options.maxSizeMB || FILE_UPLOAD_CONSTANTS.MAX_FILE_SIZE_MB}MB limit`,
    };
  }

  // Determine allowed types
  let allowedTypes: string[] = [];

  if (options.allowedTypes) {
    allowedTypes = options.allowedTypes;
  } else if (options.allowedCategories) {
    allowedTypes = options.allowedCategories.flatMap(
      (category) => ALLOWED_FILE_TYPES[category] || [],
    );
  } else {
    // Default: allow images and documents
    allowedTypes = [
      ...ALLOWED_FILE_TYPES.images,
      ...ALLOWED_FILE_TYPES.documents,
    ];
  }

  // Check file type
  if (!allowedTypes.includes(file.type)) {
    return {
      valid: false,
      error: `File type '${file.type}' is not allowed. Allowed types: ${allowedTypes.join(', ')}`,
    };
  }

  // Check file name for dangerous extensions
  const fileName = file.name.toLowerCase();

  for (const ext of DANGEROUS_EXTENSIONS) {
    if (fileName.endsWith(ext)) {
      return {
        valid: false,
        error: `File extension '${ext}' is not allowed for security reasons`,
      };
    }
  }

  // Check for double extensions (e.g., file.pdf.exe)
  const parts = fileName.split('.');
  if (parts.length > COUNT_PAIR) {
    for (let i = ONE; i < parts.length - ONE; i++) {
      const ext = `.${parts[i]}`;
      if (DANGEROUS_EXTENSIONS.includes(ext as any)) {
        return {
          valid: false,
          error: `File contains dangerous extension '${ext}' in filename`,
        };
      }
    }
  }

  // Check for suspicious file names
  const suspiciousPatterns = [
    /^con\./i,
    /^prn\./i,
    /^aux\./i,
    /^nul\./i,
    /^com[1-9]\./i,
    /^lpt[1-9]\./i,
  ];

  for (const pattern of suspiciousPatterns) {
    if (pattern.test(fileName)) {
      warnings.push('File name matches a reserved system name pattern');
      break;
    }
  }

  // Check file name length
  if (fileName.length > MAGIC_255) {
    return {
      valid: false,
      error: 'File name is too long (maximum MAGIC_255 characters)',
    };
  }

  // Check for null bytes in filename
  if (fileName.includes('\0')) {
    return {
      valid: false,
      error: 'File name contains invalid characters',
    };
  }

  return {
    valid: true,
    ...(warnings.length > ZERO && { warnings }),
  };
}

/**
 * Validate file content based on file signature (magic numbers)
 */
export async function validateFileSignature(
  file: File,
): Promise<FileValidationResult> {
  try {
    const buffer = await file.arrayBuffer();
    const bytes = new Uint8Array(buffer);

    // Check for common file signatures
    const signatures = {
      'image/jpeg': [HEX_BYTE_MAX, HEX_JPEG_MARKER_1, HEX_BYTE_MAX],
      'image/png': [HEX_PNG_SIGNATURE_1, HEX_PNG_SIGNATURE_2, HEX_PNG_SIGNATURE_3, HEX_PNG_SIGNATURE_4],
      'image/gif': [HEX_PNG_SIGNATURE_4, HEX_PNG_SIGNATURE_5, HEX_PNG_SIGNATURE_6],
      'application/pdf': [HEX_PDF_MARKER, HEX_PNG_SIGNATURE_2, HEX_PDF_SIGNATURE_1, HEX_PNG_SIGNATURE_6],
      'application/zip': [HEX_PNG_SIGNATURE_2, HEX_ZIP_SIGNATURE, MAGIC_HEX_03, MAGIC_HEX_04],
    };

    // Get the declared MIME type
    const declaredType = file.type;
    const expectedSignature =
      signatures[declaredType as keyof typeof signatures];

    if (expectedSignature) {
      const actualSignature = Array.from(
        bytes.slice(ZERO, expectedSignature.length),
      );
      const matches = expectedSignature.every(
        (byte, index) => byte === actualSignature[index],
      );

      if (!matches) {
        return {
          valid: false,
          error: `File signature does not match declared type '${declaredType}'`,
        };
      }
    }

    return { valid: true };
  } catch (error) {
    return {
      valid: false,
      error: 'Failed to validate file signature',
    };
  }
}

/**
 * Sanitize file name for safe storage
 */
export function sanitizeFileName(fileName: string): string {
  return fileName
    .replace(/[^a-zA-Z0-9._-]/g, '_') // Replace special chars with underscore
    .replace(/_{COUNT_PAIR,}/g, '_') // Replace multiple underscores with single
    .replace(/^_+|_+$/g, '') // Remove leading/trailing underscores
    .substring(ZERO, MAGIC_255); // Limit length
}

/**
 * Generate safe file name with timestamp
 */
export function generateSafeFileName(
  originalName: string,
  prefix?: string,
): string {
  const timestamp = Date.now();
  const extension = originalName.split('.').pop() || '';
  const baseName = originalName.replace(/\.[^/.]+$/, ''); // Remove extension
  const sanitizedBase = sanitizeFileName(baseName);

  const parts = [prefix, sanitizedBase, timestamp].filter(Boolean);

  return `${parts.join('_')}.${extension}`;
}

/**
 * Check if file is an image
 */
export function isImageFile(file: File): boolean {
  return ALLOWED_FILE_TYPES.images.includes(file.type as any);
}

/**
 * Check if file is a document
 */
export function isDocumentFile(file: File): boolean {
  return ALLOWED_FILE_TYPES.documents.includes(file.type as any);
}

/**
 * Get file category
 */
export function getFileCategory(
  file: File,
): keyof typeof ALLOWED_FILE_TYPES | 'unknown' {
  if (ALLOWED_FILE_TYPES.images.includes(file.type as any)) {
    return 'images';
  }
  if (ALLOWED_FILE_TYPES.documents.includes(file.type as any)) {
    return 'documents';
  }
  if (ALLOWED_FILE_TYPES.archives.includes(file.type as any)) {
    return 'archives';
  }
  return 'unknown';
}

/**
 * Validate multiple files
 */
export function validateMultipleFiles(
  files: FileList | File[],
  options: {
    maxFiles?: number;
    maxTotalSizeMB?: number;
    maxSizeMB?: number;
    allowedTypes?: string[];
    allowedCategories?: Array<keyof typeof ALLOWED_FILE_TYPES>;
  } = {},
): FileValidationResult {
  const fileArray = Array.from(files);

  // Check file count
  if (options.maxFiles && fileArray.length > options.maxFiles) {
    return {
      valid: false,
      error: `Too many files. Maximum allowed: ${options.maxFiles}`,
    };
  }

  // Check total size
  if (options.maxTotalSizeMB) {
    const totalSize = fileArray.reduce((sum, file) => sum + file.size, ZERO);
    const maxTotalSize =
      options.maxTotalSizeMB *
      FILE_UPLOAD_CONSTANTS.BYTES_PER_MB *
      FILE_UPLOAD_CONSTANTS.KB_TO_BYTES;

    if (totalSize > maxTotalSize) {
      return {
        valid: false,
        error: `Total file size exceeds ${options.maxTotalSizeMB}MB limit`,
      };
    }
  }

  // Validate each file
  for (let i = ZERO; i < fileArray.length; i++) {
    const file = fileArray[i];
    if (!file) continue;
    const result = validateFileUpload(file, options);
    if (!result.valid) {
      return {
        valid: false,
        error: `File ${i + ONE} (${fileArray[i]?.name}): ${result.error}`,
      };
    }
  }

  return { valid: true };
}

