/**
 * ç”Ÿæˆæœ¬åœ°åŒ–å…ƒæ•°æ®
 */
export async function generateLocalizedMetadata(
  locale: Locale,
  pageType: PageType,
  config: SEOConfig = {},
): Promise<Metadata> {
  // ä½¿ç”¨åŸå§‹çš„getTranslationsï¼Œç¼“å­˜å·²åœ¨åº•å±‚å®ç°
  const t = await getTranslations({ locale, namespace: 'seo' });

  const title =
    config.title || t('title', { defaultValue: SITE_CONFIG.seo.defaultTitle });
  const description =
    config.description ||
    t('description', {
      defaultValue: SITE_CONFIG.seo.defaultDescription,
    });
  const siteName = t('siteName', { defaultValue: SITE_CONFIG.name });

  const metadata: Metadata = {
    title,
    description,
    keywords: config.keywords,

    // Open Graphæœ¬åœ°åŒ–
    openGraph: {
      title,
      description,
      siteName,
      locale,
      type: (config.type === 'product' ? 'website' : config.type) || 'website',
      images: config.image ? [{ url: config.image }] : undefined,
      publishedTime: config.publishedTime,
      modifiedTime: config.modifiedTime,
      authors: config.authors,
      section: config.section,
    },

    // Twitter Card
    twitter: {
      card: 'summary_large_image',
      title,
      description,
      images: config.image ? [config.image] : undefined,
    },

    // hreflangå’Œcanonicalé“¾æ¥
    alternates: {
      canonical: generateCanonicalURL(pageType, locale),
      languages: generateLanguageAlternates(pageType),
    },

    // å…¶ä»–å…ƒæ•°æ®
    robots: {
      index: true,
      follow: true,
      googleBot: {
        'index': true,
        'follow': true,
        'max-video-preview': -ONE,
        'max-image-preview': 'large',
        'max-snippet': -ONE,
      },
    },

    // éªŒè¯æ ‡ç­¾
    verification: {
      google: process.env['GOOGLE_SITE_VERIFICATION'],
      yandex: process.env['YANDEX_VERIFICATION'],
    },
  };

  return metadata;
}

