const {RANDOM_ID_BASE} = ALERT_SYSTEM_CONSTANTS;
import { COUNT_PAIR, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { MAGIC_9 } from "@/constants/count";

// å¸¸é‡å®šä¹‰
const ALERT_ID_CONSTANTS = {
  BASE_36: ALERT_SYSTEM_CONSTANTS.RANDOM_ID_BASE,
  SUBSTR_START: COUNT_PAIR,
  SUBSTR_LENGTH: MAGIC_9,
} as const;

/**
 * è­¦æŠ¥é€šçŸ¥å¤„ç†å™¨
 * è´Ÿè´£å‘é€å„ç§ç±»å‹çš„è­¦æŠ¥é€šçŸ¥
 */

export interface AlertData {
  type: 'metric' | 'regression';
  severity: 'warning' | 'critical';
  message: string;
  metric?: string;
  value?: number;
  threshold?: number;
}

/**
 * å‘é€æ§åˆ¶å°é¢„è­¦
 */
export function sendConsoleAlerts(alerts: AlertData[]): void {
  alerts.forEach((alert) => {
    const emoji = alert.severity === 'critical' ? 'ğŸ”´' : 'ğŸŸ¡';
    const prefix = `[${alert.type.toUpperCase()}] ${emoji}`;

    if (alert.severity === 'critical') {
      logger.error(`${prefix} ${alert.message}`, {
        metric: alert.metric,
        value: alert.value,
        threshold: alert.threshold,
      });
    } else {
      logger.warn(`${prefix} ${alert.message}`, {
        metric: alert.metric,
        value: alert.value,
        threshold: alert.threshold,
      });
    }
  });
}

/**
 * å‘é€Webhooké€šçŸ¥
 */
export async function sendWebhookNotification(
  alert: AlertData,
  webhookUrl: string,
): Promise<void> {
  try {
    const payload = {
      type: 'performance_alert',
      severity: alert.severity,
      message: alert.message,
      timestamp: new Date().toISOString(),
      data: {
        metric: alert.metric,
        value: alert.value
          ? formatMetricValue(alert.metric || '', alert.value)
          : undefined,
        threshold: alert.threshold
          ? formatMetricValue(alert.metric || '', alert.threshold)
          : undefined,
      },
    };

    const response = await fetch(webhookUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    });

    if (!response.ok) {
      throw new Error(`Webhook request failed: ${response.status}`);
    }

    logger.info('Webhook notification sent successfully', {
      url: webhookUrl,
      severity: alert.severity,
    });
  } catch (error) {
    logger.error('Failed to send webhook notification', {
      error: error instanceof Error ? error.message : 'Unknown error',
      url: webhookUrl,
      alert: alert.message,
    });
  }
}

/**
 * å­˜å‚¨é¢„è­¦åˆ°æœ¬åœ°å­˜å‚¨
 */
export function storeAlerts(alerts: AlertData[]): void {
  if (typeof localStorage === 'undefined') return;

  try {
    const existingAlerts = JSON.parse(
      localStorage.getItem('performance_alerts') || '[]',
    );
    const newAlerts = alerts.map((alert) => ({
      ...alert,
      id: `alert-${Date.now()}-${Math.random().toString(ALERT_ID_CONSTANTS.BASE_36).substr(ALERT_ID_CONSTANTS.SUBSTR_START, ALERT_ID_CONSTANTS.SUBSTR_LENGTH)}`,
      timestamp: Date.now(),
    }));

    const allAlerts = [...existingAlerts, ...newAlerts];

    // é™åˆ¶å­˜å‚¨çš„è­¦æŠ¥æ•°é‡
    const maxAlerts = PERCENTAGE_FULL;
    if (allAlerts.length > maxAlerts) {
      allAlerts.splice(ZERO, allAlerts.length - maxAlerts);
    }

    localStorage.setItem('performance_alerts', JSON.stringify(allAlerts));
  } catch (error) {
    logger.error('Failed to store alerts to localStorage', {
      error: error instanceof Error ? error.message : 'Unknown error',
      alertCount: alerts.length,
    });
  }
}

/**
 * æ€§èƒ½é¢„è­¦ç³»ç»Ÿ - é¢„è­¦å‘é€å™¨
 * Performance Alert System - Alert Sender
 */

import { ONE, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { ALERT_SYSTEM_CONSTANTS } from '@/constants/performance-constants';
import type { AlertInfo } from '@/lib/web-vitals/alert-system-checker';
import type { PerformanceAlert, PerformanceAlertConfig } from '@/lib/web-vitals/types';
import {
  sendConsoleAlerts,
  sendWebhookNotification,
  storeAlerts,
} from '@/lib/web-vitals/alert-notifications';

/**
 * é¢„è­¦å†å²è®°å½•æ¥å£
 */
export interface AlertHistoryEntry {
  id: string;
  timestamp: number;
  severity: 'warning' | 'critical';
  message: string;
  metric?: string;
  value?: number;
  threshold?: number;
  level?: 'warning' | 'critical'; // ä¸ºäº†å…¼å®¹æµ‹è¯•
}

/**
 * æ€§èƒ½é¢„è­¦å‘é€å™¨ç±»
 * Performance alert sender class
 */
export class AlertSystemSender {
  private alertHistory: AlertHistoryEntry[] = [];

  /**
   * å‘é€é¢„è­¦
   */
  sendAlerts(alerts: AlertInfo[], config: PerformanceAlertConfig): void {
    // æ·»åŠ åˆ°å†å²è®°å½•
    alerts.forEach((alert) => {
      const historyEntry: AlertHistoryEntry = {
        id: this.generateAlertId(),
        timestamp: Date.now(),
        severity: alert.severity,
        message: alert.message,
        level: alert.severity, // ä¸ºäº†å…¼å®¹æµ‹è¯•
        ...(alert.metric && { metric: alert.metric }),
        ...(alert.value !== undefined && { value: alert.value }),
        ...(alert.threshold !== undefined && { threshold: alert.threshold }),
      };
      this.alertHistory.push(historyEntry);
    });

    // é™åˆ¶å†å²è®°å½•å¤§å°
    this.limitHistorySize();

    // æ§åˆ¶å°é€šçŸ¥
    if (config.channels.console) {
      sendConsoleAlerts(alerts);
    }

    // å­˜å‚¨é€šçŸ¥
    if (config.channels.storage) {
      storeAlerts(alerts);
    }

    // å›è°ƒé€šçŸ¥
    if (config.channels.callback) {
      this.sendCallbackAlerts(alerts, config.channels.callback);
    }
  }

  /**
   * å‘é€å•ä¸ªè­¦æŠ¥ (æµ‹è¯•æ–¹æ³•)
   */
  async sendAlert(
    severity: 'warning' | 'critical',
    message: string,
    config: PerformanceAlertConfig,
    data?: Record<string, unknown>,
  ): Promise<void> {
    const alert: AlertInfo = {
      type: 'metric',
      severity,
      message,
      ...data,
    };

    // ç›´æ¥æ·»åŠ åˆ°å†å²è®°å½•ï¼Œä¸é€šè¿‡sendAlertsé¿å…é‡å¤
    const alertId = this.generateAlertId();
    const historyEntry: AlertHistoryEntry = {
      id: alertId,
      timestamp: Date.now(),
      severity,
      message,
      level: severity, // ä¸ºäº†å…¼å®¹æµ‹è¯•
      ...data,
    };
    this.alertHistory.push(historyEntry);

    // é™åˆ¶å†å²è®°å½•å¤§å°
    this.limitHistorySize();

    // ç›´æ¥å‘é€é€šçŸ¥ï¼Œä¸é€šè¿‡sendAlertsé¿å…é‡å¤æ·»åŠ å†å²
    if (config.channels.console) {
      sendConsoleAlerts([alert]);
    }

    // å¤„ç†webhooké€šçŸ¥
    if (config.channels?.webhook) {
      await sendWebhookNotification(alert, config.channels.webhook);
    }
  }

  /**
   * å‘é€å›è°ƒé¢„è­¦
   */
  private sendCallbackAlerts(
    alerts: AlertInfo[],
    callback: (alert: PerformanceAlert) => void,
  ): void {
    alerts.forEach((alert) => {
      const performanceAlert: PerformanceAlert = {
        id: this.generateAlertId(),
        timestamp: Date.now(),
        severity: alert.severity,
        metric: alert.metric || 'unknown',
        value: alert.value || 0,
        threshold: alert.threshold || 0,
        message: alert.message,
        url: typeof window !== 'undefined' ? window.location.href : '',
        userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : '',
        context: {},
      };
      callback(performanceAlert);
    });
  }

  /**
   * è·å–è­¦æŠ¥å†å² (æµ‹è¯•æ–¹æ³•)
   */
  getAlertHistory(): AlertHistoryEntry[] {
    return [...this.alertHistory];
  }

  /**
   * æ¸…é™¤è­¦æŠ¥å†å² (æµ‹è¯•æ–¹æ³•)
   */
  clearAlertHistory(): void {
    this.alertHistory = [];
  }

  /**
   * æ¸…é™¤å†å²è®°å½•çš„åˆ«åæ–¹æ³• (æµ‹è¯•å…¼å®¹)
   */
  clearHistory(): void {
    this.clearAlertHistory();
  }

  /**
   * ç”Ÿæˆå”¯ä¸€çš„è­¦æŠ¥ID
   */
  private generateAlertId(): string {
    const timestamp = Date.now();
    const randomPart = Math.random()
      .toString(ALERT_SYSTEM_CONSTANTS.RANDOM_ID_BASE)
      .substr(
        ALERT_SYSTEM_CONSTANTS.RANDOM_ID_START,
        ALERT_SYSTEM_CONSTANTS.RANDOM_ID_LENGTH,
      );
    return `alert-${timestamp}-${randomPart}`;
  }

  /**
   * é™åˆ¶å†å²è®°å½•å¤§å°
   */
  private limitHistorySize(): void {
    if (this.alertHistory.length > PERCENTAGE_FULL) {
      this.alertHistory.splice(ZERO, this.alertHistory.length - PERCENTAGE_FULL);
    }
  }

  /**
   * è·å–å†å²è®°å½•ç»Ÿè®¡
   */
  getHistoryStats(): {
    total: number;
    warnings: number;
    criticals: number;
    lastAlert?: AlertHistoryEntry;
  } {
    const warnings = this.alertHistory.filter(
      (alert) => alert.severity === 'warning',
    ).length;
    const criticals = this.alertHistory.filter(
      (alert) => alert.severity === 'critical',
    ).length;
    const lastAlert = this.alertHistory[this.alertHistory.length - ONE];

    return {
      total: this.alertHistory.length,
      warnings,
      criticals,
      ...(lastAlert && { lastAlert }),
    };
  }

  /**
   * æ ¹æ®æ¡ä»¶è¿‡æ»¤å†å²è®°å½•
   */
  filterHistory(filter: {
    severity?: 'warning' | 'critical';
    metric?: string;
    startTime?: number;
    endTime?: number;
  }): AlertHistoryEntry[] {
    return this.alertHistory.filter((alert) => {
      if (filter.severity && alert.severity !== filter.severity) return false;
      if (filter.metric && alert.metric !== filter.metric) return false;
      if (filter.startTime && alert.timestamp < filter.startTime) return false;
      if (filter.endTime && alert.timestamp > filter.endTime) return false;
      return true;
    });
  }
}

