  /**
   * åˆ†æèµ„æºåŠ è½½æ€§èƒ½å¹¶ç”Ÿæˆé—®é¢˜å’Œå»ºè®®
   */
  protected analyzeResourceTiming(
    resourceTiming: DetailedWebVitals['resourceTiming'],
    issues: string[],
    recommendations: string[],
  ): void {
    if (
      resourceTiming.slowResources.length > ZERO &&
      resourceTiming.slowResources[ZERO]!.duration > 500
    ) {
      issues.push(
        `å‘ç°æ…¢é€Ÿèµ„æº: ${resourceTiming.slowResources[ZERO]!.name} (${resourceTiming.slowResources[ZERO]!.duration.toFixed(ZERO)}ms)`,
      );
      recommendations.push('ä¼˜åŒ–æ…¢é€Ÿèµ„æºçš„åŠ è½½ï¼Œè€ƒè™‘å‹ç¼©æˆ–ä½¿ç”¨ CDN');
    }

    if (resourceTiming.totalSize > COUNT_PAIR * BYTES_PER_KB * BYTES_PER_KB) {
      // 2MB
      issues.push(
        `æ€»èµ„æºå¤§å°è¿‡å¤§: ${(resourceTiming.totalSize / BYTES_PER_KB / BYTES_PER_KB).toFixed(ONE)}MB`,
      );
      recommendations.push('å‡å°‘èµ„æºå¤§å°ï¼Œå¯ç”¨ gzip å‹ç¼©');
    }
  }

  /**
   * è®¡ç®—æ€§èƒ½è¯„åˆ†
   */
  protected calculatePerformanceScore(metrics: DetailedWebVitals): number {
    const { PERFECT_SCORE } = WEB_VITALS_CONSTANTS;
    let score = PERFECT_SCORE;

    // CLS è¯„åˆ†
    if (metrics.cls > PERFORMANCE_THRESHOLDS.CLS_NEEDS_IMPROVEMENT) {
      score -= PERCENTAGE_QUARTER;
    } else if (metrics.cls > PERFORMANCE_THRESHOLDS.CLS_GOOD) {
      score -= COUNT_TEN;
    }

    // LCP è¯„åˆ†
    if (metrics.lcp > PERFORMANCE_THRESHOLDS.LCP_NEEDS_IMPROVEMENT) {
      score -= DAYS_PER_MONTH;
    } else if (metrics.lcp > PERFORMANCE_THRESHOLDS.LCP_GOOD) {
      score -= MAGIC_15;
    }

    // FID è¯„åˆ†
    if (metrics.fid > PERFORMANCE_THRESHOLDS.FID_NEEDS_IMPROVEMENT) {
      score -= PERCENTAGE_QUARTER;
    } else if (metrics.fid > PERFORMANCE_THRESHOLDS.FID_GOOD) {
      score -= COUNT_TEN;
    }

    // FCP è¯„åˆ†
    if (metrics.fcp > THREE_SECONDS_MS) {
      score -= COUNT_TEN;
    } else if (metrics.fcp > MAGIC_1800) {
      score -= COUNT_FIVE;
    }

    // TTFB è¯„åˆ†
    if (metrics.ttfb > MAGIC_800) {
      score -= COUNT_TEN;
    } else if (metrics.ttfb > MAGIC_600) {
      score -= COUNT_FIVE;
    }

    return Math.max(ZERO, score);
  }

  /**
   * æ‰§è¡Œæ‰€æœ‰æ€§èƒ½æŒ‡æ ‡åˆ†æ
   */
  protected performMetricsAnalysis(
    metrics: DetailedWebVitals,
    issues: string[],
    recommendations: string[],
  ): void {
    this.analyzeCLS(metrics.cls, issues, recommendations);
    this.analyzeLCP(metrics.lcp, issues, recommendations);
    this.analyzeFID(metrics.fid, issues, recommendations);
    this.analyzeResourceTiming(metrics.resourceTiming, issues, recommendations);
  }

  /**
   * æ„å»ºè¯Šæ–­æŠ¥å‘Šç»“æœå¯¹è±¡
   */
  protected buildDiagnosticResult(
    metrics: DetailedWebVitals,
    issues: string[],
    recommendations: string[],
    score: number,
  ): {
    metrics: DetailedWebVitals;
    analysis: {
      issues: string[];
      recommendations: string[];
      score: number;
    };
  } {
    return {
      metrics,
      analysis: {
        issues,
        recommendations,
        score,
      },
    };
  }

  /**
   * ç”Ÿæˆå®Œæ•´çš„è¯Šæ–­æŠ¥å‘Š
   */
  public generateDiagnosticReport(metrics: DetailedWebVitals): {
    metrics: DetailedWebVitals;
    analysis: {
      issues: string[];
      recommendations: string[];
      score: number;
    };
  } {
    const issues: string[] = [];
    const recommendations: string[] = [];

    // æ‰§è¡Œæ‰€æœ‰åˆ†æ
    this.performMetricsAnalysis(metrics, issues, recommendations);

    // è®¡ç®—æ€§èƒ½è¯„åˆ†
    const score = this.calculatePerformanceScore(metrics);

    // æ„å»ºå¹¶è¿”å›ç»“æœ
    return this.buildDiagnosticResult(metrics, issues, recommendations, score);
  }

  /**
   * åˆ†ææ€§èƒ½è¶‹åŠ¿
   */
  public analyzePerformanceTrend(
    currentMetrics: DetailedWebVitals,
    previousMetrics: DetailedWebVitals,
  ): {
    trend: 'improving' | 'declining' | 'stable';
    changes: Array<{
      metric: string;
      current: number;
      previous: number;
      change: number;
      changePercent: number;
    }>;
  } {
    const changes = [
      {
        metric: 'LCP',
        current: currentMetrics.lcp,
        previous: previousMetrics.lcp,
        change: currentMetrics.lcp - previousMetrics.lcp,
        changePercent:
          previousMetrics.lcp > ZERO
            ? ((currentMetrics.lcp - previousMetrics.lcp) /
                previousMetrics.lcp) *
              PERCENTAGE_FULL
            : ZERO,
      },
      {
        metric: 'FID',
        current: currentMetrics.fid,
        previous: previousMetrics.fid,
        change: currentMetrics.fid - previousMetrics.fid,
        changePercent:
          previousMetrics.fid > ZERO
            ? ((currentMetrics.fid - previousMetrics.fid) /
                previousMetrics.fid) *
              PERCENTAGE_FULL
            : ZERO,
      },
      {
        metric: 'CLS',
        current: currentMetrics.cls,
        previous: previousMetrics.cls,
        change: currentMetrics.cls - previousMetrics.cls,
        changePercent:
          previousMetrics.cls > ZERO
            ? ((currentMetrics.cls - previousMetrics.cls) /
                previousMetrics.cls) *
              PERCENTAGE_FULL
            : ZERO,
      },
    ];

    // è®¡ç®—æ€»ä½“è¶‹åŠ¿
    const improvingCount = changes.filter((c) => c.change < ZERO).length;
    const decliningCount = changes.filter((c) => c.change > ZERO).length;

    let trend: 'improving' | 'declining' | 'stable';
    if (improvingCount > decliningCount) {
      trend = 'improving';
    } else if (decliningCount > improvingCount) {
      trend = 'declining';
    } else {
      trend = 'stable';
    }

    return { trend, changes };
  }
}

/**
 * Web Vitals æ”¶é›†å™¨åŸºç¡€ç±»
 * Web Vitals collector base class
 */

'use client';

import { COUNT_TEN, PERCENTAGE_FULL, ZERO } from "@/constants/magic-numbers";
import { WebVitalsObservers } from '@/lib/web-vitals/observers';
import type { DetailedWebVitals } from '@/lib/web-vitals/types';

/**
 * Web Vitals æ”¶é›†å™¨åŸºç¡€ç±»
 * è´Ÿè´£åŸºç¡€æ•°æ®æ”¶é›†å’Œåˆå§‹åŒ–
 */
export class WebVitalsCollectorBase {
  protected metrics: Partial<DetailedWebVitals> = {};
  protected webVitalsObservers: WebVitalsObservers;
  protected isCollecting = false;

  constructor() {
    this.webVitalsObservers = new WebVitalsObservers(this.metrics);
    this.initializeCollection();
  }

  protected initializeCollection() {
    if (typeof window === 'undefined' || this.isCollecting) return;

    this.isCollecting = true;
    this.collectBasicPageInfo();
    this.collectDeviceInfo();
    this.collectNetworkInfo();
    this.collectNavigationTiming();
    this.collectResourceTiming();
    this.collectWebVitals();
  }

  protected collectBasicPageInfo() {
    this.metrics.page = {
      url: window.location.href,
      referrer: document.referrer,
      title: document.title,
      timestamp: Date.now(),
    };
  }

  protected collectDeviceInfo() {
    const nav = navigator as Navigator & {
      deviceMemory?: number;
      hardwareConcurrency?: number;
    };
    this.metrics.device = {
      userAgent: navigator.userAgent,
      ...(nav.deviceMemory !== undefined && { memory: nav.deviceMemory }),
      ...(nav.hardwareConcurrency !== undefined && {
        cores: nav.hardwareConcurrency,
      }),
      viewport: {
        width: window.innerWidth,
        height: window.innerHeight,
      },
    };
  }

  protected collectNetworkInfo() {
    const nav = navigator as Navigator & {
      connection?: {
        effectiveType: string;
        downlink: number;
        rtt: number;
        saveData: boolean;
      };
    };
    if (nav.connection) {
      this.metrics.connection = {
        effectiveType: nav.connection.effectiveType,
        downlink: nav.connection.downlink,
        rtt: nav.connection.rtt,
        saveData: nav.connection.saveData,
      };
    }
  }

  protected collectNavigationTiming() {
    const navigation = performance.getEntriesByType(
      'navigation',
    )[ZERO] as PerformanceNavigationTiming;
    if (navigation) {
      // ä½¿ç”¨ startTime ä½œä¸ºåŸºå‡†æ—¶é—´ç‚¹ï¼ˆç›¸å½“äº navigationStartï¼‰
      this.metrics.fcp = navigation.responseEnd - navigation.startTime;
      this.metrics.domContentLoaded =
        navigation.domContentLoadedEventEnd - navigation.startTime;
      this.metrics.loadComplete =
        navigation.loadEventEnd - navigation.startTime;
      this.metrics.ttfb = navigation.responseStart - navigation.startTime;
    }
  }

  protected collectResourceTiming() {
    const resources = performance.getEntriesByType(
      'resource',
    ) as PerformanceResourceTiming[];

    const slowResources = resources
      .filter((resource) => resource.duration > PERCENTAGE_FULL) // è¶…è¿‡100msçš„èµ„æº
      .map((resource) => ({
        name: resource.name,
        duration: resource.duration,
        size: resource.transferSize || ZERO,
        type: this.getResourceType(resource.name),
      }))
      .sort((a, b) => b.duration - a.duration)
      .slice(ZERO, COUNT_TEN); // åªä¿ç•™å‰COUNT_TENä¸ªæœ€æ…¢çš„èµ„æº

    this.metrics.resourceTiming = {
      totalResources: resources.length,
      slowResources,
      totalSize: resources.reduce((sum, r) => sum + (r.transferSize || ZERO), ZERO),
      totalDuration: resources.reduce((sum, r) => sum + r.duration, ZERO),
    };
  }

  protected getResourceType(url: string): string {
    if (url.includes('.js')) return 'JavaScript';
    if (url.includes('.css')) return 'CSS';
    if (url.match(/\.(jpg|jpeg|png|gif|webp|svg)$/i)) return 'Image';
    if (url.match(/\.(woff|woff2|ttf|otf)$/i)) return 'Font';
    if (url.includes('api/') || url.includes('/api/')) return 'API';
    return 'Other';
  }

  protected collectWebVitals() {
    // å¯åŠ¨æ‰€æœ‰ Web Vitals è§‚å¯Ÿå™¨
    this.webVitalsObservers.startAllObservers();
  }

  /**
   * è·å–é»˜è®¤çš„èµ„æºæ—¶é—´ä¿¡æ¯
   */
  protected getDefaultResourceTiming() {
    return {
      totalResources: ZERO,
      slowResources: [],
      totalSize: ZERO,
      totalDuration: ZERO,
    };
  }

  /**
   * è·å–é»˜è®¤çš„è¿æ¥ä¿¡æ¯
   */
  protected getDefaultConnection() {
    return {
      effectiveType: 'unknown' as const,
      downlink: ZERO,
      rtt: ZERO,
      saveData: false,
    };
  }

  /**
   * è·å–é»˜è®¤çš„è®¾å¤‡ä¿¡æ¯
   */
  protected getDefaultDevice() {
    const {deviceMemory} = (navigator as Navigator & { deviceMemory?: number });
    const {hardwareConcurrency} = navigator;

    return {
      ...(deviceMemory !== undefined && { memory: deviceMemory }),
      ...(hardwareConcurrency !== undefined && { cores: hardwareConcurrency }),
      userAgent: navigator.userAgent,
      viewport: {
        width: window?.innerWidth || ZERO,
        height: window?.innerHeight || ZERO,
      },
    };
  }

  /**
   * è·å–é»˜è®¤çš„é¡µé¢ä¿¡æ¯
   */
  protected getDefaultPage() {
    return {
      url: '',
      referrer: '',
      title: '',
      timestamp: Date.now(),
    };
  }

  public getDetailedMetrics(): DetailedWebVitals {
    // ç¡®ä¿æ‰€æœ‰å¿…éœ€çš„å­—æ®µéƒ½æœ‰é»˜è®¤å€¼
    return {
      cls: this.metrics.cls || ZERO,
      fid: this.metrics.fid || ZERO,
      lcp: this.metrics.lcp || ZERO,
      fcp: this.metrics.fcp || ZERO,
      firstPaint: this.metrics.firstPaint || ZERO,
      ttfb: this.metrics.ttfb || ZERO,
      domContentLoaded: this.metrics.domContentLoaded || ZERO,
      loadComplete: this.metrics.loadComplete || ZERO,
      page: this.metrics.page || this.getDefaultPage(),
      device: this.metrics.device || this.getDefaultDevice(),
      connection: this.metrics.connection || this.getDefaultConnection(),
      resourceTiming:
        this.metrics.resourceTiming || this.getDefaultResourceTiming(),
    };
  }

  /**
   * è·å–æ”¶é›†çŠ¶æ€ï¼ˆä¾›å­ç±»è®¿é—®ï¼‰
   */
  protected get collectingStatus(): boolean {
    return this.isCollecting;
  }

  public cleanup() {
    this.webVitalsObservers.cleanup();
    this.isCollecting = false;
  }
}

