let whatsappService: WhatsAppService | null = null;

export function getWhatsAppService(): WhatsAppService {
  if (!whatsappService) {
    whatsappService = new WhatsAppService();
  }
  return whatsappService;
}

// æ¶ˆæ¯ç±»å‹å®šä¹‰
export interface WhatsAppMessage {
  to: string;
  type: 'text' | 'template' | 'media';
  content: {
    body?: string;
    templateName?: string;
    languageCode?: string;
    components?: Array<Record<string, unknown>>;
    mediaUrl?: string;
    caption?: string;
  };
}

// å‘é€æ¶ˆæ¯çš„é€šç”¨å‡½æ•°
export function sendWhatsAppMessage(message: WhatsAppMessage) {
  const service = getWhatsAppService();

  switch (message.type) {
    case 'text':
      return service.sendTextMessage(message.to, message.content.body!);

    case 'template':
      return service.sendTemplateMessage(
        message.to,
        message.content.templateName!,
        message.content.languageCode,
        message.content.components,
      );

    default:
      throw new Error(`Unsupported message type: ${message.type}`);
  }
}

/**
 * é›†ä¸­URLç”ŸæˆæœåŠ¡
 * ç»Ÿä¸€ç®¡ç†æ‰€æœ‰URLç”Ÿæˆé€»è¾‘ï¼Œç¡®ä¿ä¸€è‡´æ€§å’Œç±»å‹å®‰å…¨
