export const SUPPORTED_LANGUAGE_CODES = [
  'en_US', // English (US)
  'en_GB', // English (UK)
  'es_ES', // Spanish (Spain)
  'es_MX', // Spanish (Mexico)
  'pt_BR', // Portuguese (Brazil)
  'fr_FR', // French (France)
  'de_DE', // German (Germany)
  'it_IT', // Italian (Italy)
