 * Union type for incoming WhatsApp messages
 */
export type IncomingWhatsAppMessage =
  | IncomingTextMessage
  | IncomingImageMessage
  | IncomingDocumentMessage
  | IncomingAudioMessage
  | IncomingVideoMessage
  | IncomingLocationMessage
  | IncomingContactsMessage
  | IncomingInteractiveMessage
  | IncomingReactionMessage
  | IncomingStickerMessage
  | IncomingOrderMessage
  | IncomingSystemMessage
  | IncomingButtonMessage
  | IncomingTemplateReply;

/**
 * æ¶ˆæ¯ç±»å‹å¸¸é‡
 * Message type constants
 */
export const INCOMING_MESSAGE_TYPES = [
  'text',
  'image',
  'document',
  'audio',
  'video',
  'location',
  'contacts',
  'interactive',
  'reaction',
  'sticker',
  'order',
  'system',
  'button',
  'template_reply',
] as const;

export const MEDIA_MESSAGE_TYPES = [
  'image',
  'document',
  'audio',
  'video',
  'sticker',
] as const;

export const INTERACTIVE_MESSAGE_TYPES = [
  'interactive',
  'button',
  'template_reply',
] as const;

/**
 * æ¶ˆæ¯ç±»å‹
 * Message types
 */
export type IncomingMessageType = (typeof INCOMING_MESSAGE_TYPES)[number];
export type MediaMessageType = (typeof MEDIA_MESSAGE_TYPES)[number];
export type InteractiveMessageType = (typeof INTERACTIVE_MESSAGE_TYPES)[number];

/**
 * ç±»å‹å®ˆå«å‡½æ•°
 * Type guard functions
 */
export function isTextMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingTextMessage {
  return message.type === 'text';
}

export function isImageMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingImageMessage {
  return message.type === 'image';
}

export function isDocumentMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingDocumentMessage {
  return message.type === 'document';
}

export function isAudioMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingAudioMessage {
  return message.type === 'audio';
}

export function isVideoMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingVideoMessage {
  return message.type === 'video';
}

export function isLocationMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingLocationMessage {
  return message.type === 'location';
}

export function isContactsMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingContactsMessage {
  return message.type === 'contacts';
}

export function isInteractiveMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingInteractiveMessage {
  return message.type === 'interactive';
}

export function isReactionMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingReactionMessage {
  return message.type === 'reaction';
}

export function isStickerMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingStickerMessage {
  return message.type === 'sticker';
}

export function isOrderMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingOrderMessage {
  return message.type === 'order';
}

export function isSystemMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingSystemMessage {
  return message.type === 'system';
}

export function isButtonMessage(
  message: IncomingWhatsAppMessage,
): message is IncomingButtonMessage {
  return message.type === 'button';
}

export function isTemplateReply(
  message: IncomingWhatsAppMessage,
): message is IncomingTemplateReply {
  return message.type === 'template_reply';
}

export function isMediaMessage(
  message: IncomingWhatsAppMessage,
): message is
  | IncomingImageMessage
  | IncomingDocumentMessage
  | IncomingAudioMessage
  | IncomingVideoMessage
  | IncomingStickerMessage {
  return MEDIA_MESSAGE_TYPES.includes(message.type as MediaMessageType);
}

export function isInteractiveMessageType(
  message: IncomingWhatsAppMessage,
): message is
  | IncomingInteractiveMessage
  | IncomingButtonMessage
  | IncomingTemplateReply {
  return INTERACTIVE_MESSAGE_TYPES.includes(
    message.type as InteractiveMessageType,
  );
}

/**
 * æ¶ˆæ¯å†…å®¹æå–å™¨
 * Message content extractors
 */
export function getMessageText(
  message: IncomingWhatsAppMessage,
): string | null {
  switch (message.type) {
    case 'text':
      return message.text.body;
    case 'image':
    case 'document':
    case 'video':
      return message[message.type]?.caption || null;
    case 'interactive':
      return (
        message.interactive.button_reply?.title ||
        message.interactive.list_reply?.title ||
        null
      );
    case 'button':
      return message.button.text;
    case 'template_reply':
      return message.template_reply.button_text;
    case 'system':
      return message.system.body;
    default:
      return null;
  }
}

export function getMessageMediaId(
  message: IncomingWhatsAppMessage,
): string | null {
  if (isMediaMessage(message)) {
    const mediaData = (message as any)[message.type];
    return mediaData?.id || null;
  }
  return null;
}

export function hasMessageContext(message: IncomingWhatsAppMessage): boolean {
  return message.context !== undefined;
}

/**
 * WhatsApp Webhook å·¥å…·ç±»
 * WhatsApp Webhook Utility Class
 */

import { MAGIC_0_95, MAGIC_0_99, ONE, ZERO } from "@/constants/magic-numbers";
import type { WebhookEntry, WebhookPayload } from '@/types/whatsapp-webhook-base';
import type { IncomingWhatsAppMessage } from '@/types/whatsapp-webhook-messages';
import type {
  EventFilter,
  EventStatistics,
  MessageReceivedEvent,
  WebhookEvent,
} from '@/types/whatsapp-webhook-events';
import type {
  SignatureVerificationConfig,
  WebhookParsingResult,
  WebhookValidationResult,
} from '@/types/whatsapp-webhook-utils/interfaces';

/**
 * Webhookå·¥å…·å‡½æ•°
 * Webhook utility functions
 */
export class WebhookUtils {
  /**
   * è§£æWebhookè½½è·ä¸ºäº‹ä»¶
   * Parse webhook payload into events
   */
  static parseWebhookPayload(payload: WebhookPayload): WebhookParsingResult {
    const startTime = Date.now();
    const events: WebhookEvent[] = [];
    const errors: Array<{
      entry_id?: string;
      error: string;
      raw_data?: Record<string, unknown> | string | unknown[];
    }> = [];

    try {
      for (const entry of payload.entry) {
        try {
          const entryEvents = this.parseWebhookEntry(entry);
          events.push(...entryEvents);
        } catch (error) {
          errors.push({
            entry_id: entry.id,
            error:
              error instanceof Error ? error.message : 'Unknown parsing error',
            raw_data: entry as unknown as Record<string, unknown>,
          });
        }
      }

      return {
        success: errors.length === ZERO,
        events,
        errors,
        metadata: {
          total_entries: payload.entry.length,
          parsed_entries: payload.entry.length - errors.length,
          total_events: events.length,
          parsing_time_ms: Date.now() - startTime,
        },
      };
    } catch (error) {
      return {
        success: false,
        events: [],
        errors: [
          {
            error:
              error instanceof Error
                ? error.message
                : 'Failed to parse webhook payload',
            raw_data: payload as unknown as Record<string, unknown>,
          },
        ],
        metadata: {
          total_entries: payload.entry?.length || ZERO,
          parsed_entries: ZERO,
          total_events: ZERO,
          parsing_time_ms: Date.now() - startTime,
        },
      };
    }
  }

  /**
   * è§£æå•ä¸ªWebhookæ¡ç›®
   * Parse single webhook entry
   */
  private static parseWebhookEntry(entry: WebhookEntry): WebhookEvent[] {
    const events: WebhookEvent[] = [];
    const timestamp = new Date().toISOString();

    for (const change of entry.changes) {
      const { value } = change;
      const phoneNumberId = value.metadata.phone_number_id;

      // å¤„ç†æ¶ˆæ¯
      if (value.messages) {
        for (const message of value.messages) {
          const contact = value.contacts?.find((c) => c.wa_id === message.from);
          const messageEvent: MessageReceivedEvent = {
            type: 'message_received',
            timestamp,
            phone_number_id: phoneNumberId,
            from: message.from,
            message: message as IncomingWhatsAppMessage,
          };

          // åªæœ‰å½“contactå­˜åœ¨æ—¶æ‰è®¾ç½®å¯é€‰å±æ€§
          if (contact) {
            messageEvent.contact = contact;
          }

          events.push(messageEvent);
        }
      }

      // å¤„ç†çŠ¶æ€æ›´æ–°
      if (value.statuses) {
        for (const status of value.statuses) {
          events.push({
            type: 'message_status',
            timestamp,
            phone_number_id: phoneNumberId,
            status_update: status,
          });
        }
      }

      // å¤„ç†é”™è¯¯
      if (value.errors) {
        for (const error of value.errors) {
          events.push({
            type: 'webhook_error',
            timestamp,
            phone_number_id: phoneNumberId,
            error,
          });
        }
      }
    }

    return events;
  }

  /**
   * éªŒè¯Webhookè½½è·
   * Validate webhook payload
   */
  static validateWebhookPayload(payload: unknown): WebhookValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    // åŸºæœ¬ç»“æ„éªŒè¯
    if (!payload || typeof payload !== 'object') {
      errors.push('Payload must be an object');
      return { is_valid: false, errors, warnings };
    }

    if (
      (payload as Record<string, unknown>).object !==
      'whatsapp_business_account'
    ) {
      errors.push('Invalid object type, expected "whatsapp_business_account"');
    }

    if (!Array.isArray((payload as Record<string, unknown>).entry)) {
      errors.push('Entry must be an array');
    } else {
      // éªŒè¯æ¯ä¸ªæ¡ç›®
      (
        (payload as Record<string, unknown>).entry as Record<string, unknown>[]
      ).forEach((entry: Record<string, unknown>, index: number) => {
        if (!entry.id) {
          errors.push(`Entry ${index}: Missing id`);
        }
        if (!Array.isArray(entry.changes)) {
          errors.push(`Entry ${index}: Changes must be an array`);
        } else {
          (entry.changes as Record<string, unknown>[]).forEach(
            (change: Record<string, unknown>, changeIndex: number) => {
              if (!change.value) {
                errors.push(
                  `Entry ${index}, Change ${changeIndex}: Missing value`,
                );
              }
              const changeValue = change.value as Record<string, unknown>;
              const metadata = changeValue?.metadata as Record<string, unknown>;
              if (!metadata?.phone_number_id) {
                errors.push(
                  `Entry ${index}, Change ${changeIndex}: Missing phone_number_id`,
                );
              }
            },
          );
        }
      });
    }

    return {
      is_valid: errors.length === ZERO,
      errors,
      warnings,
      payload_valid: errors.length === ZERO,
    };
  }

  /**
   * éªŒè¯Webhookç­¾å
   * Verify webhook signature
   */
  static verifyWebhookSignature(
    payload: string,
    signature: string,
    config: SignatureVerificationConfig,
  ): boolean {
    try {
      const crypto = require('crypto');
      const expectedSignature = crypto
        .createHmac(config.algorithm, config.app_secret)
        .update(payload)
        .digest('hex');

      const receivedSignature = signature.replace(/^sha\d+=/, '');
      return crypto.timingSafeEqual(
        Buffer.from(expectedSignature, 'hex'),
        Buffer.from(receivedSignature, 'hex'),
      );
    } catch (error) {
      return false;
    }
  }

  /**
   * ç”Ÿæˆäº‹ä»¶å”¯ä¸€é”®
   * Generate event unique key
   */
  static generateEventKey(event: WebhookEvent): string {
    switch (event.type) {
      case 'message_received':
        return `msg_${event.message.id}_${event.timestamp}`;
      case 'message_status':
        return `status_${event.status_update.id}_${event.status_update.status}_${event.timestamp}`;
      default:
        return `${event.type}_${event.phone_number_id}_${event.timestamp}`;
    }
  }

  /**
   * è¿‡æ»¤äº‹ä»¶
   * Filter events
   */
  static filterEvents(
    events: WebhookEvent[],
    filter: EventFilter,
  ): WebhookEvent[] {
    return events.filter((event) => {
      // äº‹ä»¶ç±»å‹è¿‡æ»¤
      if (filter.event_types && !filter.event_types.includes(event.type)) {
        return false;
      }

      // ç”µè¯å·ç è¿‡æ»¤
      if (
        filter.phone_number_ids &&
        !filter.phone_number_ids.includes(event.phone_number_id)
      ) {
        return false;
      }

      // å‘é€è€…è¿‡æ»¤
      if (filter.sender_filters && 'from' in event) {
        const from = (event as unknown as Record<string, unknown>)
          .from as string;
        if (
          filter.sender_filters.include &&
          !filter.sender_filters.include.includes(from)
        ) {
          return false;
        }
        if (
          filter.sender_filters.exclude &&
          filter.sender_filters.exclude.includes(from)
        ) {
          return false;
        }
      }

      // æ—¶é—´èŒƒå›´è¿‡æ»¤
      if (filter.time_range) {
        const eventTime = new Date(event.timestamp);
        const startTime = new Date(filter.time_range.start);
        const endTime = new Date(filter.time_range.end);
        if (eventTime < startTime || eventTime > endTime) {
          return false;
        }
      }

      return true;
    });
  }

  /**
   * èšåˆäº‹ä»¶ç»Ÿè®¡
   * Aggregate event statistics
   */
  static aggregateEventStatistics(events: WebhookEvent[]): EventStatistics {
    const eventsByType: Record<string, number> = {};
    const processingTimes: number[] = [];

    events.forEach((event) => {
      eventsByType[event.type] = (eventsByType[event.type] || 0) + 1;
      // æ¨¡æ‹Ÿå¤„ç†æ—¶é—´ï¼ˆå®é™…åº”ç”¨ä¸­åº”è¯¥è®°å½•çœŸå®çš„å¤„ç†æ—¶é—´ï¼‰
      processingTimes.push(Math.random() * 100);
    });

    processingTimes.sort((a, b) => a - b);

    const result: EventStatistics = {
      total_events: events.length,
      events_by_type: eventsByType,
      processing_times: {
        average_ms:
          processingTimes.reduce((a, b) => a + b, ZERO) / processingTimes.length ||
          ZERO,
        min_ms: processingTimes[ZERO] || ZERO,
        max_ms: processingTimes[processingTimes.length - ONE] || ZERO,
        p95_ms: processingTimes[Math.floor(processingTimes.length * MAGIC_0_95)] || ZERO,
        p99_ms: processingTimes[Math.floor(processingTimes.length * MAGIC_0_99)] || ZERO,
      },
      error_rate: ZERO, // åº”è¯¥åŸºäºå®é™…é”™è¯¯è®¡ç®—
      success_rate: ONE, // åº”è¯¥åŸºäºå®é™…æˆåŠŸç‡è®¡ç®—
    };

    // åªæœ‰å½“æœ‰äº‹ä»¶æ—¶æ‰è®¾ç½®å¯é€‰å±æ€§
    if (events.length > ZERO) {
      result.last_processed_at = new Date().toISOString();
    }

    return result;
  }
}
